        $ch = curl_init();

        curl_setopt($ch, CURLOPT_URL, "http://sms.zone.ps/API/SendSMS.aspx?id=d597f4679d9e20c1a642d60ea904ef9d&sender=ToGo&to=$numbers&msg=$msg");
