                    if (false) { // assigned transporters logic follows ->
                        $query_Get_Network_Payment = "SELECT id, FromTransporterId, ToTransporterId, DeliveryCost FROM
                        togo.network_order_payment WHERE IsPaid = 0 AND OrderId ='$togo_id'";
