                                $paymentId = $row['id'];
                                $query_Update_Payment = "Update togo.network_order_payment set IsPaid = 1 where id = '$paymentId'";
                                $this->dataBase->query($query_Update_Payment);
                            }
                        }
                    }
                    
                } else {
                    echo "OrderNotFound";
                    return;
                }

                $finishDate = date("Y-m-d H:i:s");

                /* edited (update order status to 'Order Finished') */
