import React, { useState, useEffect } from "react";
import { Badge, Form, Table } from "react-bootstrap";
import { FixedSizeList } from 'react-window';
import './RecordsCard.css';

export default function RecordsCard(props) {

    const [records, setRecords] = useState(props.list);

    // window height and width variables to set records container width and height
    const [windowHeight, setWindowHeight] = useState(window.innerHeight);
    const [windowWidth, setWindowWidth] = useState(window.innerWidth / 4.5);

    useEffect(() => {
        setRecords(props.list)
    }, [props.list])

    // update window width and height when change window dimensions (zoom in and out, move to a bigger screen)
    /* useEffect(() => {
        function handleResize() {
            setWindowHeight(window.innerHeight)
            setWindowWidth(window.innerWidth / 4.5)
        }

        window.addEventListener('resize', handleResize)
    }) */

    // filter records (search by order id)
    const filterHandler = (val) => {
        let tempRecords = props.list.filter(record => record.order_id.includes(val.toLowerCase()));

        setRecords(tempRecords)
    }

    // format action by action-id to meet its description
    const formatAction = (record) => {

        switch (record.action_id) {
            case "2":
                return <td style={{ textAlign: "left" }}>{record.party_one_name + " created order " + record.order_id}</td>
            case "3":
                return <td style={{ textAlign: "left" }}>{record.party_one_name + " bid on order " + record.order_id}</td>
            case "4":
                return <td style={{ textAlign: "left" }}>{record.party_one_name + " cancel order " + record.order_id}</td>
            case "5":
                return <td style={{ textAlign: "left" }}>{record.party_one_name + " updated bid on order " + record.order_id}</td>
            case "6":
                return <td style={{ textAlign: "left" }}>{record.party_one_name + " canceled bid on order " + record.order_id}</td>
            case "7":
                return <td style={{ textAlign: "left" }}>{record.party_one_name + " accepted " + record.party_two_name + "'s bid on order " + record.order_id}</td>
            case "8":
                return <td style={{ textAlign: "left" }}>{record.party_one_name + " picked up order " + record.order_id}</td>
            case "9":
                return <td style={{ textAlign: "left" }}>{record.party_one_name + " finished order " + record.order_id}</td>
            case "10":
                return <td style={{ textAlign: "left" }}>{record.party_one_name + " assigned order " + record.order_id + " to " + record.party_two_name}</td>
            case "11":
                return <td style={{ textAlign: "left" }}>{record.party_one_name + " rejected order " + record.order_id}</td>
            case "12":
                return <td style={{ textAlign: "left" }}>{record.party_one_name + " accepted order " + record.order_id}</td>
            case "17":
                return <td style={{ textAlign: "left" }}>{record.party_one_name + " canceled assign on order " + record.order_id}</td>
            case "18":
                return <td style={{ textAlign: "left" }}>{record.party_one_name + " created and assigned order " + record.order_id + " to " + record.party_two_name}</td>
            case "19":
                return <td style={{ textAlign: "left" }}>{record.party_one_name + " marked order " + record.order_id + " as stuck"}</td>
            case "20":
                return <td style={{ textAlign: "left" }}>{record.party_one_name + " marked order " + record.order_id + " as returned"}</td>
        }
    }

    /* FixedSizeList at each row */
    const RowComponent = ({ record, num, style }) => (
        <div style={style} className={"list-group-item"}>
            {formatAction(record)}
        </div>
    );

    /* FixedSizeList row */
    const Row = ({ index, style }) => {
        return <RowComponent record={records[index]} num={index} style={style} />
    };

    /* FixedSizeList, used to render only the visible rows to the DOM to enhance the performance */
    const ListComponent = () => {
        return <FixedSizeList
            height={windowHeight}
            width={windowWidth}
            itemSize={50}
            itemCount={records.length}
            className="list-container"
        >
            {Row}
        </FixedSizeList>
    };

    return (
        <div className="h-100 d-flex flex-column bg-white p-3 rounded-22 shadow" style={{ position: "relative" }}>

            <div style={{ position: "absolute", top: "15px", left: "20px", right: "15px" }}>
                <p className="text-black">{props.title}</p>
                <Form.Control style={{ position: "absolute", right: "0", top: "0", height: "25px" }} className="w-25" type="text" placeholder="Order ID..." onChange={(e) => filterHandler(e.target.value)} />
            </div>

            {/* <ListComponent /> */}

            <Table className="mt-3" style={{ fontSize: "1rem" }}>
                <thead>
                    <tr>
                        <th></th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                    {records?.map((record, index) => {
                        return <tr key={index}>
                            <td style={{ textAlign: "left" }}>
                                <span style={{ fontWeight: "bold", color: "#03A65A" }}>{record.description}</span>
                                <span>{record.time_stamp.split(" ")[0] + " "}<Badge bg="primary">{record.time_stamp.split(" ")[1]}</Badge></span>
                            </td>
                            {formatAction(record)}
                        </tr>
                    })}
                </tbody>
            </Table>

        </div>
    )
}
