import React, {useState} from "react";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import FormControl from "react-bootstrap/FormControl";
import InputGroup from "react-bootstrap/InputGroup";
import {setTripCost} from "../APIs/OrdersAPIs";
import translate from "../../i18n/translate";

export function AcceptOrder(props) {
    const [show, setShow] = useState(false);
    const [cost, setCost] = useState("");

    const handleClose = () => {
        setShow(false);
    };

    const handleShow = () => setShow(true);

    const handleChange = (e) => {
        setCost(e.target.value);
    };

    const acceptOrder = (orderId, cost) => {
        setTripCost(orderId, cost);
        handleClose();
    };

    return (
        <>
            <Button
                variant="primary"
                onClick={handleShow}
                style={{
                    float: "right",
                    border: "none",
                }}
            >
                {translate("ORDER_DETAILS.ACCEPT_ORDER")}{" "}
            </Button>
            <Modal show={show} onHide={handleClose} centered>
                <Modal.Header closeButton>
                    <Modal.Title>{translate("ORDER_DETAILS.MAKE_A_BID")}</Modal.Title>
                </Modal.Header>
                <InputGroup className="mb-3">
                    <InputGroup.Prepend>
                        <InputGroup.Text>{translate("ORDER_DETAILS.BID_PRICE")}</InputGroup.Text>
                    </InputGroup.Prepend>
                    <FormControl placeholder="0.0" value={cost} onChange={handleChange}/>
                </InputGroup>
                <Modal.Body>5% {translate("ORDER_DETAILS.WILL_TAKEN_CHARGE")}</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        {translate("GENERAL.CLOSE")}
                    </Button>
                    <Button
                        variant="primary"
                        onClick={() => acceptOrder(props.orderId, cost)}
                    >
                        {translate("GENERAL.PROCEED")}
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}
