import { motion } from "framer-motion";
import React, { useState, useEffect } from "react";

import Form from 'react-bootstrap/Form' /* edited (new import) */
import "./UserCard.css" /* edited (new import) */
import Modal from "react-bootstrap/Modal"; /* edited (new import) */
import { Accordion, Card, Button, Badge } from "react-bootstrap"; /* edited (new import) */
import translate from "../../i18n/translate"; /* edited (new import) */
import { Container, Row, Col } from 'react-bootstrap' /* edited (new import) */

export default function UsersCard(props) {
    /* console.log("******************( temp test )*********************");
     if (props.title === "All Transporters") {
         console.log("Transpoters: ");
     } else {
          console.log("Clients: ");
     }*/

    // console.log(props.list); // temp test

    /* edited (add state.users) */
    const [users, setUsers] = useState(props.list);

    const [show, setShow] = useState(false);

    const handleClose = () => {
        setShow(false);
    };

    const handleShow = () => {
        setShow(true)
    }

    const [user, setUser] = useState({});

    /* edited (add useEffect to store users value) */
    useEffect(() => {
        setUsers(props.list)
    }, [props.list])

    /* edited (add filterHandler to filter clients and transporters by name) */
    const filterHandler = (val) => {

        let tempUsers = props.list.filter(user => user.FullName.toLowerCase().includes(val.toLowerCase()) || user.PhoneNumber.includes(val));

        // console.log(tempUsers);
        setUsers(tempUsers)
    }

    const styles = {
        defaultLine: {
            width: "80%",
            margin: "auto",
            marginTop: "10px",
            marginBottom: "10px"
        },
        info: {
            color: "#3FB79E",
            fontSize: "20px"
        },
        fontSize_1: {
            fontSize: "20px"
        },
        cardHeaderLg: {
            position: 'absolute',
            left: '20px',
            right: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold'
        },
        cardHeaderSm: {
            position: 'absolute',
            left: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold'
        }
    }

    return (
        <div /* style={{flex: 1}} */

            className="h-100 d-flex flex-column bg-white  p-3 rounded-22 shadow">

            <p className="text-black">{props.title}</p>

            {/* edited (search bar for all-clients/transporters) */}

            <Form.Control className="mt-3 mb-3" type="text" placeholder="search..." onChange={(e) => filterHandler(e.target.value)} />

            <div style={{ overflowY: "auto", overflowX: "hidden", fontFamily: "system-ui" }}>
                {
                    users?.map((user, index) => {
                        // console.log("http://46.253.93.4/togo/MobileAPi/" + user.PersonalImgPath) // temp test
                        return (
                            <motion.div
                                key={index}
                                onClick={() => { handleShow(); setUser(user) }}
                                variants={{
                                    mount: { opacity: 0.8 },
                                    rest: { opacity: 1 }
                                }}
                                initial='mount'
                                whileHover={{ opacity: 1 }}
                                className="d-flex gap-3 align-items-center d-flex gap-3 text-nowrap mb-2 userSelect">
                                <div
                                    className="rounded-circle d-flex light-turquoise-bg wh-45px "
                                >
                                    <img
                                        className="user-img"

                                        src={user.PersonalImgPath ? "http://46.253.93.4/togo/MobileAPi/" + user.PersonalImgPath : "https://freepikpsd.com/file/2019/10/avatar-icon-png-5-Images-PNG-Transparent.png"}

                                        alt="user-profile-pic"
                                    />
                                </div>
                                <div style={{ width: "40%" }}>
                                    <div>{user.FullName}</div>
                                    <div>{user.PhoneNumber}</div>
                                </div>

                            </motion.div>
                        )
                    })
                }
            </div>

            {/* edited (Modal added to display client/transporter info) */}

            {/* <Modal show={show} onHide={handleClose} centered className="">
                <Modal.Header style={{  }}>
                    <div className="w-100 d-flex justify-content-center">
                        <div
                            className="rounded-circle d-flex light-turquoise-bg wh-45px "
                            style={{ width: "70px", height: "70px" }}
                        >
                            <img
                                className="user-img"

                                src={user.PersonalImgPath ? "http://46.253.93.4/togo/MobileAPi/" + user.PersonalImgPath : "https://freepikpsd.com/file/2019/10/avatar-icon-png-5-Images-PNG-Transparent.png"}

                                alt="user-profile-pic"
                            />
                        </div>
                        <div className="mt-3 ms-3 d-flex justify-content-center" style={{ fontSize: "1.5rem" }}>{user.FullName}</div>
                    </div>
                </Modal.Header>

                <Modal.Body style={{  }}>

                    <div className="container-fluid">

                        <div className="row">
                            <div className="col d-flex justify-content-between">
                                <span style={styles.fontSize_1}>{user.BusinessName ? "Business Name" : "Account Name"}:</span>
                                <span style={styles.info}>{user.BusinessName ? user.BusinessName : user.AccountName}</span>
                            </div>
                        </div>

                        <hr style={styles.defaultLine} />

                        <div className="row">
                            <div className="col d-flex justify-content-between">
                                <span style={styles.fontSize_1}>{"Phone Number"}:</span>
                                <span style={styles.info}>{user.PhoneNumber}</span>
                            </div>
                        </div>

                        <hr style={styles.defaultLine} />

                        <div className="row">
                            <div className="col d-flex justify-content-between">
                                <span style={styles.fontSize_1}>{"Email"}:</span>
                                <span style={styles.info}>{user.Email}</span>
                            </div>
                        </div>

                        <hr style={styles.defaultLine} />

                        <div className="row">
                            <div className="col d-flex justify-content-between">
                                <span style={styles.fontSize_1}>{user.BusinessPlace ? "Business Place" : "Licence Number"}:</span>
                                <span style={styles.info}>{user.BusinessPlace ? user.BusinessPlace : user.LicenceNumber}</span>
                            </div>
                        </div>

                        <hr style={styles.defaultLine} />

                        <div className="row">
                            <div className="col d-flex justify-content-between">
                                <span style={styles.fontSize_1}>{"ID"}:</span>
                                <span style={styles.info}>{user.ID}</span>
                            </div>
                        </div>
                    </div>

                </Modal.Body>
                <Modal.Footer>
                    <Button className="w-100" variant="outline-secondary" onClick={handleClose}>
                        {translate("GENERAL.CLOSE")}
                    </Button>
                </Modal.Footer>
            </Modal> */}

            <Modal fullscreen={true} show={show} onHide={handleClose} centered className="" animation={false}>
                <Modal.Body style={{}}>
                    <div onClick={handleClose} className="d-flex justify-content-start backBtn">
                        <svg xmlns="http://www.w3.org/2000/svg" width="30" height="30" fill="currentColor" className="bi bi-caret-left-fill" viewBox="0 0 16 16" >
                            <path d="m3.86 8.753 5.482 4.796c.646.566 1.658.106 1.658-.753V3.204a1 1 0 0 0-1.659-.753l-5.48 4.796a1 1 0 0 0 0 1.506z" />
                        </svg>
                        <span>Back</span>
                    </div>
                    
                    {/* image */}
                    <div className="w-100 d-flex justify-content-center">
                        {/* <div style={{  }}>
                            <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-pencil-fill" viewBox="0 0 16 16">
                                <path d="M12.854.146a.5.5 0 0 0-.707 0L10.5 1.793 14.207 5.5l1.647-1.646a.5.5 0 0 0 0-.708l-3-3zm.646 6.061L9.793 2.5 3.293 9H3.5a.5.5 0 0 1 .5.5v.5h.5a.5.5 0 0 1 .5.5v.5h.5a.5.5 0 0 1 .5.5v.5h.5a.5.5 0 0 1 .5.5v.207l6.5-6.5zm-7.468 7.468A.5.5 0 0 1 6 13.5V13h-.5a.5.5 0 0 1-.5-.5V12h-.5a.5.5 0 0 1-.5-.5V11h-.5a.5.5 0 0 1-.5-.5V10h-.5a.499.499 0 0 1-.175-.032l-.179.178a.5.5 0 0 0-.11.168l-2 5a.5.5 0 0 0 .65.65l5-2a.5.5 0 0 0 .168-.11l.178-.178z" />
                            </svg>
                        </div> */}
                        <div
                            className="rounded-circle d-flex light-turquoise-bg wh-45px "
                            style={{ width: "70px", height: "70px" }}
                        >
                            <img
                                className="user-img"

                                src={user.PersonalImgPath ? "http://46.253.93.4/togo/MobileAPi/" + user.PersonalImgPath : "https://freepikpsd.com/file/2019/10/avatar-icon-png-5-Images-PNG-Transparent.png"}

                                alt="user-profile-pic"
                            />
                        </div>
                        <div className="mt-3 ms-3 d-flex justify-content-center" style={{ fontSize: "1.5rem" }}>{user.FullName}</div>
                    </div>

                    <hr />

                    <Container>
                        <Row>

                        </Row>
                    </Container>

                </Modal.Body>
            </Modal>
        </div>
    )
}
