import React, { useEffect, useRef, useState } from 'react';
import Avatar from 'react-avatar-edit';

const TestImage = () => {
    const [src, serSrc] = useState(null);
    const [preview, serPreview] = useState(null);
    
    const onClose = () => {
        serPreview(null);
    }

    const onCrop = view => {
        serPreview(view);
    }

    useEffect(() => {
        console.log(preview)
    }, [preview])

    return (
        <div>
            <Avatar 
                width={400}
                height={300}
                onCrop={onCrop}
                onClose={onClose}
                src={src}
            />

            {preview && <img src={preview} />}
        </div>
    )
}

export default TestImage;
