import React, { useEffect, useState } from "react";
import Address from "../Address/Address";
import Map from "../Address/Map"; /* edited (Map imported) */
import { Accordion, Card, Button, Badge, Dropdown, Form, Modal, Spinner, Col, Row, Container, Table } from "react-bootstrap"; /* edited (new importd) */
import { MdOutlineFastfood } from 'react-icons/md'; /* edited (new import) */
import { BiPackage } from 'react-icons/bi'; /* edited (new import) */
import { FiPackage } from 'react-icons/fi'; /* edited (new import) */
import { FcPackage } from 'react-icons/fc'; /* edited (new import) */
import { GiEnvelope } from 'react-icons/gi'; /* edited (new import) */

import { FinalizeOrder } from "../OrdersManager/FinalizeOrder";
import { CancelOrder } from "../OrdersManager/CancelOrder";
import { BidOrder } from "../OrdersManager/AcceptOrder";
import { DeleteTripCost } from "../OrdersManager/DeleteTripCost";
import { EditTripCost } from "../OrdersManager/EditTripCost";
import Loader from "../Loader/Loader";
import "./OrderDetails.css";
import { getOrderDetails, checkTripCost, AcceptOfferReq, GetTransporterLocation } from "../../APIs/OrdersAPIs";
import translate from "../../i18n/translate";

import { Alert, AlertDescription, AlertIcon, AlertTitle } from "@chakra-ui/alert";
import { Box } from "@chakra-ui/layout";
import { deliverFormatter, packageFormatter } from "../Orders/OrdersTabularView";
import { isTeamMember, isTransporter, isTransporterMaster } from "../../Util";
import AssignToTransporter from "../AssigneToTransporter";

import { useLocation, useParams, useHistory } from "react-router";

import AssignToMemberOnNetworkDialog from "../AssginToMemberOnNetworkDialog";
import { refreshPage } from "../../Functions/CommonFunctions";

import Timeline from "../TimeLine/TimeLine"; /* edited (TimeLine imported for old orders "to be removed") */
// import FullTimeline from "../FullTimeline/FullTimeline"; /* edited (FullTimeline imported) */

import Rating from '@material-ui/lab/Rating'; /* edited (Rating imported) */

import { transactionsByOrder, getTransporterInfo, getTimeLine, responseToAssignedOrder } from '../../APIs/OrdersAPIs'; /* edited (transactionsByOrder, getTimeLine, responseToAssignedOrder imported) */

/* edited (to test the timeline) */
// import { acceptOffer, pickupPackage, acceptAssignedOrder } from '../../APIs/OrdersAPIs';

// var Barcode = require('react-barcode');

function timeFormat(time) {
    // Check correct time format and split into components
    time = time.toString().match(/^([01]\d|2[0-3])(:)([0-5]\d)(:[0-5]\d)?$/) || [time];

    if (time.length > 1) { // If time format correct
        time = time.slice(1);  // Remove full string match value
        time[5] = +time[0] < 12 ? ' AM' : ' PM'; // Set AM/PM
        time[0] = +time[0] % 12 || 12; // Adjust hours
    }
    return time.join(''); // return adjusted time or original string
}

const calculateRange = (data, rowsPerPage) => {
    const range = [];
    const num = Math.ceil(data.length / rowsPerPage);
    let i = 1;
    for (let i = 1; i <= num; i++) {
        range.push(i);
    }
    return range;
};

const sliceData = (data, page, rowsPerPage) => {
    return data.slice((page - 1) * rowsPerPage, page * rowsPerPage);
};

const useTable = (data, page, rowsPerPage) => {
    const [tableRange, setTableRange] = useState([]);
    const [slice, setSlice] = useState([]);

    useEffect(() => {
        const range = calculateRange(data, rowsPerPage);
        setTableRange([...range]);

        const slice = sliceData(data, page, rowsPerPage);
        setSlice([...slice]);
    }, [data, setTableRange, page, setSlice]);

    return { slice, range: tableRange };
};

const TableFooter = ({ range, setPage, page, slice }) => {
    useEffect(() => {
        if (slice.length < 1 && page !== 1) {
            setPage(page - 1);
        }
    }, [slice, page, setPage]);
    return (
        <div className={'tableFooter'}>

            {/*<Button onClick={() => console.log(range)}>{"<"}</Button>*/}

            {range.map((el, index) => (
                <button
                    key={index}
                    className={`${'button'} ${page === el ? 'activeButton' : 'inactiveButton'
                        }`}
                    onClick={() => setPage(el)}
                >
                    {el}
                </button>
            ))}
        </div>
    );
};

const CustomTable = ({ data, rowsPerPage }) => {
    const [page, setPage] = useState(1);
    const { slice, range } = useTable(data, page, rowsPerPage);
    return (
        <>
            <Table>
                <thead>
                    <tr>
                        {/*<th scope="col">{translate("TRANSACTIONS.ORDER_ID")}</th>*/}
                        <th scope="col">{translate("TRANSACTIONS.NAME")}</th>
                        <th scope="col">{translate("TRANSACTIONS.DATE")}</th>
                        <th scope="col">{translate("TRANSACTIONS.TIME")}</th>
                        <th scope="col">{translate("TRANSACTIONS.JOURNAL_NAME")}</th>
                        <th scope="col">{translate("TRANSACTIONS.AMOUNT")}</th>
                    </tr>
                    <tr>
                        <th scope="col" colSpan="4"></th>
                        <th scope="col">
                            <div className="d-flex justify-content-around">
                                <span>{translate("TRANSACTIONS.IN")}</span>
                                <span>{translate("TRANSACTIONS.OUT")}</span>
                            </div>
                        </th>
                    </tr>
                </thead>
                <tbody>
                    {slice.map((item, index) =>
                        <tr
                            key={index}
                        >

                            {/*<td>
                              {item.ref}
                            </td>*/}
                            <td>
                                {item.move_name}
                            </td>
                            <td>
                                {item.create_date.split(" ")[0]}
                            </td>
                            <td>
                                {timeFormat(item.create_date.split(" ")[1])}
                            </td>
                            <td>
                                {item.journal_id_name}
                            </td>
                            <td>
                                <div className="d-flex justify-content-around">
                                    <span>{item.credit !== 0 && item.credit + " NIS"}</span>
                                    <span>{item.debit !== 0 && item.debit + " NIS"}</span>
                                </div>
                            </td>

                        </tr>
                    )}
                    {/*{!slice.length && <tr><td colSpan={columns.length}>Transactions not found!</td></tr>}*/}
                </tbody>
            </Table>
            <TableFooter range={range} slice={slice} setPage={setPage} page={page} />
        </>
    );
};

