import React, { useEffect, useState } from "react";
import { Button, Dropdown, Modal, Table } from "react-bootstrap";
import { AssginOrderToMemberOnNetwork, GetAllNetworkMembers } from "../../APIs/OrdersAPIs";
import { useDispatch } from "react-redux";
import { toastNotification } from "../../Actions/GeneralActions";

export default function AssignToNetworkMemberCo({ orderId, onSuccess }) {
    const styles = {
        cardHeaderLg: {
            position: 'absolute',
            left: '20px',
            right: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold',
            fontSize: "1rem"
        },
        cardHeaderSm: {
            position: 'absolute',
            left: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold',
            fontSize: "1rem"
        },
    }

    let dispatch = useDispatch();

    const [show, setShow] = useState(false);
    const [transporters, setTransporters] = useState();
    const [selectedTransporter, setSelectedTransporter] = useState();

    const handleClose = () => {
        setShow(false);
    }

    useEffect(() => {
        GetAllNetworkMembers().then((res) => {
            setTransporters(res.data.server_response);
            setSelectedTransporter(res.data.server_response[0])
        });
    }, []);

    return (
        <>
            <Button className="border btn-grad" style={{ width: "150px", marginRight: "5px", marginLeft: "5px" }} onClick={() => {
                setShow(true);
            }}>
                Assgin To
            </Button>

            <Modal show={show} onHide={handleClose} centered>
                <Modal.Header style={styles.cardHeaderLg}>
                    <Modal.Title>Assign to Netwoek Member</Modal.Title>
                </Modal.Header>
                <Modal.Body className="mt-5">
                    <p>Set Assignee pickup and delivery addresses:</p>

                    <Table>
                        <thead>
                            <tr>
                                <th scope="col"></th>
                                <th scope="col">Account Name</th>
                                <th scope="col">Full Name</th>
                                <th scope="col">Mobile</th>
                                <th scope="col">Discount Value</th>
                            </tr>
                        </thead>

                        <tbody>

                        </tbody>
                    </Table>
                </Modal.Body>

                <Modal.Footer className="justify-content-center border-0 pt-0">
                    <Button variant="secondary" className="border rounded-22 togo-button" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" className="border rounded-22 togo-button"
                        onClick={() => {
                            AssginOrderToMemberOnNetwork(orderId, selectedTransporter.NetworkMemberId).then((res) => {
                                /* onSuccess?.(); */ /* edited (replaced) */
                                if (
                                    res.data == "NoBalanceMember" ||
                                    res.data == "No Token" ||
                                    res.data == "Error while assign order" ||
                                    res.data == "Blocked" ||
                                    res.data == "TokenError"
                                ) {
                                    dispatch(toastNotification("Error!", res.data, "error"));
                                } else {
                                    onSuccess();
                                }
                            });
                        }}>
                        Submit
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    )
}
