import { AddIcon, MinusIcon } from "@chakra-ui/icons";
import React, { useRef } from "react";
import { Button, ButtonGroup, FormControl } from "react-bootstrap";
import { TransferToMaster, TransferToMember } from "./APIs/OrdersAPIs";

export default function TransferBtn({TransporterId, onSuccess}){
    const ref = useRef();
    return (
        <ButtonGroup variant="danger">
            <Button variant="secondary" onClick={()=>{
                TransferToMaster(TransporterId, ref.current.value).then(()=>{
                    onSuccess();
                });
            }}>
                <MinusIcon />
            </Button>
            <FormControl
                type="number"
                defaultValue="0"
                style={{width: "60px"}}
                ref={ref}
            />
            
            <Button variant="secondary" onClick={()=>{
                TransferToMember(TransporterId, ref.current.value).then(()=>{
                    onSuccess();
                });
            }}>
                <AddIcon />
            </Button>
        </ButtonGroup>
    )
}
