import React, { useState, useEffect, useRef } from "react";
import UsersCard from "./UserCard";
import { GetAllClients, GetAllTransporters, getAllOrders } from "../../../APIs/AdminPanelApis";
import { Form, FloatingLabel, Spinner } from 'react-bootstrap';
import OrdersCard from "../OrdersCard";

export default function AllCustomers() {

    const [allTransporters, setAllTransporters] = useState([]); // all transporters
    const [allClients, setAllClients] = useState([]); // all clients

    const [loadingClients, setLoadingClients] = useState(false);
    const [loadingTransporters, setLoadingTransporters] = useState(false);

    const [orders, setOrders] = useState([]);
    const [searching, setSearching] = useState(false);

    const searchRef = useRef();

    useEffect(() => {

        setLoadingClients(true);
        setLoadingTransporters(true);

        // get all transporters
        GetAllTransporters().then((res) => {
            let tempArr = res.data.transporters_list;

            tempArr.sort((a, b) => b.balance - a.balance);

            setAllTransporters(tempArr);

            setLoadingTransporters(false);
        });

        // get all clients
        GetAllClients().then((res) => {
            // console.log(res.data.clients_list)

            let tempArr = res.data.clients_list;

            tempArr.sort((a, b) => b.balance - a.balance);

            setAllClients(tempArr);

            setLoadingClients(false);
        });
    }, []);

    const searchOrdersHandler = (val) => {
        
        if (!!!searchRef.current.value/* val.target.value */) {
            setOrders([]);
        } else {
            setSearching(true);
            getAllOrders(/* val.target.value */searchRef.current.value).then((res) => {
                // console.log(res.data);
                setOrders(res.data.orders_list);
                setSearching(false);
            });
        }
        
    }

    return (
        <div className="d-flex w-100 panel-wrapper" style={{ backgroundColor: "#eaeced" }}>
            <div className="m-3" style={{ width: "20%" }}>
                <UsersCard title="All Transporters" list={allTransporters} loading={loadingTransporters} />
            </div>
            <div className="m-3" style={{ width: "60%" }}>
                

                <OrdersCard title="Matched Orders" list={orders} />
            </div>
            <div className="m-3" style={{ width: "20%" }}>
                <UsersCard title="All Clients" list={allClients} loading={loadingClients} />
            </div>
        </div>)
}
