import React, { useEffect, useRef, useState } from "react";
import './CustomerInfo.css';
import { Container, Row, Col, Card, Table, Button, Form, Tab, Tabs, Sonnet } from 'react-bootstrap';
import { GetTransporterPersonalInfo, GetTransporterWorkingTimes } from "../../APIs/AdminPanelApis";

function TransporterPersonalInfoDisplay({ id }) {

    const [info, setInfo] = useState();
    const [fullName, setFullName] = useState();

    const [workingTimes, setWorkingTimes] = useState();

    const fullNameRef = useRef();
    const accountNameRef = useRef();
    const emailRef = useRef();
    const IDNumberRef = useRef();
    const licenceNumberRef = useRef();

    /* validation */
    const [beginValidation, setBeginValidation] = useState(false);
    const [fullNameCheck, setFullNameCheck] = useState(false);
    const [accountNameCheck, setAccountNameCheck] = useState(false);
    const [emailCheck, setEmailCheck] = useState(false);
    const [IDNumberCheck, setIDNumberCheck] = useState(false);
    const [licenceNumberCheck, setlicenceNumberCheck] = useState(false);

    useEffect(() => {

        GetTransporterPersonalInfo(id).then((res) => {
            // console.log(res.data);
            setInfo(res.data.server_response);
            // setFullName(res.data.server_response.fullName);
        })

        GetTransporterWorkingTimes(id).then((res) => {
            console.log(res.data.TimeResponse);
            setWorkingTimes(res.data.TimeResponse);
        })

    }, [])

    function validatePersonalInfo() {
        setBeginValidation(true);

        if (fullNameRef.current.value) {
            setFullNameCheck(true);
        } else {
            setFullNameCheck(false);
        }
        if (accountNameRef.current.value) {
            setAccountNameCheck(true);
        } else {
            setAccountNameCheck(false);
        }
        if (emailRef.current.value) {
            setEmailCheck(true);
        } else {
            setEmailCheck(false);
        }
        if (IDNumberRef.current.value) {
            setIDNumberCheck(true);
        } else {
            setIDNumberCheck(false);
        }
        if (licenceNumberRef.current.value) {
            setlicenceNumberCheck(true);
        } else {
            setlicenceNumberCheck(false);
        }
    }

    function updatePersonalInfo() {

        validatePersonalInfo();

        if (fullNameRef.current.value && accountNameRef.current.value && emailRef.current.value && IDNumberRef.current.value && licenceNumberRef.current.value) {
            console.log("updated!")
        }
    }

    function test() {
        console.log(info)
    }

    return (
        <>
            <Tabs defaultActiveKey="PersonalInformation" id="uncontrolled-tab-example" className="tabPane">
                <Tab eventKey="PersonalInformation" title="Personal Information">
                    <Card className="customCardSm shadow m-5">
                        {/* <Card.Header>
                            Personal Information
                        </Card.Header> */}
                        <Card.Body>

                            <Container fluid>
                                <Row>
                                    <Col lg={6}>
                                        <Table className="infoTable">
                                            <tbody>
                                                <tr>
                                                    <th>Full Name:</th>
                                                    <td>
                                                        <Form.Control
                                                            placeholder={info?.fullName}
                                                            ref={fullNameRef}
                                                        /* onChange={(event) => {setFullName(event.target.value); console.log(event.target.value)}} */
                                                        />
                                                        {beginValidation && !fullNameCheck && <div className="required-error"><span>*</span></div>}
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <th>Account Name:</th>
                                                    <td>
                                                        <Form.Control
                                                            placeholder={info?.AccountName}
                                                            ref={accountNameRef}
                                                        />
                                                        {beginValidation && !accountNameCheck && <div className="required-error"><span>*</span></div>}
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <th>Email:</th>
                                                    <td>
                                                        <Form.Control
                                                            type="email"
                                                            placeholder={info?.Email}
                                                            ref={emailRef}
                                                        />
                                                        {beginValidation && !emailCheck && <div className="required-error"><span>*</span></div>}
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <th>ID Number:</th>
                                                    <td>
                                                        <Form.Control
                                                            type="number"
                                                            placeholder={info?.IDNumber}
                                                            ref={IDNumberRef}
                                                        />
                                                        {beginValidation && !IDNumberCheck && <div className="required-error"><span>*</span></div>}
                                                    </td>
                                                </tr>
                                                {/* <tr>
                                                    <th>Place of Issuing Identity:</th>
                                                    <td>
                                                        <Form.Control
                                                            placeholder={info?.NamePlace}
                                                            ref={IDNumberRef}
                                                            onClick={test}
                                                        />
                                                        {beginValidation && !IDNumberCheck && <div className="required-error"><span>*</span></div>}
                                                    </td>
                                                </tr> */}
                                                <tr>
                                                    <th>Licence Number:</th>
                                                    <td>
                                                        <Form.Control
                                                            type="number"
                                                            placeholder={info?.LicenceNumber}
                                                            ref={licenceNumberRef}
                                                        />
                                                        {beginValidation && !licenceNumberCheck && <div className="required-error"><span>*</span></div>}
                                                    </td>
                                                </tr>
                                            </tbody>
                                        </Table>
                                    </Col>
                                    <Col lg={6}>
                                        <div className="w-100 d-flex justify-content-center">
                                            <div
                                                className="rounded-circle d-flex light-turquoise-bg wh-45px "
                                                style={{ width: "200px", height: "200px" }}
                                            >
                                                <img
                                                    className="user-img-details"

                                                    src={info?.img ? "http://46.253.93.4/togo/MobileAPi/" + info.img : "https://freepikpsd.com/file/2019/10/avatar-icon-png-5-Images-PNG-Transparent.png"}

                                                    alt="user-profile-pic"
                                                />
                                            </div>
                                        </div>
                                        <div className="mt-3 ms-3 d-flex justify-content-center" style={{ fontSize: "1.5rem" }}>{/* fullName */ info?.fullName}</div>
                                    </Col>
                                </Row>
                            </Container>
                        </Card.Body>
                        <Card.Footer>
                            <Button className="update-btn" onClick={updatePersonalInfo}>
                                UPDATE
                            </Button>
                        </Card.Footer>
                    </Card>
                </Tab>
                <Tab eventKey="WorkingTimeAndBusinessLocation" title=" Working Time and Business Location">
                    <Card className="customCardSm shadow mt-5">
                        {/* <Card.Header>
                            Working Time and Business Location
                        </Card.Header> */}
                        <Card.Body>
                            Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
                        </Card.Body>
                        <Card.Footer>
                            <Button className="update-btn">
                                UPDATE
                            </Button>
                        </Card.Footer>
                    </Card>
                </Tab>
                <Tab eventKey="VehiclesInformation" title="Vehicles Information">
                    <Card className="customCardSm shadow mt-5">
                        {/* <Card.Header>
                            Vehicles Information
                        </Card.Header> */}
                        <Card.Body>
                            labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
                        </Card.Body>
                        <Card.Footer>
                            <Button className="update-btn">
                                UPDATE
                            </Button>
                        </Card.Footer>
                    </Card>
                </Tab>
            </Tabs>
        </>
    )
}

function ClientPersonalInfoDisplay() {
    return (
        <>
            <Tabs defaultActiveKey="PersonalInformation" id="uncontrolled-tab-example" className="tabPane">
                <Tab eventKey="PersonalInformation" title="Personal Information">
                    <Card className="customCardSm shadow m-5">
                        {/* <Card.Header>
                            Personal Information
                        </Card.Header> */}
                        <Card.Body>

                            <Container fluid>
                                <Row>
                                    <Col lg={6}>
                                        <Table className="infoTable">
                                            <tbody>
                                                <tr>
                                                    <th>Full Name:</th>
                                                    <td>
                                                        <Form.Control
                                                            placeholder={info?.fullName}
                                                            ref={fullNameRef}
                                                        /* onChange={(event) => {setFullName(event.target.value); console.log(event.target.value)}} */
                                                        />
                                                        {beginValidation && !fullNameCheck && <div className="required-error"><span>*</span></div>}
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <th>Account Name:</th>
                                                    <td>
                                                        <Form.Control
                                                            placeholder={info?.AccountName}
                                                            ref={accountNameRef}
                                                        />
                                                        {beginValidation && !accountNameCheck && <div className="required-error"><span>*</span></div>}
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <th>Email:</th>
                                                    <td>
                                                        <Form.Control
                                                            type="email"
                                                            placeholder={info?.Email}
                                                            ref={emailRef}
                                                        />
                                                        {beginValidation && !emailCheck && <div className="required-error"><span>*</span></div>}
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <th>ID Number:</th>
                                                    <td>
                                                        <Form.Control
                                                            type="number"
                                                            placeholder={info?.IDNumber}
                                                            ref={IDNumberRef}
                                                        />
                                                        {beginValidation && !IDNumberCheck && <div className="required-error"><span>*</span></div>}
                                                    </td>
                                                </tr>
                                                {/* <tr>
                                                    <th>Place of Issuing Identity:</th>
                                                    <td>
                                                        <Form.Control
                                                            placeholder={info?.NamePlace}
                                                            ref={IDNumberRef}
                                                            onClick={test}
                                                        />
                                                        {beginValidation && !IDNumberCheck && <div className="required-error"><span>*</span></div>}
                                                    </td>
                                                </tr> */}
                                                <tr>
                                                    <th>Licence Number:</th>
                                                    <td>
                                                        <Form.Control
                                                            type="number"
                                                            placeholder={info?.LicenceNumber}
                                                            ref={licenceNumberRef}
                                                        />
                                                        {beginValidation && !licenceNumberCheck && <div className="required-error"><span>*</span></div>}
                                                    </td>
                                                </tr>
                                            </tbody>
                                        </Table>
                                    </Col>
                                    <Col lg={6}>
                                        <div className="w-100 d-flex justify-content-center">
                                            <div
                                                className="rounded-circle d-flex light-turquoise-bg wh-45px "
                                                style={{ width: "200px", height: "200px" }}
                                            >
                                                <img
                                                    className="user-img-details"

                                                    src={info?.img ? "http://46.253.93.4/togo/MobileAPi/" + info.img : "https://freepikpsd.com/file/2019/10/avatar-icon-png-5-Images-PNG-Transparent.png"}

                                                    alt="user-profile-pic"
                                                />
                                            </div>
                                        </div>
                                        <div className="mt-3 ms-3 d-flex justify-content-center" style={{ fontSize: "1.5rem" }}>{/* fullName */ info?.fullName}</div>
                                    </Col>
                                </Row>
                            </Container>
                        </Card.Body>
                        <Card.Footer>
                            <Button className="update-btn" onClick={updatePersonalInfo}>
                                UPDATE
                            </Button>
                        </Card.Footer>
                    </Card>
                </Tab>
                <Tab eventKey="businessInformation" title="Business Information">
                    <Card className="customCardSm shadow mt-5">
                        {/* <Card.Header>
                            Working Time and Business Location
                        </Card.Header> */}
                        <Card.Body>
                            Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
                        </Card.Body>
                        <Card.Footer>
                            <Button className="update-btn">
                                UPDATE
                            </Button>
                        </Card.Footer>
                    </Card>
                </Tab>
            </Tabs>
        </>
    )
}

export default function CustomerInfo(props) {

    // console.log(props);

    return (
        <>
            <div onClick={props.handleClose} className="d-flex justify-content-start backBtn">
                <svg xmlns="http://www.w3.org/2000/svg" width="30" height="30" fill="currentColor" className="bi bi-caret-left-fill" viewBox="0 0 16 16" >
                    <path d="m3.86 8.753 5.482 4.796c.646.566 1.658.106 1.658-.753V3.204a1 1 0 0 0-1.659-.753l-5.48 4.796a1 1 0 0 0 0 1.506z" />
                </svg>
                <span>Back</span>
            </div>

            {/* upper background */}
            <div className="upperBackground">
            </div>

            <div className="mainContainer">
                {props.isTransporter ? <TransporterPersonalInfoDisplay id={props.customerId} /> : <ClientPersonalInfoDisplay />}
            </div>
        </>
    )
}
