import { motion } from "framer-motion";
import React, { useState, useEffect } from "react";
import { PackageTypesIcons } from "../CreateNewOrder";
import { FixedSizeList } from 'react-window';
import { GetAllFinishedOrders } from "../../APIs/AdminPanelApis";
import { useHistory } from "react-router";

export default function OrdersCard(props) {

    const history = useHistory();

    const [returnedOrdersActiveClass, setReturnedOrdersActiveClass] = useState("active-orders-tab");
    const [finishedOrdersActiveClass, setfinishedOrdersActiveClass] = useState("");

    const exceptionsOrders = props.list;

    // console.log(props)


    // window height and width variables to set records container width and height
    const [windowHeight, setWindowHeight] = useState(window.innerHeight);
    const [windowWidth, setWindowWidth] = useState(window.innerWidth / 2.2);

    // update window width and height when change window dimensions (zoom in and out, move to a bigger screen)
    useEffect(() => {
        function handleResize() {
            setWindowHeight(window.innerHeight)
            setWindowWidth(window.innerWidth / 2.2)
        }

        window.addEventListener('resize', handleResize)
    })

    /* FixedSizeList at each row */
    const RowComponent = ({ order, num, style }) => (
        <div style={style} className={"list-group-item"} onClick={() => {
            // history.push("/adminapp/orderDetails/" + order.id)
            window.open("/adminapp/orderDetails/" + order.id, '_blank');
        }}>
            <div className="list-inner-item">
                {/* <div className="d-flex justify-content-between w-100">
                    <div className="d-flex justify-content-between">
                        {order.TypeLoad && React.createElement(PackageTypesIcons[order.TypeLoad],
                            { style: { width: "30px", height: "30px", margin: "auto" } })
                        }
                        <span className="h4 mt-2">ID {order.id}</span>
                    </div>

                    <div className="d-flex justify-content-between">
                        {order.clientBusinessName}
                    </div>
                </div>

                <div className="d-flex justify-content-between w-100">

                </div>

                <div className="d-flex justify-content-between w-100">

                </div> */}
                <div className="container-fluid">
                    <div className="row">
                        <div className="col d-flex justify-content-start">
                            {order.TypeLoad && React.createElement(PackageTypesIcons[order.TypeLoad],
                                { style: { width: "30px", height: "30px" } })
                            }
                            <span className="h4 ms-2 order-id">Order {order.id}</span>
                        </div>

                        <div className="col">
                            {order.bidsCount != "0" && props.title.indexOf("New") != -1 &&
                                <div
                                    className="me-2 p-1 rounded-22 purply-lbl d-flex justify-content-center">
                                    {order.bidsCount} Bids
                                </div>
                            }

                            {order.bidsCount == "0" && props.title.indexOf("New") != -1 &&
                                <div className={"me-2 p-1 d-flex justify-content-center rounded-22 " + (order.order_status === "Bid Accepted" ? "verdigris" : (order.order_status === "Delivered" || order.order_status === "Out for delivery") ? "purply" : order.order_status === "Waiting for Bids" ? "purply" : "orangy") + "-lbl"} >
                                    {order.order_status}
                                </div>
                            }

                            {props.title.indexOf("New") == -1 &&
                                <div className={"me-2 p-1 d-flex justify-content-center rounded-22 " + (order.order_status === "Bid Accepted" ? "verdigris" : (order.order_status === "Delivered" || order.order_status === "Out for delivery") ? "purply" : "orangy") + "-lbl"} >
                                    {order.order_status}
                                </div>
                            }
                        </div>
                    </div>

                    <div className="row">
                        <div className="col">
                            <span className="h6">{order.DateLoad}</span>
                        </div>

                        <div className="col d-flex justify-content-between">
                            <span className="h6">{order.fromCity}</span>
                            <i className="bi bi-arrow-right"></i>
                            <span className="h6">{order.toCity}</span>
                        </div>
                    </div>

                    <div className="row">
                        <div className="col d-flex justify-content-start">
                            <i className="bi bi-send me-1"></i>
                            <span className="h6">{order.clientBusinessName}</span>
                        </div>

                        {order.transporterAccountName && <div className="col d-flex justify-content-start">
                            <i className="bi bi-truck me-1"></i>
                            <span className="h6">{order.transporterAccountName}</span>
                        </div>}
                    </div>
                </div>
                {/* <div
                    className="rounded-circle d-flex light-turquoise-bg wh-45px"
                >
                    {order.TypeLoad && React.createElement(PackageTypesIcons[order.TypeLoad],
                        { style: { width: "30px", height: "30px", margin: "auto" } })
                    }
                </div>
                <div style={{ width: "35%" }}>
                    <div>From:{order.fromCity}</div>
                    <div style={{ color: "#03A65A" }}>ID {order.id}</div>
                </div>
                <div style={{ width: "35%" }}>
                    <div>To {order.toCity}</div>
                    <div>{order.DateLoad}</div>
                </div>
                <div>
                    {order.bidsCount != "0" && props.title.indexOf("New") != -1 &&
                        <div
                            className="me-2 p-1 rounded purply-lbl">
                            {order.bidsCount} Bids
                        </div>
                    }

                    {order.bidsCount == "0" && props.title.indexOf("New") != -1 &&
                        <div className={"me-2 p-1 rounded " + (order.order_status === "Bid Accepted" ? "verdigris" : (order.order_status === "Delivered" || order.order_status === "Out for delivery") ? "purply" : "orangy") + "-lbl"} >
                            {order.order_status}
                        </div>
                    }

                    {props.title.indexOf("New") == -1 &&
                        <div className={"me-2 p-1 rounded " + (order.order_status === "Bid Accepted" ? "verdigris" : (order.order_status === "Delivered" || order.order_status === "Out for delivery") ? "purply" : "orangy") + "-lbl"} >
                            {order.order_status}
                        </div>
                    }
                </div> */}
            </div>
        </div>
    );

    /* FixedSizeList row */
    const Row = ({ index, style }) => {
        return <RowComponent order={props.list[index]} num={index} style={style} />
    };

    /* FixedSizeList, used to render only the visible rows to the DOM to enhance the performance */
    const ListComponent = () => {
        // props.list.length > 0 && props.title.indexOf("Finished") != -1 && console.log(props.list)
        return props?.list?.length > 0 && <FixedSizeList
            height={windowHeight}
            width={windowWidth}
            itemSize={115}
            itemCount={props.list.length}
            className="list-container"
        >
            {Row}
        </FixedSizeList>
    };

    const toggleFinishedOrdersTabe = () => {
        setfinishedOrdersActiveClass("active-orders-tab");
        setReturnedOrdersActiveClass("");

        GetAllFinishedOrders().then((res) => {
            console.log(res.data.orders_list)

            props.list = res.data.orders_list;
            props.title = "Finished Orders";
        });
    }

    const toggleReturnedOrdersTabe = () => {
        setfinishedOrdersActiveClass("");
        setReturnedOrdersActiveClass("active-orders-tab");

        props.list = exceptionsOrders;
        props.title = " Returned/Stuck Orders";
    }

    return (
        <div className="h-100 d-flex flex-column bg-white p-3 rounded-22 shadow" style={{ position: "relative" }}>
            {/* <p style={{ position: "absolute", left: "20px" }} className="text-black">{props.title}</p> */}
            {props.title == "Returned/Stuck Orders" ?
                <div className="toggle-orders">
                    <span className={returnedOrdersActiveClass + " orders-tab"} onClick={() => {
                        toggleReturnedOrdersTabe()
                    }}>Returned/Stuck Orders</span>
                    <span className={finishedOrdersActiveClass + " orders-tab ms-3"} onClick={() => {
                        toggleFinishedOrdersTabe()
                    }}>Finished Orders</span>
                </div> :
                <p style={{ position: "absolute", left: "20px" }} className="text-black">{props.title}</p>}
            {
                props.list.length != 0 ?
                    <ListComponent /> : <>
                        <div className="mt-5 d-flex justify-content-center h4">
                            No {props.title} Found!
                        </div>
                        <div className="mt-5 d-flex justify-content-center h4">
                            <i className="bi bi-list" style={{ fontSize: "10rem", color: "lightgray" }}></i>
                        </div>
                    </>
            }
        </div>
    )
}
