import React from "react";
import "./AdminPanel.css"
import {
    GetAllActiveOrders,
    GetAllFinishedOrders,
    GetAllDeletedOrders,
    GetAllReturnedOrders,
    GetAllNewOrders,
    getTotalBalance,
    GetAllClientsNum,
    GetAllTransportersNum,
    GetAllMarkedOrders,
    getTotalTempBalance
} from "../../APIs/AdminPanelApis";
import { useEffect, useState } from "react";
import OrdersCard from "./OrdersCard";
import 'bootstrap-icons/font/bootstrap-icons.css';

import CustomIcon from '../../assets/icons';
import Nav from 'react-bootstrap/Nav';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Navbar from 'react-bootstrap/Navbar';
import Spinner from 'react-bootstrap/Spinner';
import "./OrdersPage.css";

export default function AdminPanel() {

    const [newOrders, setNewOrders] = useState([]); // new orders
    const [activeOrders, setActiveOrders] = useState([]); // in-process orders
    const [finishedOrders, setFinishedOrders] = useState([]); // finished orders
    const [deletedOrders, setDeletedOrders] = useState([]); // deleted orders
    const [exceptionOrders, setExceptionOrders] = useState([]); // returned/stuck orders
    const [creditAmount, setCreditAmount] = useState(0); // total escrow
    const [totalEscrowBalance, setTotalEscrowBalance] = useState(0); // total in escrow (active money)
    const [totalClients, setTotalClinets] = useState(); // number of clients
    const [totalTransporters, setTotalTransporters] = useState(); // number of transporters

    // to display loading spinner
    const [newOrdersLoading, setNewOrdersLoading] = useState(false);
    const [activeOrdersLoading, setActiveOrdersLoading] = useState(false);
    const [finishedOrdersLoading, setFinishedOrdersLoading] = useState(false);
    const [exceptionOrdersLoading, setExceptionOrdersLoading] = useState(false);
    const [deletedOrdersLoading, setDeletedOrdersLoading] = useState(false);

    const [newOrdersLoadMore, setNewOrdersLoadMore] = useState(10);
    const [finishedOrdersLoadMore, setFinishedOrdersLoadMore] = useState(10);
    const [activeOrdersLoadMore, setActiveOrdersLoadMore] = useState(10);
    const [deletedOrdersLoadMore, setDeletedOrdersLoadMore] = useState(10);
    const [exceptionOrdersLoadMore, setExceptionOrdersLoadMore] = useState(10);

    const [loadNewOrders, setLoadNewOrders] = useState(false);
    const [loadActiveOrders, setLoadActiveOrders] = useState(false);
    const [loadFinishedOrders, setLoadFinishedOrders] = useState(false);
    const [loadDeletedOrders, setLoadDeletedOrders] = useState(false);
    const [loadExceptionOrders, setLoadExceptionOrders] = useState(false);

    const [tempBalance, setTempBalance] = useState(0);

    /* useEffect(() => {
        getTotalTempBalance().then((res) => {
            // console.log(res.data);
            setTempBalance(res.data);
        })
    }, [])

    // get total balance
    useEffect(() => {
        getTotalBalance().then((res) => {

            // console.log(res.data?.server_response);

            setTotalEscrowBalance(res.data?.server_response?.data?.result.differ_balance);
            setCreditAmount(res.data?.server_response?.data?.result.credit_amount);
        });
    }, []); */

    /* get all orders */
    useEffect(() => {
        setNewOrdersLoading(true);
        setActiveOrdersLoading(true);
        setFinishedOrdersLoading(true);
        setExceptionOrdersLoading(true);
        setDeletedOrdersLoading(true);

        // get all new orders
        GetAllNewOrders().then((res) => {
            setNewOrders(res.data.orders_list);

            setNewOrdersLoading(false);
        });

        // get all in-process orders
        GetAllActiveOrders().then((res) => {
            // console.log(res.data.orders_list)
            setActiveOrders(res.data.orders_list);

            setActiveOrdersLoading(false);
        });

        // get all stuck/returned orders
        GetAllReturnedOrders().then((res) => {
            setExceptionOrders(res.data.orders_list);

            setExceptionOrdersLoading(false);
        });

        // get all deleted orders
        GetAllDeletedOrders().then((res) => {
            setDeletedOrders(res.data.orders_list)

            setDeletedOrdersLoading(false);
        })
    }, []);

    useEffect(() => {
        // get all finished orders
        GetAllFinishedOrders(finishedOrdersLoadMore).then((res) => {
            // console.log(res.data.orders_list)
            setFinishedOrders(res.data.orders_list)

            setFinishedOrdersLoading(false);

            setFinishedOrdersLoadMore(finishedOrdersLoadMore + 10);
        })
    }, [loadFinishedOrders])

    useEffect(() => {
        // get all transporters count
        GetAllTransportersNum().then((res) => {
            setTotalTransporters(res.data.NumberOfTransporters);
        });

        // get all clients count
        GetAllClientsNum().then((res) => {
            setTotalClinets(res.data.NumberOfClients);
        });
    }, []);

    return (
        <>
            {/* <div className="statDiv">
                <div className="container-fluid h-100">
                    <div className='row h-100 mt-1'>
                        <div className='col rightBorder'>
                            <div className='row h-50'>
                                <div className='col'>Total Clients:</div>
                                <div className='col togo-dark-green'>{totalClients}</div>
                            </div>
                            <div className='row h-50'>
                                <div className='col'>Total Transporters:</div>
                                <div className='col togo-dark-green'>{totalTransporters}</div>
                            </div>
                        </div>
                        <div className='col'>
                            <div className='row h-25'>
                                <div className='col'>Total in Escrow:</div>
                                <div className='col togo-dark-green'>{totalEscrowBalance}{" NIS"}</div>
                            </div>
                            <div className='row h-25'>
                                <div className='col'>Total Escrow:</div>
                                <div className='col togo-dark-green'>{creditAmount}{" NIS"}</div>
                            </div>
                            <div className='row h-25'>
                                <div className='col'>Temp Balance:</div>
                                <div className='col togo-dark-green'>{tempBalance}{" NIS"}</div>
                            </div>
                        </div>
                    </div>
                </div>
            </div> */}

            {/* <div className="d-flex w-100 panel-wrapper" style={{ backgroundColor: "#eaeced" }}>
                <div className="m-3" style={{ width: "25%" }}>
                    <OrdersCard title="New Orders" list={newOrders} loading={newOrdersLoading} />
                </div>
                <div className="m-3" style={{ width: "25%" }}>
                    <OrdersCard title="Active Orders" list={activeOrders} loading={activeOrdersLoading} />
                </div>
                <div className="m-3" style={{ width: "25%" }}>
                    <OrdersCard title="Finished Orders" list={finishedOrders} loading={finishedOrdersLoading} />
                </div>
                <div className="m-3" style={{ width: "25%" }}>
                    <OrdersCard exception={true} title="Returned/Stuck Orders" list={exceptionOrders} loading={exceptionOrdersLoading} />
                    <OrdersCard exception={true} title="Deleted Orders" list={deletedOrders} loading={deletedOrdersLoading} />
                </div>
            </div> */}

            <div className='orders-grid'>
                <OrdersColumn index="first" title="New Orders" orders={newOrders} loading={newOrdersLoading} loadMore={() => {setLoadNewOrders(!loadNewOrders)}} />
                <OrdersColumn title="Active Orders" orders={activeOrders} loading={activeOrdersLoading} loadMore={() => {setLoadActiveOrders(!loadActiveOrders)}} />
                <OrdersColumn title="Finished Orders" orders={finishedOrders} loading={finishedOrdersLoading} loadMore={() => {setLoadFinishedOrders(!loadFinishedOrders)}} />
                <OrdersColumn index="last" title="Deleted Orders" orders={deletedOrders} loading={deletedOrdersLoading} loadMore={() => {setLoadDeletedOrders(!loadDeletedOrders)}} />
                <OrdersColumn title="Returned/Stuck Orders" orders={exceptionOrders} loading={exceptionOrdersLoading} loadMore={() => {setLoadExceptionOrders(!loadExceptionOrders)}} />
            </div>
        </>
    )
}

const OrdersColumn = ({ index, title, orders, loading, loadMore }) => {

    const [ordersState, setOrdersState] = useState([]);

    useEffect(() => {
        setOrdersState(orders);
    }, [orders])

    const filterHandler = (val) => {
        if (!!val) {
            let tempOrders = ordersState.filter(order => (order.id.includes(val) || (order.fromCity && order.fromCity.toLowerCase().includes(val.toLowerCase())) || (order.toCity && order.toCity.toLowerCase().includes(val.toLowerCase())) || (order.receiverName && order.receiverName.toLowerCase().includes(val.toLowerCase())) || (order.clientBusinessName && order.clientBusinessName.toLowerCase().includes(val.toLowerCase())) || (order.transporterAccountName && order.transporterAccountName.toLowerCase().includes(val.toLowerCase())) || (order.DateLoad && order.DateLoad.includes(val)) || (order.foreign_order_error == 1 && val == "!!")));

            setOrdersState(tempOrders)
        } else {
            setOrdersState(orders);
        }
    }

    return (
        <div className="orders-column">
            <div className='header' style={{ marginLeft: index !== "first" ? "-8px" : "", marginRight: index !== "last" ? "-8px" : "" }}>
                <div>
                    {/* <span className='me-1'>
                        <input className="form-check-input" type="checkbox" value="" id="flexCheckDefault" />
                    </span> */}
                    {title}
                </div>
                <Form.Control style={{ height: "25px", width: "35%" }} type="text" placeholder="Search..." onChange={(e) => filterHandler(e.target.value)} />
                {/* <div>...</div> */}
            </div>
            <div className="column">
                <div>
                    {
                        loading ? <div className="d-flex justify-content-center"><Spinner animation="border" size="lg" /></div> :
                            ordersState?.length == 0 ? <div className="d-flex justify-content-center">No {title}</div> :
                                ordersState?.map((order, index) => {
                                    return <div key={index}>
                                        <OrderCard order={order} />
                                    </div>
                                })
                    }
                    <div>
                        <Button onClick={loadMore}>Load More</Button>
                    </div>
                </div>
            </div>
        </div>
    )
}

const OrderCard = ({ order }) => {

    const [isActive, setIsActive] = useState(false);
    const [isIdHover, setIsIdHover] = useState(false);

    const getColorStatus = (status) => {
        switch (status) {
            case "Wating for Bids": return "#6cc6f0"
            case "Bid Accepted": return "#4697b7"
            case "Out for Delivery": return "#e98945"
            case "Delivered": return "#8dcb8e"
            case "Deleted": return "#565656"
            case "Returned": return "#e54149"
            case "Stuck": return "#e54149"
        }

        return "#848484";
    }

    const handleIdMouseEnter = () => {
        setIsIdHover(true);
    };

    const handleIdMouseLeave = () => {
        setIsIdHover(false);
    };

    return (
        <div className='order-card'>

            <div className={'order-card-body' + (isActive ? '-active' : order.foreign_order_error == 1 && "-exception")}>
                <div className="left-vertical-ine"></div>
                {order.foreign_order_error == 1 && <>
                    <div className="exception-row">
                        <i className="bi bi-exclamation-circle-fill"></i>
                        <div className="text">{order.errMsg}</div>
                    </div>

                    <hr />
                </>}
                <div className='first-row'>
                    <div className='first-row-first-col'>
                        <div className='order-id' >
                            <>
                                <OrderCheckBox orderId={order.id} active={isActive} setIsChecked={(isChecked) => { setIsActive(isChecked) }} />
                                <div
                                    className='id ms-2'
                                    onMouseEnter={handleIdMouseEnter}
                                    onMouseLeave={handleIdMouseLeave}
                                    onClick={() => {
                                        window.open("/adminapp/orderDetails/" + order.id, '_blank');
                                    }}
                                >
                                    {order.id}
                                    {isIdHover && <CustomIcon iconName="eye_icon" color="lightgray" size="20" />}
                                </div>
                            </>
                        </div>
                        <div className='cod'>
                            <div className='text'>COD</div>{order.CostLoad}
                        </div>
                    </div>
                    <div className='first-row-second-col'>
                        <div className='status' style={{ backgroundColor: getColorStatus(order.order_status) }}>{order.order_status}</div>
                        <div className='date-created'>
                            <CustomIcon iconName="date_created" color="#848484" size="17" />
                            <div className='text'>{order.DateLoad}</div>
                        </div>
                        {!!order.dateFinished ? <div className='date-finished'>
                            <CustomIcon iconName="date_finished" color="#848484" size="17" />
                            <div className='text'>{order.dateFinished}</div>
                        </div> : <div style={{ width: "100%", height: "20px" }}></div>}
                    </div>
                </div>

                <hr />

                <div className='second-row-full'>
                    <CustomIcon iconName="merchant_2" color="#848484" size="30" />
                    <div className='text'>{order.clientBusinessName}</div>
                </div>

                <div className='third-row-full'>
                    {!!order.transporterAccountName ? <>
                        <CustomIcon iconName="transporter_2" color="#848484" size="30" />
                        <div className='text'>{order.transporterAccountName}</div>
                    </> : <div style={{ width: "100%", height: "27px" }}></div>}
                </div>

                <hr />

                <div className='fourth-row-full'>
                    <CustomIcon iconName="sender" color="#848484" size="30" />
                    <div className='text'>
                        <div className='address-name'>{order.receiverName}</div>
                        <div className='address-city'>{order.toCity}</div>
                    </div>
                </div>

                <div className='status-triangle'>
                    <CustomIcon iconName="status_triangle" color={getColorStatus(order.order_status)} size="25" />
                </div>
            </div>
        </div>
    )
}

const OrderCheckBox = ({ orderId, active, setIsChecked }) => {

    const [isActive, setIsActive] = useState(active);
    const [isHover, setIsHover] = useState(false);

    const handleMouseEnter = () => {
        setIsHover(true);
    };
    const handleMouseLeave = () => {
        setIsHover(false);
    };

    const handleclick = () => {
        setIsChecked(!isActive);
        setIsActive(!isActive);
    };

    const styles = {
        mainDiv: {
            width: "30px",
            height: "30px",
            display: "flex",
            alignItem: "center",
            justifyContent: "center",
            borderRadius: "5px",
            cursor: "pointer",
            transition: "background-color, transform 0.1s ease",
            backgroundColor: isHover || isActive ? "#1C7A68" : "#24A693",
            transform: isHover && "scale(1.1)"
        }
    }

    return (
        <div
            style={styles.mainDiv}
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
            onClick={handleclick}
        >
            <CustomIcon iconName={isActive ? "order_check_active" : isHover ? "order_check_hover" : "order_check_inactive"} color="white" size="20" />
        </div>
    )
}
