import { Button, Dropdown, Form, Modal, Spinner, Table, Card, Container, Row, Col, ListGroup, FloatingLabel } from "react-bootstrap";
import React, { useEffect, useRef, useState } from "react";
import translate from "../../i18n/translate";
import { ReactComponent as SendIcon } from "../../assets/images/send.svg";
import { CreateNewOrderReq, GetDefinedAddresses, testBarq, GetAllClientNetworkMembers, ClientAssignOrder, checkTripCost, GetCitiesArea } from "../../APIs/OrdersAPIs";
import { GetTransporterClients } from "../../APIs/OrdersAPIs"
import { ReactComponent as FoodIcon } from "../../assets/images/food.svg";
import { ReactComponent as SmBoxIcon } from "../../assets/images/smallBox.svg";
import { ReactComponent as MedBoxIcon } from "../../assets/images/medbox.svg";
import { ReactComponent as BigBoxIcon } from "../../assets/images/largebox.svg";
import { ReactComponent as DeliveryTruckIcon } from "../../assets/images/deliveryTruck.svg";
import { ReactComponent as LocationIcon } from "../../assets/images/location.svg";
import { ReactComponent as AttachmentIcon } from "../../assets/images/attachment.svg";
import { IoIosGitNetwork } from 'react-icons/io';
import { DeliveryTypes, PackageTypes } from "../Orders/OrdersTabularView";
import AddressDropdown from "../AddressDropdown";
import ClientDropdown from "../ClientDropdown";
import CreateAddress from "../CreateAddress";
import { AddIcon } from "@chakra-ui/icons";
import { useIntl } from "react-intl";
import { useDispatch } from "react-redux";
import { toastNotification } from "../../Actions/GeneralActions";
import { isTransporter } from "../../Util";
import SubmitAndAssignDialog from "../SubmitAndAssignDialog";
import "../CreateNewOrder.css";
import { useHistory } from "react-router";
import { imgBaseUrl } from "../../Constants/GeneralCont";

export const PackageTypesIcons = {
    "1": FoodIcon,
    "2": SmBoxIcon,
    "3": MedBoxIcon,
    "4": BigBoxIcon
};


export default function CreateNewOrder(props) {

    const history = useHistory();

    const styles = {
        cardHeaderLg: {
            position: 'absolute',
            left: '20px',
            right: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold',
            fontSize: "1.5rem"
        },
        cardHeaderSm: {
            position: 'absolute',
            left: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold',
            fontSize: "1.2rem"
        },
    }

    let dispatch = useDispatch();

    const [open, setOpen] = useState(false);
    const [openConf, setOpenConf] = useState(false);
    const handleClose = () => { setOpen(false); }
    const [packageType, setPackageType] = useState("2");
    const [deliveryType, setDeliveryType] = useState("1");
    const [inputValue, setInputValue] = useState('');
    const [clientInputValue, setClientInputValue] = useState(''); /* edited (clientInputValue added) */
    const [deliverAddresses, setDeliverAddresses] = useState([]);
    const [allClients, setAllClients] = useState([]); /* edited (setAllClients added) Note: not all clients */
    const [selectedDeliveryAddress, setSelectedDeliveryAddress] = useState("");
    const [selectedPickUpAddress, setSelectedPickUpAddress] = useState("");
    const [selectedClient, setSelectedClient] = useState(""); /* edited (setSelectedClient added) */
    const [loading, setLoading] = useState(false);
    const [loadingSubmit, setLoadingSubmit] = useState(false);

    const [openAssignDialog, setOpenAssignDialog] = useState(false);
    const [assignAddressClint, setAssignAddressClint] = useState(false);
    const [assignDeliveryParams, setAssignDeliveryParams] = useState(false);

    /* edited (validation variables) */

    const [beginValidation, setBeginValidation] = useState(false);
    const [selectedClientCheck, setSelectedClientCheckShow] = useState(false);
    const [deliveryCostCheck, setDeliveryCostCheckShow] = useState(false);
    const [selectedPickUpAddressCheck, setSelectedPickUpAddressCheckShow] = useState(false);
    const [amountCheck, setAmountCheck] = useState(false);
    const [selectedDeliveryAddressCheck, setSelectedDeliveryAddressCheckShow] = useState(false);

    const [deliverTypeArr, setDeliverTypeArr] = useState([{ name: translate("ORDERS." + DeliveryTypes[1]), type: "1", active: "active" }, { name: translate("ORDERS." + DeliveryTypes[2]), type: "2", active: "" }]);

    const [myNetwork, setMyNetwork] = useState([]);
    const [networkInputValue, setNetworkInputValue] = useState('');

    const [orderSubmitted, setOrderSubmitted] = useState(false);
    const [createdOrderId, setCreatedOrderId] = useState("");
    const [assignLoading, setAssignLoading] = useState(false);
    const [offers, setOffers] = useState([]);
    const [availableTransporters, setAvailableTransporters] = useState([]);

    const deliveryCostRef = useRef();
    const orderIdRef = useRef();
    const notesRef = useRef();
    const heightRef = useRef();
    const widthRef = useRef();
    const lengthRef = useRef();
    const weightRef = useRef();
    const codAmountRef = useRef();
    const pickAmountRef = useRef();
    const intl = useIntl();

    /* useEffect(() => {

        setNetworkLoading(true);

        // console.log(selectedPickUpAddress.cityId + " -> " + selectedDeliveryAddress.cityId);

        GetAllClientNetworkMembers(selectedPickUpAddress.cityId, selectedDeliveryAddress.cityId).then((res) => {

            setNetworkLoading(false);

            setMyNetwork(res.data.membersData); // .filter(member => member.FullName.toLowerCase().replaceAll("أ", 'ا').replaceAll("إ", 'ا').includes(networkInputValue.toLowerCase()) || member.PhoneNumber.includes(networkInputValue))
        });

    }, [networkInputValue, selectedPickUpAddress, selectedDeliveryAddress]); */

    useEffect(() => {
        const timer = setTimeout(() => {
            setLoading(true);

            GetDefinedAddresses(inputValue).then((res) => {
                // console.log(res.data)
                setLoading(false);
                setDeliverAddresses(res.data);
            });
        }, 500)

        return () => {
            return clearTimeout(timer)
        }
    }, [inputValue, open]);

    /* edited (get all clients) */
    useEffect(() => {
        const timer = setTimeout(() => {
            setLoading(true);

            GetTransporterClients().then((res) => {
                setLoading(false);

                setAllClients(res.data.membersData.filter(client => client.FullName.toLowerCase().replaceAll("أ", 'ا').replaceAll("إ", 'ا').includes(clientInputValue.toLowerCase())));
            });
        }, 500)

        return () => {
            return clearTimeout(timer)
        }
    }, [clientInputValue, open]);

    /* edited (show and hide validation error messages handles) */

    function showValidation() {
        // selectedClient && deliveryCostRef && selectedPickUpAddress && selectedDeliveryAddress && deliveryCostRef.current.value > 0

        if (isTransporter()) {
            if (selectedClient) {
                setSelectedClientCheckShow(false)
            } else {
                setSelectedClientCheckShow(true)
            }

            if (deliveryCostRef && deliveryCostRef.current.value > 0) {
                setDeliveryCostCheckShow(false)
            } else {
                setDeliveryCostCheckShow(true)
            }
        }

        if (selectedPickUpAddress) {
            setSelectedPickUpAddressCheckShow(false)
        } else {
            setSelectedPickUpAddressCheckShow(true)
        }

        if (selectedDeliveryAddress) {
            setSelectedDeliveryAddressCheckShow(false)
        } else {
            setSelectedDeliveryAddressCheckShow(true)
        }

        if (codAmountRef.current?.value && codAmountRef.current?.value >= 3) {
            setAmountCheck(false)
        } else {
            setAmountCheck(true)
        }

        // console.log("show validation");
    }

    function hideValidation() {
        setBeginValidation(false);
        setSelectedClientCheckShow(false);
        setDeliveryCostCheckShow(false);
        setSelectedPickUpAddressCheckShow(false);
        setSelectedDeliveryAddressCheckShow(false);
        setAmountCheck(false);

        // console.log("hide validation");
    }

    function setCloseAssignDialog() {
        setOpenAssignDialog(false);
    }

    function setCloseMainDialog() {
        setOpen(false);
    }

    const handleDeliveryTypeClick = (index, type) => {
        setDeliveryType(type);

        let tempArr = [...deliverTypeArr];

        for (let i = 0; i < tempArr.length; i++) {
            tempArr[i].active = "";
        }

        tempArr[index].active = "active";

        setDeliverTypeArr(tempArr);
    }

    const createOrderHandler = (DeliveryParams, CreatedBy, AddressClint) => {
        CreateNewOrderReq(JSON.stringify(DeliveryParams), CreatedBy, JSON.stringify(AddressClint)).then((res) => {
            /* edited (test data) */
            // console.log(res.data); // {"server_response":[{"OrderId":"936"}]}

            /* to be edited (to print order by returned id) */

            // orderIdRef.current = JSON.parse(res.data.replace("}{", ",")).server_response[0].OrderId;   // <--------------------------------------------------------------------------- to test
            //https://therichpost.com/reactjs-convert-html-into-pdf-working-functionality/

            /*  if (res.data == "Blocked") {
                 dispatch(toastNotification("Warning!", translate("GENERAL.BLOCKED"), "warning"));
             } */

            let orderId = res.data.split('OrderId":"')[1].split('"')[0];
            // console.log(orderId);
            setCreatedOrderId(orderId);

            // setOpenConf(true);

            setOrderSubmitted(true);

            if (!isTransporter()) {
                // getClientNetwork();
                // location.href = "#availableOffers";

                history.push("/account/Order/" + orderId);
            }

            setLoadingSubmit(false);

            if (isTransporter()) {
                history.push("/account/Order/" + orderId);
            }
        });
    }

    const getClientNetwork = () => {

        GetAllClientNetworkMembers(selectedPickUpAddress.cityId, selectedDeliveryAddress.cityId).then((res) => {

            setMyNetwork(res.data.membersData); // .filter(member => member.FullName.toLowerCase().replaceAll("أ", 'ا').replaceAll("إ", 'ا').includes(networkInputValue.toLowerCase()) || member.PhoneNumber.includes(networkInputValue))

            // merge with previous 
        });
    }

    const getoOffers = () => {
        checkTripCost(orderId, isTransporter()).then((tripCostRes) => {

            // console.log(tripCostRes); // temp test

            if (tripCostRes && tripCostRes !== "CostNotSend") {
                setOffers(isTransporter() ? tripCostRes.server_response[0] : tripCostRes.CostDetail);
                setCostSet(true);
            } else {
                setCostSet(false);
            }
        });
    }

    const [validated, setValidated] = useState(false);

    const [provinces, setProvinces] = useState([]);
    const [governorates, setGovernorates] = useState([]);
    const [cities, setCities] = useState([]);
    const [areas, setAreas] = useState([]);

    /* useEffect(() => {
        // provinces, governorates, cities, areas
        GetCitiesArea("provinces", -1).then((res) => {
            // console.log(res.data.server_response);
            setProvinces(res.data.server_response);
        })
    }, []) */

    const getProvences = () => {
        GetCitiesArea("provinces", -1).then((res) => {
            // console.log(res.data.server_response);
            setProvinces(res.data.server_response);
        })
    }

    const updateSubLevel = (type, superId) => {
        GetCitiesArea(type, superId, 1).then((res) => {
            // console.log(res.data.server_response)

            if (type === "governorates") {
                setGovernorates([]);
                setCities([]);
                setAreas([]);
                setGovernorates(res.data.server_response);
            } else if (type === "cities") {
                setCities([]);
                setAreas([]);
                setCities(res.data.server_response);
            } else {
                setAreas([]);
                setAreas(res.data.server_response);
            }
        })
    }

    return (
        <>
            {/* upper background */}
            <div className="upperBackground">
            </div>

            <div className="mainContainer" style={{ height: "80%" }}>
                <Container fluid className="pb-5">
                    <Row className="h2 d-flex justify-content-center" style={{ marginTop: "150px", marginBottom: "120px", color: "#26a69a" }}>
                        {translate("ORDERS.CREATE_ORDER")}
                    </Row>

                    {/* ------------------( transporter clients )------------------ */}
                    {isTransporter() && <Row className="mb-5">
                        <Col>
                            <Card className="rounded-3 shadow">
                                <Card.Header style={styles.cardHeaderLg}>
                                    <Card.Title className="mt-2">{translate("TEMP.MY_CLIENTS")}</Card.Title>
                                </Card.Header>
                                <Card.Body className="mb-3 mt-5">
                                    <div className="row">
                                        <div className="col-lg-9">

                                            <ClientDropdown
                                                loading={loading}
                                                clients={allClients}
                                                onSearch={setClientInputValue}
                                                onSelect={setSelectedClient}
                                                selectedClient={selectedClient}
                                            />

                                            {/*  {beginValidation && selectedClientCheck && <span style={{ color: "red" }}>required!</span>} */}

                                        </div>
                                        <div className="col-lg-3">
                                            <Form.Control type="number" style={{ marginTop: "9px" }} className="rounded-22 shadow" placeholder={intl.formatMessage({ id: "TEMP.DELIVERY_COST" })} ref={deliveryCostRef} />

                                            {/* {beginValidation && deliveryCostCheck && <span style={{ color: "red" }}>required!</span>} */}
                                        </div>
                                    </div>
                                </Card.Body>
                            </Card>
                        </Col>
                    </Row>}

                    {/* ------------------( order info )------------------ */}
                    <Row className="mb-5">
                        <Col>
                            <Card className="rounded-3 shadow">
                                <Card.Header style={styles.cardHeaderLg}>
                                    <Card.Title className="mt-2">{translate("TEMP.ORDER_INFO")}</Card.Title>
                                </Card.Header>

                                <Form id="orderForm" validated={validated} noValidate onSubmit={(event) => {
                                    event.preventDefault();
                                    event.stopPropagation();

                                    const formData = new FormData(event.target), formDataObj = Object.fromEntries(formData.entries());

                                    // console.log(formDataObj);

                                    const form = event.currentTarget;
                                    if (form.checkValidity() === true) {
                                        // if all set, create order here: (get data from formDataObj)

                                        let CreatedBy = "";

                                        let DeliveryParams = {
                                            deliveryWay: deliveryType,
                                            TypeLoad: packageType,
                                            CostLoad: (deliveryType === "2" && codAmountRef.current.value) || (deliveryType === "4" && pickAmountRef.current.value) || "", // 2 or 4
                                            DetailsLoad: notesRef.current.value,
                                            LengthLoad: lengthRef.current?.value || "",
                                            WidthLoad: widthRef.current?.value || "",
                                            HeightLoad: heightRef.current?.value || "",
                                            WeightLoad: weightRef.current?.value || "",
                                            qrCode: ""
                                        };

                                        const AddressClint = {
                                            IdCity: selectedPickUpAddress.cityId,  // from selected pick up address
                                            IdArea: selectedPickUpAddress.areaId,  // from selected pick up address
                                            OtherDetails: selectedPickUpAddress.details, // details from selected pickup address
                                            LatSender: selectedPickUpAddress.latitude, // from selected pick up address
                                            LongSender: selectedPickUpAddress.longitude, //  from selected pick up address
                                            SenderAddressId: selectedPickUpAddress.id, // from selected pick up address

                                            IdCityDes: selectedDeliveryAddress.cityId, // from selected delivery to address
                                            IdAreaDes: selectedDeliveryAddress.areaId, // from selected delivery to address
                                            OtherDetailsDes: selectedDeliveryAddress.details, // details from selected delivery to address
                                            LatReciver: selectedDeliveryAddress.latitude, // from selected delivery to address
                                            LongReciver: selectedDeliveryAddress.longitude, // from selected delivery to address
                                            ReciverAddressId: selectedDeliveryAddress.id,// from selected delivery to address
                                            ReceiverAddressNum: selectedDeliveryAddress.phone_number
                                        };

                                        if (isTransporter()) {
                                            setLoadingSubmit(true);

                                            CreatedBy = "Transporter";

                                            DeliveryParams.ClientMobileNumber = selectedClient.PhoneNumber;
                                            DeliveryParams.DeliveryPrice = deliveryCostRef.current.value;

                                            // createOrderHandler(DeliveryParams, CreatedBy, AddressClint);
                                        } else {
                                            setLoadingSubmit(true);

                                            CreatedBy = "Client";

                                            // createOrderHandler(DeliveryParams, CreatedBy, AddressClint);
                                        }
                                    }

                                    setValidated(true);
                                }}>
                                    <ListGroup variant="flush" className="mt-5">

                                        {/* ------------------( package types )------------------ */}
                                        <ListGroup.Item className="py-4">
                                            <div className="container-fluid">
                                                <div className="row">
                                                    <div className="mb-2 h5" style={{ color: "#26a69a" }}>
                                                        {translate("ORDERS.WHAT_TO_TRANS")}
                                                    </div>

                                                    <div className="col-lg-4">
                                                        <Dropdown
                                                            style={{ width: "100%" }}
                                                            className="togo-dropdown shadow"
                                                            onSelect={(eve) => {
                                                                setPackageType(eve);
                                                            }}>
                                                            <Dropdown.Toggle variant="" className="w-100 text-start d-flex align-items-center">
                                                                {React.createElement(PackageTypesIcons[packageType], { style: { width: "20px", height: "20px" }, className: "me-1" })}
                                                                <div style={{ width: "97%" }}>
                                                                    {translate("ORDERS." + PackageTypes[packageType])}
                                                                </div>
                                                            </Dropdown.Toggle>

                                                            <Dropdown.Menu className="w-100">
                                                                <Dropdown.Item eventKey="2" className="d-flex">
                                                                    <FoodIcon style={{ width: "20px", height: "20px" }} className="me-1" />
                                                                    {translate("ORDERS." + PackageTypes[2])}
                                                                </Dropdown.Item>
                                                                <Dropdown.Item eventKey="4" className="d-flex">
                                                                    <BigBoxIcon style={{ width: "20px", height: "20px" }} className="me-1" />
                                                                    {translate("ORDERS." + PackageTypes[4])}
                                                                </Dropdown.Item>
                                                                <Dropdown.Item eventKey="3" className="d-flex">
                                                                    <MedBoxIcon style={{ width: "20px", height: "20px" }} className="me-1" />
                                                                    {translate("ORDERS." + PackageTypes[3])}
                                                                </Dropdown.Item>
                                                                <Dropdown.Item eventKey="1" className="d-flex">
                                                                    <SmBoxIcon style={{ width: "20px", height: "20px" }} className="me-1" />
                                                                    {translate("ORDERS." + PackageTypes[1])}
                                                                </Dropdown.Item>
                                                            </Dropdown.Menu>
                                                        </Dropdown>
                                                    </div>

                                                    {packageType !== "1" && packageType !== "2" && <div className="col-lg-8" style={{ marginTop: "2px" }}>
                                                        <div className="row">
                                                            <div className="col-md-3">
                                                                <Form.Group>
                                                                    <Form.Control
                                                                        name="height"
                                                                        type="number"
                                                                        className="rounded-22 input-inner-shadow"
                                                                        placeholder={intl.formatMessage({ id: "TEMP.HEIGHT" })}
                                                                        ref={heightRef}
                                                                    />
                                                                </Form.Group>
                                                            </div>
                                                            <div className="col-md-3">
                                                                <Form.Control
                                                                    name="width"
                                                                    type="number"
                                                                    className="rounded-22 input-inner-shadow"
                                                                    placeholder={intl.formatMessage({ id: "TEMP.WIDTH" })}
                                                                    ref={widthRef}
                                                                />
                                                            </div>
                                                            <div className="col-md-3">
                                                                <Form.Control
                                                                    name="length"
                                                                    type="number"
                                                                    className="rounded-22 input-inner-shadow"
                                                                    placeholder={intl.formatMessage({ id: "TEMP.LENGTH" })}
                                                                    ref={lengthRef}
                                                                />
                                                            </div>
                                                            <div className="col-md-3">
                                                                <Form.Control
                                                                    name="weight"
                                                                    type="number"
                                                                    className="rounded-22 input-inner-shadow"
                                                                    placeholder={intl.formatMessage({ id: "TEMP.WEIGHT" })}
                                                                    ref={weightRef}
                                                                />
                                                            </div>
                                                        </div>
                                                    </div>}
                                                </div>
                                            </div>
                                        </ListGroup.Item>

                                        {/* ------------------( delivery type )------------------ */}
                                        <ListGroup.Item className="py-4" style={{ backgroundColor: "#ededed" }}>
                                            <div className="container-fluid">
                                                <div className="d-flex align-items-center mb-2 h5" style={{ color: "#26a69a" }}>
                                                    <DeliveryTruckIcon style={{ width: "20px", height: "20px" }} className="me-1" />
                                                    {translate("TEMP.SELECT_DELIVERY_TYPE")}
                                                </div>

                                                <div className="row">
                                                    <div className="col-lg-10">
                                                        <div className={"toggleButtonsContainer"}>
                                                            {
                                                                deliverTypeArr.map((item, index) => {
                                                                    return <div key={index} className={"toggleButton " + item.active + ((localStorage.getItem("Language") || "en") === "en" ? " me-2" : " ms-2")} onClick={() => { handleDeliveryTypeClick(index, item.type) }}>
                                                                        <div className="radio"><div className="innerRadio"></div></div> {item.name}
                                                                    </div>
                                                                })
                                                            }
                                                        </div>
                                                    </div>
                                                    <div className="col-lg-2">
                                                        {deliveryType === "2" && <>
                                                            <Form.Control
                                                                required={true}
                                                                name="codAmount"
                                                                min="3"
                                                                type="number"
                                                                className="rounded-22 input-inner-shadow" style={{ width: "100%" }}
                                                                placeholder={intl.formatMessage({ id: "ORDERS.AMOUNT" })}
                                                                ref={codAmountRef}
                                                            />

                                                            {/* {beginValidation && amountCheck && <span style={{ color: "red" }}>required! (minimum 3 NIS)</span>} */}
                                                        </>}
                                                    </div>
                                                </div>
                                            </div>
                                        </ListGroup.Item>

                                        {/* ------------------( addresses )------------------ */}
                                        <ListGroup.Item className="py-4">
                                            <div className="container-fluid">
                                                <div className="row">
                                                    <div className="d-flex align-items-center mb-2 h5" style={{ color: "#26a69a" }}>
                                                        <LocationIcon style={{ width: "20px", height: "20px" }} className="me-1" />
                                                        {translate("ORDERS.PICKUP_ADDRESS")}
                                                    </div>
                                                </div>
                                                <div className="row">
                                                    <div className="col-lg-10">

                                                        {/* <AddressDropdown
                                                            loading={loading}
                                                            addresses={deliverAddresses}
                                                            onSearch={setInputValue}
                                                            onSelect={setSelectedPickUpAddress}
                                                            selectedAddress={selectedPickUpAddress}
                                                        /> */}

                                                        {/* receiver-full-name, address-name, receiver-phone-number */}

                                                        <Table>
                                                            <thead>
                                                                <tr>
                                                                    <th>Name</th>
                                                                    <th>Address</th>
                                                                    <th>Mobile Number</th>
                                                                    <th>Phone Number</th>
                                                                </tr>
                                                            </thead>
                                                            <tbody>
                                                                <tr>
                                                                    <td>name</td>
                                                                    <td>address</td>
                                                                    <td>number</td>
                                                                    <td>number</td>
                                                                </tr>
                                                            </tbody>
                                                        </Table>

                                                        {/* {beginValidation && selectedPickUpAddressCheck && <span style={{ color: "red" }}>required!</span>} */}

                                                    </div>

                                                    <div className="col-lg-2 d-flex justify-content-center">
                                                        <Button className="btn-grad-circle"><i className="bi bi-arrow-repeat"></i></Button>
                                                    </div>

                                                    {/* <div className="w-50 m-inline-e-2">
                                                    <div className="d-flex align-items-center mb-2 h5" style={{ color: "#26a69a" }}>
                                                        <SendIcon style={{ width: "20px", height: "20px" }} className="me-1" />
                                                        <p>{translate("ORDERS.DELIVER_TO")}</p>
                                                    </div>

                                                    <AddressDropdown
                                                        loading={loading}
                                                        addresses={deliverAddresses}
                                                        onSearch={setInputValue}
                                                        onSelect={setSelectedDeliveryAddress}
                                                        selectedAddress={selectedDeliveryAddress}
                                                    />

                                                    {beginValidation && selectedDeliveryAddressCheck && <span style={{ color: "red" }}>required!</span>}

                                                </div>
                                                <CreateAddress>
                                                    <div style={{ cursor: "pointer", marginTop: "30px" }} className="btn-grad-circle p-2">
                                                        <AddIcon w={6} h={6} color="gray.50" />
                                                    </div>
                                                </CreateAddress> */}
                                                </div>
                                            </div>
                                        </ListGroup.Item>

                                        {/* ------------------(  )------------------ */}
                                        <ListGroup.Item className="py-4" style={{ backgroundColor: "#ededed" }}>
                                            <div className="container-fluid">
                                                <div className="d-flex align-items-center h5" style={{ color: "#26a69a" }}>
                                                    <AttachmentIcon style={{ width: "20px", height: "20px" }} className="me-1" />
                                                    {translate("ORDERS.DELIVER_TO")}
                                                </div>

                                                <div className="row">
                                                    <div className="col-lg-6">
                                                        <Form.Group>
                                                            <FloatingLabel className="mb-3" controlId="placeName" label={translate("ORDERS.ADDRESS_NAME")}>
                                                                <Form.Control className="rounded-22 input-inner-shadow" type="text" placeholder="..." name="placename" required />
                                                                <Form.Control.Feedback type="invalid">
                                                                    Please add place name
                                                                </Form.Control.Feedback>
                                                            </FloatingLabel>
                                                        </Form.Group>
                                                    </div>
                                                    <div className="col-lg-6">
                                                        <Form.Group>
                                                            <FloatingLabel className="mb-3" controlId="userPhone" label={translate("ORDERS.ADDRESS_PHONE")}>
                                                                <Form.Control className="rounded-22 input-inner-shadow" type="tel" placeholder="..." name="phone" pattern="^05[0-9]{8}?$" required />
                                                                <Form.Control.Feedback type="invalid">
                                                                    Please enter a valid phone number example 0568000000
                                                                </Form.Control.Feedback>
                                                            </FloatingLabel>
                                                        </Form.Group>
                                                    </div>
                                                </div>

                                                <div className="row">
                                                    <Col lg={3} className="mb-4">
                                                        <Form.Label>{translate("TEMP.PROVINCE")}:</Form.Label>
                                                        <Form.Select style={{ cursor: "pointer" }} className="shadow" name="province" required aria-label="Default select example"
                                                            onClick={() => {
                                                                getProvences();
                                                            }}
                                                            onChange={(e) => {
                                                                updateSubLevel("governorates", e.target.value)
                                                            }}>
                                                            <option value={""} style={{ color: "lightgray" }}>Select Provice</option>
                                                            {
                                                                provinces.map((item, index) => {
                                                                    return <option key={index} value={item.id}>{/* item.id + " - " +  */item.name}</option>
                                                                })
                                                            }
                                                        </Form.Select>
                                                    </Col>
                                                    <Col lg={3} className="mb-4">
                                                        <Form.Label>{translate("TEMP.GOVERNORATE")}:</Form.Label>
                                                        <Form.Select style={{ cursor: "pointer" }} className="shadow" name="governorate" required aria-label="Default select example" onChange={(e) => {
                                                            updateSubLevel("cities", e.target.value)
                                                        }}>
                                                            <option value={""} style={{ color: "lightgray" }}>Select Governorate</option>
                                                            {
                                                                governorates.map((item, index) => {
                                                                    return <option key={index} value={item.id}>{/* item.id + " - " +  */item.name}</option>
                                                                })
                                                            }
                                                        </Form.Select>
                                                    </Col>
                                                    <Col lg={3} className="mb-4">
                                                        <Form.Label>{translate("TEMP.CITY")}:</Form.Label>
                                                        <Form.Select style={{ cursor: "pointer" }} className="shadow" name="city" required aria-label="Default select example" onChange={(e) => {
                                                            updateSubLevel("areas", e.target.value)
                                                        }}>
                                                            <option value={""} style={{ color: "lightgray" }}>Select City</option>
                                                            {
                                                                cities.map((item, index) => {
                                                                    return <option key={index} value={item.id}>{/* item.id + " - " +  */item.name}</option>
                                                                })
                                                            }
                                                        </Form.Select>
                                                    </Col>
                                                    <Col lg={3} className="">
                                                        <Form.Label>{translate("TEMP.AREA")}:</Form.Label>
                                                        <Form.Select style={{ cursor: "pointer" }} className="shadow" name="area" required aria-label="Default select example">
                                                            <option value={""} style={{ color: "lightgray" }}>Select Area</option>
                                                            {
                                                                areas.map((item, index) => {
                                                                    return <option key={index} value={item.id}>{/* item.id + " - " +  */item.name}</option>
                                                                })
                                                            }
                                                        </Form.Select>
                                                    </Col>
                                                </div>

                                                <div className="row">
                                                    <div className="col-lg-6">
                                                        <Form.Group>
                                                            <FloatingLabel className="mb-3" controlId="userAddress" label={translate("ORDERS.ADDRESS")}>
                                                                <Form.Control className="rounded-22 input-inner-shadow" type="text" placeholder="..." name="address" />
                                                            </FloatingLabel>
                                                        </Form.Group>
                                                    </div>
                                                    <div className="col-lg-6">
                                                        <Form.Group>
                                                            <FloatingLabel className="mb-3" controlId="addressInfo" label={translate("ORDERS.ADDRESS_INFO")}>
                                                                <Form.Control className="rounded-22 input-inner-shadow" type="text" placeholder="..." name="addressinfo" />
                                                            </FloatingLabel>
                                                        </Form.Group>
                                                    </div>
                                                </div>
                                            </div>
                                        </ListGroup.Item>

                                        {/* ------------------( order notes )------------------ */}
                                        <ListGroup.Item className="py-4">
                                            <div className="container-fluid">
                                                <div className="d-flex align-items-center mb-2 h5" style={{ color: "#26a69a" }}>
                                                    <AttachmentIcon style={{ width: "20px", height: "20px" }} className="me-1" />
                                                    {translate("ORDERS.ATTACHMENTS_LABEL")}
                                                </div>

                                                <Form.Control name="notes" type="text" className="mt-2 rounded-22 input-inner-shadow" placeholder={intl.formatMessage({ id: "TEMP.NOTES" })} ref={notesRef} />
                                            </div>
                                        </ListGroup.Item>
                                    </ListGroup>

                                    {true ? <Button
                                        className="btn-grad"
                                        style={{ width: "30%", float: "right" }}
                                        disabled={loadingSubmit}
                                        type="submit"
                                    >
                                        {loadingSubmit && <Spinner animation="border" size="sm" />}
                                        {isTransporter() ? translate("ORDERS.CREATE_ORDER") : translate("ORDERS.SUBMIT_ORDER")}
                                    </Button> : <span className="h6" style={{ color: "#26a69a", float: "right" }}>{isTransporter() ? translate("TEMP.SUCCISSFULLY_PUBLISHED") : translate("TEMP.SUCCISSFULLY_PUBLISHED_CLIENT")}</span>}
                                </Form>
                                <Card.Footer>

                                </Card.Footer>
                            </Card>
                        </Col>
                    </Row>

                    {/* ------------------( network & offers )------------------ */}
                    {/* {orderSubmitted && !isTransporter() && <Row>
                        <Col>
                            <Card className="rounded-3 shadow">
                                <Card.Header style={styles.cardHeaderLg}>
                                    <Card.Title className="mt-2">{translate("TEMP.NETWORK_OFFERS")}</Card.Title>
                                </Card.Header>

                                <Card.Body className="mt-5" id="availableOffers">
                                    <Table>
                                        <thead>
                                            <tr>
                                                <th scope="col"></th>
                                                <th scope="col">{translate("TEMP.NAME")}</th>
                                                <th scope="col">{translate("TEMP.PHONE")}</th>
                                                <th scope="col">{translate("TEMP.PRICE")}</th>
                                                <th scope="col"></th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {
                                                myNetwork.map((member, index) => {
                                                    return <tr key={index}>
                                                        <td>
                                                            <div // image
                                                                style={{
                                                                    background: "linear-gradient(90deg, #26a69a, #69d4a5)",
                                                                    width: "70px",
                                                                    height: "70px",
                                                                    borderRadius: "45%",
                                                                }}

                                                                className="align-self-center d-flex justify-content-center"
                                                            >
                                                                <img
                                                                    style={{
                                                                        width: "60px",
                                                                        height: "60px",
                                                                        objectFit: "cover"
                                                                    }}
                                                                    className="rounded-circle align-self-center"
                                                                    src={`${imgBaseUrl}${member.Image}`}
                                                                    alt={member.PhoneNumber}
                                                                />
                                                            </div>
                                                        </td>
                                                        <td>{member.FullName}</td>
                                                        <td>{member.PhoneNumber}</td>
                                                        <td>{member.deliveryPrice + " NIS"}</td>
                                                        <td>
                                                            <Button disabled={assignLoading ? true : false} className="btn-grad" onClick={() => {
                                                                setAssignLoading(true);

                                                                ClientAssignOrder(createdOrderId, member.CustomerId, member.deliveryPrice).then((res) => {
                                                                    // console.log(res.data);

                                                                    setAssignLoading(false);
                                                                    history.push("/account/Order/" + createdOrderId);
                                                                })
                                                            }}>
                                                                {assignLoading && <Spinner animation="border" size="sm" />}
                                                                {translate("TEMP.ASSIGN")}
                                                            </Button>
                                                        </td>
                                                    </tr>
                                                })
                                            }
                                        </tbody>
                                    </Table>
                                </Card.Body>
                            </Card>
                        </Col>
                    </Row>} */}

                    {openAssignDialog && <SubmitAndAssignDialog
                        onSuccess={() => props.onSuccess()}
                        setCloseAssignDialog={setCloseAssignDialog}
                        setCloseMainDialog={setCloseMainDialog}
                        loadingSubmit={loadingSubmit}
                        assignDeliveryParams={assignDeliveryParams}
                        assignAddressClint={assignAddressClint} />}

                    <Modal
                        show={openConf}
                        onHide={() => {

                            setOpenConf(false);
                        }}
                        animation={false}
                        backdrop="static"
                    >
                        <Modal.Header closeButton>
                            <Modal.Title>Confirmation</Modal.Title>
                        </Modal.Header>

                        <Modal.Body>
                            <p>Would you like to print a waybill ?</p>
                        </Modal.Body>

                        <Modal.Footer>
                            <Button
                                variant="secondary"
                                onClick={() => {
                                    setOpenConf(false);
                                }}
                            >
                                No
                            </Button>

                            <Button
                                variant="primary"
                                onClick={() => {
                                    setOpenConf(false);
                                    window.open("/account/bill-for-order/" + orderIdRef.current + "?print=true")
                                }}
                            >
                                Yes
                            </Button>
                        </Modal.Footer>
                    </Modal>

                </Container>
            </div>
        </>
    )
}
