import React, {useEffect, useState} from "react";
import "./Header.css";
import {
    Menu,
    Button,
    MenuButton,
    MenuList,
    MenuItem,
    Box,
    Icon,
    Text,
    Flex,
    Image
} from "@chakra-ui/react";
import {SettingsIcon} from '@chakra-ui/icons';
import {useSelector, useDispatch} from "react-redux";
import {useHistory} from "react-router";
import {IoIosWallet, IoIosCard, IoMdPerson, IoIosExit} from 'react-icons/io';
import {IoCaretBack} from 'react-icons/io5';
import {Link} from "react-router-dom";
import {getWallet} from "../../APIs/ProfileAPIs";
import {setWallet, toastMessage} from "../../Actions/GeneralActions";
import {LOGOUT} from "../../Actions/ActionsTypes";
import whiteLogo from '../../assets/whiteLogo.png';
import translate from "../../i18n/translate";
import LanguageSelector from "../LanguageSelector/LanguageSelector";
import Loader from "../Loader/Loader";


const styles = {
    settingsButton: {
        variant: "ghost",
        size: "lg",
        iconSpacing: 0,
        lineHeight: "0.2",
        _active: {
            background: "none",
            transform: "scale(1.3)",
            color: "black",
            opacity: "0.5"
        },
        _hover: {transform: "scale(1.3)"},
        _focus: {outline: "none"}
    },
    actionsContainer: {
        position: "absolute",
        top: "1rem",
        right: "1rem",
        alignItems: "center",
        background: "#00000036",
        borderRadius: "29px"
    },
    headerItemsContainer: {
        alignItems: "center",
        cursor: "pointer",
        px: "1.5rem",
        _hover: {
            transform: "scale(1.1)",
            underline: "unset",
        }
    },
    horizontalBreakLine: {
        w: "3px",
        h: "20px",
        bgColor: "white",
        borderRadius: 999,
        opacity: 0.5
    },
    link: {
        color: "white",
        textDecoration: "none"
    },
    backBtnContainer: {
        position: "absolute",
        left: "1rem",
        top: "1rem",
        alignItems: "center",
        pt: "0.2rem"
    },
    logo: {
        position: "absolute",
        top: "50%",
        left: "50%",
        transform: "translate(-50%, -50%)",
        _hover: {
            opacity: 0.5,
            transform: "scale(1.2) translate(-40%, -40%)"
        }
    },
    headerStyle: {
        position: "relative",
        background: "linear-gradient(to left, #26A69A,#69D4A5)",
        color: "#fff",
        textAlign: "center",
        padding: "40px 0",
        height: "140px",
        direction: "ltr"
    }
};

function Header() {
    const authenticated = useSelector(state => state.general.authenticated);
    //const [wallet, setWallet] = useState(null);
    const wallet = useSelector(state => state.general.wallet);
    const history = useHistory();
    const [isHomePage, setIsHomePage] = useState(history.location.pathname === '/orders' || history.location.pathname === '/');
    const dispatch = useDispatch();

    const handleLogout = () => {
        dispatch({
            type: LOGOUT
        });
        localStorage.removeItem("userId");
        localStorage.removeItem("TokenDevice");
        localStorage.removeItem("UserType");
        localStorage.removeItem("userId");
        history.push("/");
    };

    useEffect(() => {
        if (authenticated) {
            getWallet().then(({data: {server_response}}) => {
                dispatch(setWallet(server_response[0].TransporterBalance));
            }).catch(err => {
                dispatch(toastMessage(err));
            })
        }
        return () => setWallet(null);
    }, [authenticated, dispatch]);

    useEffect(() => {
        let historyUnListen = history.listen(location => {
            setIsHomePage(location.pathname === '/account/orders' || location.pathname === '/');
        });
        return () => historyUnListen();
    }, [history]);

    return (
        <header style={styles.headerStyle} className="togo-header">
            {authenticated && (
                <Box>
                    {!isHomePage && <Flex {...styles.backBtnContainer}>
                        <Flex {...styles.headerItemsContainer} onClick={() => history.goBack()}>
                            <Icon as={IoCaretBack} mr={2} fontSize="2xl"/>
                            <Text>{translate("HEADER.BACK")}</Text>
                        </Flex>
                    </Flex>}
                    <Flex {...styles.actionsContainer}>
                        <Link to='/account/main/financial-transaction' style={styles.link}>
                            <Flex {...styles.headerItemsContainer}>
                                <Icon as={IoIosWallet} fontSize="2xl"/>
                                {isNaN(wallet) ? <Loader color="white" width="40px" height="40px"/> : <Text fontSize="14px">{wallet} NIS</Text>}
                            </Flex>
                        </Link>
                        <Box {...styles.horizontalBreakLine}/>
                        <LanguageSelector/>
                        <Box {...styles.horizontalBreakLine}/>
                        <Menu>
                            <MenuButton {...styles.settingsButton} as={Button} rightIcon={<SettingsIcon/>}/>
                            <MenuList color="black">
                                <MenuItem icon={<IoMdPerson/>}
                                          onClick={() => history.push("/account/account-details")}>{translate("HEADER.ACCOUNT_PROFILE")}</MenuItem>
                                <MenuItem icon={<IoIosCard/>}
                                          onClick={() => history.push("/")}>{translate("HEADER.PAYMENT_INFO")}</MenuItem>
                                <MenuItem icon={<IoIosExit/>}
                                          onClick={handleLogout}>{translate("HEADER.LOGOUT")}</MenuItem>
                            </MenuList>
                        </Menu>
                    </Flex>
                </Box>
            )}
            <Link to="/">
                <Image
                    {...styles.logo}
                    src={whiteLogo}
                    w={100}/>
            </Link>
        </header>
    );
}

export default Header;
