import React from "react";
import {Redirect, Route} from "react-router-dom";

export const PrivateRoute = ({component: Component, ...rest}) => {
    let TokenDevice = localStorage.getItem('TokenDevice');
    let signInPath = "/account/signin";
    let mainInPath = "/account/main/";

    return (<Route {...rest} render={(props) => {
            const {location: {pathname}} = props;
            return !!TokenDevice ? (pathname === signInPath ? <Redirect to={mainInPath}/> : <Component {...props} />) :
                (pathname === signInPath ? <Component {...props} /> : <Redirect to={signInPath}/>);
        }}/>
    );
};
