import React from 'react';
import { Box } from "@chakra-ui/react";
import Header from "../../components/Header/Header";
import { PrivateRoute } from "../../components/PrivateRoute/PrivateRoute";
import Main from "../../components/Orders/Main";
import OrderDetails from "../../components/OrderDetails/OrderDetails";
import AccountDetails from "../AccountDetails/AccountDetails";
import WalletContainer from "../WalletContainer/WalletContainer";
import QRCodeGenerator from "../../components/QRCodeGenerator/QRCodeGenerator";
import WayBill from "../../components/WayBill";
import NetworkClient from "../../components/NetworkClient"; /* edited (import NetworkClient) */
// import Network from "../../components/Network"; /* edited (import Network) */
import Network from "../../components/CustomNetwork"; /* edited (import Network) */
import { isTransporter } from "../../Util"; /* edited (import isTransporter) */

import { io } from "socket.io-client";

export default function Account({ match: { path } }) {

    const [socket, setSocket] = useState(null);

    useEffect(() => {
        // if (localStorage.getItem("userId") != undefined) {
            const initScket = io("http://46.253.93.4:5000");
            initScket.emit("addUpdateUser", localStorage.getItem("userId"), localStorage.getItem("UserType"));
            setSocket(initScket);
        // }
    }, [])

    return (
        <>
            <Header socket={socket} />
            <Box className="root-container">
                <PrivateRoute exact path={`${path}/main/*`} component={Main} />
                <PrivateRoute path={`${path}/signin`} component={QRCodeGenerator} />
                <PrivateRoute path={`${path}/order/:id`} component={() => <OrderDetails socket={socket} />} />
                <PrivateRoute path={`${path}/account-details`} component={AccountDetails} />
                <PrivateRoute path={`${path}/wallet`} component={WalletContainer} />
                <PrivateRoute path={`${path}/bill-for-order/:id`} component={WayBill} />
                {/*<PrivateRoute path={`${path}/my-network`} component={isTransporter() ? Network : NetworkClient}/>*/}
                <PrivateRoute path={`${path}/my-network`} component={Network} />
                <PrivateRoute path={`${path}/print/:id`} component={WayBill} />
            </Box>
        </>
    );
};
