import React, { useEffect, useState } from "react";
import { getTransporterCitiesPrices, updateTransporterCitiesPrices } from '../../APIs/ProfileAPIs';

export default function CitiesPrices() {

    const [pricesArr, setPricesArr] = useState([]);

    useEffect(() => {
        getTransporterCitiesPrices().then((res) => {
            // console.log(res.data);
            setPricesArr(res.data.response);
        })
    }, [])

    return (
        <Container fluid className="p-5">
            <Row>
                <Col>
                    <Table>
                        <thead>
                            <tr>
                                <th>from</th>
                                <th>to</th>
                                <th>price</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                pricesArr.map((item, index) => {
                                    return <tr key={index}>
                                        <td style={{ borderBottom: (index+1)%51==0 && "2px solid red" }}>{item.fromName}</td>
                                        <td style={{ borderBottom: (index+1)%51==0 && "2px solid red" }}>{item.toName}</td>
                                        <td style={{ borderBottom: (index+1)%51==0 && "2px solid red" }}>
                                            <Form.Group className="d-flex justify-content-center" controlId={index}>
                                                <Form.Control style={{ width: "100px" }} type="number" placeholder={item.price} onChange={(e) => {
                                                    handleChange(item.id, e.target.value)
                                                }} />
                                            </Form.Group>
                                        </td>
                                    </tr>
                                })
                            }
                        </tbody>
                    </Table>
                </Col>
            </Row>
        </Container>
    )
}
