import React from "react";
import "./AdminPanel.css"
import { GetAllActiveOrders, GetAllClients, GetAllFinishedOrders, GetAllReturnedOrders, GetAllNewOrders, GetAllTransporters, getRecordsActions, getTotalBalance, getLastOrderId, getLastRecord, updateAdminWebNotificationToken } from "../../APIs/AdminPanelApis";
import { useEffect, useState } from "react";
import { ResponsivePie } from '@nivo/pie';
import OrdersCard from "./OrdersCard";
import UsersCard from "./UserCard";
import RecordsCard from "./RecordsCard";
import { useDispatch } from "react-redux";
import { useHistory } from "react-router";

//https://dribbble.com/shots/15254181-CRM-Dashboard-Managency
//https://dribbble.com/shots/16485287-Trustland-Logistic-Management-App

export default function AdminPanel() {

    const history = useHistory();

    let dispatch = useDispatch();

    const styles = {
        cardHeaderLg: {
            position: 'absolute',
            left: '20px',
            right: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold'
        },
        cardHeaderSm: {
            position: 'absolute',
            left: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold'
        }
    }

    const [orderStat, setOrderStat] = useState([]); // for orders stat. 
    const [userStat, setUserStat] = useState([]); // for users stat.
    const [newOrders, setNewOrders] = useState([]); // all newly created orders
    const [activeOrders, setActiveOrders] = useState([]); // all in process orders
    // const [finishedOrders, setFinishedOrders] = useState([]); // all finished/deleted orders
    const [exceptionOrders, setExceptionOrders] = useState([]); // all stuck/returned orders
    const [activeOrdersNum, setActiveOrdersNum] = useState(0); // number of active orders
    const [records, setRecords] = useState([]); // all system actions
    const [creditAmount, setCreditAmount] = useState(0); // total escrow, all system balance
    const [totalEscrowBalance, setTotalEscrowBalance] = useState(0); // total in escrow, active money
    const [allTransporters, setAllTransporters] = useState([]); // all transporters
    const [allClients, setAllClients] = useState([]); // all clients
    const [lastorderId, setLastorderId] = useState(); // last action record id, from system actions

    /* get last order id as number of total orders */
    useEffect(() => {
        getLastOrderId().then((res) => {
            setLastorderId(res.data);
        })
    }, []);

    /* get system actions */
    useEffect(() => {
        getRecordsActions().then((res) => {
            setRecords(res.data.records_list);
        });
    }, []);

    /* get total balance */
    useEffect(() => {
        getTotalBalance().then((res) => {

            setTotalEscrowBalance(res.data.server_response.data?.result.differ_balance);
            setCreditAmount(res.data.server_response.data?.result.credit_amount);
        });
    }, []);

    /* get all orders */
    useEffect(() => {
        // reset orders stat. to reset orders pie chart
        setOrderStat([]);

        // get all new orders
        GetAllNewOrders().then((res) => {
            setNewOrders(res.data.orders_list);
            setOrderStat((prev) => {
                return [...prev, {
                    "id": "new",
                    "label": "New",
                    "value": res.data.NumberOfOrders,
                }]
            });
        });

        // get all in-process orders
        GetAllActiveOrders().then((res) => {
            setActiveOrders(res.data.orders_list);
            setActiveOrdersNum(res.data.NumberOfOrders);
            setOrderStat((prev) => {
                return [...prev, {
                    "id": "active",
                    "label": "Active",
                    "value": res.data.NumberOfOrders,
                }]
            });
        });

        // get all stuck/returned orders
        GetAllReturnedOrders().then((res) => {
            // setFinishedOrders(res.data.orders_list);
            setExceptionOrders(res.data.orders_list);
            setOrderStat((prev) => {
                return [...prev, {
                    "id": "finished",
                    "label": "Finished",
                    "value": res.data.NumberOfOrders,
                }]
            });

        });
    }, []);

    useEffect(() => {
        // reset users stat. to reset users pie chart
        setUserStat([]);

        // get all transporters
        GetAllTransporters().then((res) => {
            let tempArr = res.data.transporters_list;

            setAllTransporters(tempArr);
            setUserStat((prev) => {
                return [...prev, {
                    "id": "transporter",
                    "label": "Transporters",
                    "value": res.data.NumberOfTransporters,
                }]
            });
        });

        // get all clients
        GetAllClients().then((res) => {
            let tempArr = res.data.clients_list;

            setAllClients(tempArr);
            setUserStat((prev) => {
                return [...prev, {
                    "id": "client",
                    "label": "Clients",
                    "value": res.data.NumberOfClients,
                }]
            });
        });
    }, []);

    return (
        <>
            <div className="d-flex w-100 panel-wrapper" style={{ backgroundColor: "#eaeced" }}>
                <div
                    className="bg-white mt-3 mb-3 ms-3 rounded-22 shadow admin-left-menu"
                >

                    <div className=" h-100">
                        <RecordsCard title="Records Log Table" list={records} />
                    </div>

                </div>
                <div className="w-100 ps-3" style={{ overflowX: "auto" }}>
                    <div className="w-100 d-flex">
                        <div
                            className="bg-white me-3 mt-3 rounded-22 shadow togo-chart"
                        >
                            <ResponsivePie
                                data={orderStat}
                                margin={{ top: 40, right: 80, bottom: 80, left: 80 }}
                                innerRadius={0.5}
                                padAngle={0.7}
                                cornerRadius={3}
                                activeOuterRadiusOffset={8}
                                borderWidth={1}
                                borderColor={{
                                    from: 'color',
                                    modifiers: [
                                        [
                                            'darker',
                                            0.2
                                        ]
                                    ]
                                }}
                                arcLinkLabelsSkipAngle={10}
                                arcLinkLabelsTextColor="#333333"
                                arcLinkLabelsThickness={2}
                                arcLinkLabelsColor={{ from: 'color' }}
                                arcLabelsSkipAngle={10}
                                arcLabelsTextColor={{
                                    from: 'color',
                                    modifiers: [
                                        [
                                            'darker',
                                            2
                                        ]
                                    ]
                                }}
                                defs={[
                                    {
                                        id: 'dots',
                                        type: 'patternDots',
                                        background: 'inherit',
                                        color: 'rgba(255, 255, 255, 0.3)',
                                        size: 4,
                                        padding: 1,
                                        stagger: true
                                    },
                                    {
                                        id: 'lines',
                                        type: 'patternLines',
                                        background: 'inherit',
                                        color: 'rgba(255, 255, 255, 0.3)',
                                        rotation: -45,
                                        lineWidth: 6,
                                        spacing: 10
                                    }
                                ]}
                                fill={[

                                ]}
                                legends={[
                                    {
                                        anchor: 'bottom',
                                        direction: 'row',
                                        justify: false,
                                        translateX: 0,
                                        translateY: 56,
                                        itemsSpacing: 0,
                                        itemWidth: 100,
                                        itemHeight: 18,
                                        itemTextColor: '#999',
                                        itemDirection: 'left-to-right',
                                        itemOpacity: 1,
                                        symbolSize: 18,
                                        symbolShape: 'circle',
                                        effects: [
                                            {
                                                on: 'hover',
                                                style: {
                                                    itemTextColor: '#000'
                                                }
                                            }
                                        ]
                                    }
                                ]}
                            />
                        </div>
                        <div

                            className="bg-white me-3 mt-3 rounded-22 shadow togo-chart"
                        >
                            <ResponsivePie
                                margin={{ top: 40, right: 80, bottom: 80, left: 80 }}
                                data={userStat}
                                animate={true}
                                colors={{
                                    scheme: 'set2'//datum: 'data.color' 
                                }}
                                activeOuterRadiusOffset={8}
                                innerRadius={0.6}
                                padAngle={0.5}
                                cornerRadius={5}
                                arcLinkLabelsColor={{
                                    from: 'color',
                                }}
                                arcLinkLabelsThickness={3}
                                arcLinkLabelsTextColor={{
                                    from: 'color',
                                    modifiers: [['darker', 1.2]],
                                }}
                                legends={[
                                    {
                                        anchor: 'bottom',
                                        direction: 'row',
                                        justify: false,
                                        translateX: 0,
                                        translateY: 56,
                                        itemsSpacing: 0,
                                        itemWidth: 100,
                                        itemHeight: 18,
                                        itemTextColor: '#999',
                                        itemDirection: 'left-to-right',
                                        itemOpacity: 1,
                                        symbolSize: 18,
                                        symbolShape: 'circle',
                                        effects: [
                                            {
                                                on: 'hover',
                                                style: {
                                                    itemTextColor: '#000'
                                                }
                                            }
                                        ]
                                    }
                                ]}
                            />
                        </div>
                        <div
                            style={{ position: "relative" }}
                            className="bg-white me-3 mt-3 rounded-22 shadow togo-chart p-3"
                        >
                            <div className="row">
                                <div style={{ fontSize: "1rem" }}>Total in Escrow: <span style={{ color: "#35b09d" }}>{totalEscrowBalance}</span></div>
                                <div style={{ fontSize: "1rem" }}>Total Escrow: <span style={{ color: "#35b09d" }}>{creditAmount}</span></div>
                            </div>

                            <hr style={{ marginTop: "20px", marginBottom: "20px" }} />

                            <div className="row">
                                <div style={{ fontSize: "1rem" }}>Active Packages: <span style={{ color: "#35b09d" }}>{activeOrdersNum}</span></div>
                                <div style={{ fontSize: "1rem" }}>Total Packages: <span style={{ color: "#35b09d" }}>{lastorderId}</span></div>
                            </div>
                        </div>
                    </div>

                    <div className="w-100 d-flex h-25">
                        <OrdersCard title="New Orders" list={newOrders} />
                        <OrdersCard title="Active Orders" list={activeOrders} />
                        <OrdersCard title="Exceptions" list={exceptionOrders} />

                    </div>

                    {/* <div className="w-100 d-flex h-25">
                        <OrdersCard title="Active Orders" list={activeOrders} />
                    </div>

                    <div className="w-100 d-flex h-25">
                        <OrdersCard title="Exceptions" list={exceptionOrders} />
                    </div> */}
                </div>
                <div className="m-3" style={{ width: "30%" }}>

                    <div className="" style={{ height: "49%" }}>
                        <UsersCard title="All Clients" list={allClients} />
                    </div>

                    <div className="mt-3" style={{ height: "49%" }}>
                        <UsersCard title="All Transporters" list={allTransporters} />
                    </div>
                </div>
            </div>
        </>
    )
}
