import React, { useEffect, useRef, useState } from "react";
import './styles.css';
import { Container, Row, Col, Card, Table, Button, Form, Tab, Tabs } from 'react-bootstrap';
import { Checkbox, Grid } from "@chakra-ui/react";
import { GetTransporterPersonalInfo, GetTransporterWorkingTimes, GetTransporterBusinessLocation, GetTransporterVehiclesInfo } from "../../../APIs/AdminPanelApis";
import { convert24TimeTo12 } from "../../Util";

export default function TransporterPersonalInfoDisplay({ id }) {

    const [info, setInfo] = useState();
    const [fullName, setFullName] = useState();

    // const [workingTime, setWorkingTime] = useState();
    const [workingTimeList, setWorkingTimeList] = useState([]);

    const [businessLocations, setBusinessLocations] = useState([]);

    const [vehiclesInfo, setVehiclesInfo] = useState([]);

    const fullNameRef = useRef();
    const accountNameRef = useRef();
    const emailRef = useRef();
    const IDNumberRef = useRef();
    const licenceNumberRef = useRef();

    /* validation */
    const [beginValidation, setBeginValidation] = useState(false);
    const [fullNameCheck, setFullNameCheck] = useState(false);
    const [accountNameCheck, setAccountNameCheck] = useState(false);
    const [emailCheck, setEmailCheck] = useState(false);
    const [IDNumberCheck, setIDNumberCheck] = useState(false);
    const [licenceNumberCheck, setlicenceNumberCheck] = useState(false);

    useEffect(() => {

        GetTransporterPersonalInfo(id).then((res) => {
            // console.log(res.data);
            setInfo(res.data.server_response);
            // setFullName(res.data.server_response.fullName);
        })

        GetTransporterWorkingTimes(id).then((res) => {
            // console.log(res.data.TimeResponse);
            // setWorkingTime(res.data.TimeResponse);

            const workingTime = res.data.TimeResponse;

            // console.log(workingTime[0])

            /* const {
                SatTimeStart, SatTimeFinish, SunTimeStart, SunTimeFinish, MonTimeStart,
                MonTimeFinish, TueTimeStart, TueTimeFinish, WenTimeStart, WenTimeFinish,
                ThuTimeStart, ThuTimeFinish, FriTimeStart, FriTimeFinish
            } = workingTime; */

            let tempWorkingTimeList = [
                {
                    title: "Saturday",
                    id: "Sat",
                    from: formatTime(workingTime[0].SatTimeStart),
                    to: formatTime(workingTime[0].SatTimeFinish)
                },
                {
                    title: "Sunday",
                    id: "Sun",
                    from: formatTime(workingTime[0].SunTimeStart),
                    to: formatTime(workingTime[0].SunTimeFinish)
                },
                {
                    title: "Monday",
                    id: "Mon",
                    from: formatTime(workingTime[0].MonTimeStart),
                    to: formatTime(workingTime[0].MonTimeFinish)
                },
                {
                    title: "Tuesday",
                    id: "Tue",
                    from: formatTime(workingTime[0].TueTimeStart),
                    to: formatTime(workingTime[0].TueTimeFinish)
                },
                {
                    title: "Wednesday",
                    id: "Wen",
                    from: formatTime(workingTime[0].WenTimeStart),
                    to: formatTime(workingTime[0].WenTimeFinish)
                },
                {
                    title: "Thursday",
                    id: "Thu",
                    from: formatTime(workingTime[0].ThuTimeStart),
                    to: formatTime(workingTime[0].ThuTimeFinish)
                },
                {
                    title: "Friday",
                    id: "Fri",
                    from: formatTime(workingTime[0].FriTimeStart),
                    to: formatTime(workingTime[0].FriTimeFinish)
                }
            ];

            // console.log(tempWorkingTimeList)

            setWorkingTimeList(tempWorkingTimeList);
        })

        GetTransporterBusinessLocation(id).then((res) => {
            // console.log(res.data.CityResponse)

            setBusinessLocations(res.data.CityResponse);
        })

        GetTransporterVehiclesInfo(id).then((res) => {
            console.log(res.data)

            setVehiclesInfo(res.data.carArray);
        })

    }, [])

    const formatTime = (value) => {
        if (!value) return null;
        let time = value.split(" ")[1].slice(0, -3);
        return convert24TimeTo12(time);
    };

    function validatePersonalInfo() {
        setBeginValidation(true);

        if (fullNameRef.current.value) {
            setFullNameCheck(true);
        } else {
            setFullNameCheck(false);
        }
        if (accountNameRef.current.value) {
            setAccountNameCheck(true);
        } else {
            setAccountNameCheck(false);
        }
        if (emailRef.current.value) {
            setEmailCheck(true);
        } else {
            setEmailCheck(false);
        }
        if (IDNumberRef.current.value) {
            setIDNumberCheck(true);
        } else {
            setIDNumberCheck(false);
        }
        if (licenceNumberRef.current.value) {
            setlicenceNumberCheck(true);
        } else {
            setlicenceNumberCheck(false);
        }
    }

    function updatePersonalInfo() {

        validatePersonalInfo();

        if (fullNameRef.current.value && accountNameRef.current.value && emailRef.current.value && IDNumberRef.current.value && licenceNumberRef.current.value) {
            console.log("updated!")
        }
    }

    return (
        <>
            <Tabs defaultActiveKey="PersonalInformation" id="uncontrolled-tab-example" className="tabPane">
                <Tab eventKey="PersonalInformation" title="Personal Information">
                    <Card className="customCardSm shadow m-5">
                        {/* <Card.Header>
                            Personal Information
                        </Card.Header> */}
                        <Card.Body>

                            <Container fluid>
                                <Row>
                                    <Col lg={6}>
                                        <Table className="infoTable">
                                            <tbody>
                                                <tr>
                                                    <th>Full Name:</th>
                                                    <td>
                                                        <Form.Control
                                                            placeholder={info?.fullName}
                                                            ref={fullNameRef}
                                                        /* onChange={(event) => {setFullName(event.target.value); console.log(event.target.value)}} */
                                                        />
                                                        {beginValidation && !fullNameCheck && <div className="required-error"><span>*</span></div>}
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <th>Account Name:</th>
                                                    <td>
                                                        <Form.Control
                                                            placeholder={info?.AccountName}
                                                            ref={accountNameRef}
                                                        />
                                                        {beginValidation && !accountNameCheck && <div className="required-error"><span>*</span></div>}
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <th>Phone Number:</th>
                                                    <td>
                                                        <Form.Control
                                                            placeholder={info?.phone}
                                                            ref={accountNameRef}
                                                        />
                                                        {beginValidation && !accountNameCheck && <div className="required-error"><span>*</span></div>}
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <th>Email:</th>
                                                    <td>
                                                        <Form.Control
                                                            type="email"
                                                            placeholder={info?.Email}
                                                            ref={emailRef}
                                                        />
                                                        {beginValidation && !emailCheck && <div className="required-error"><span>*</span></div>}
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <th>ID Number:</th>
                                                    <td>
                                                        <Form.Control
                                                            type="number"
                                                            placeholder={info?.IDNumber}
                                                            ref={IDNumberRef}
                                                        />
                                                        {beginValidation && !IDNumberCheck && <div className="required-error"><span>*</span></div>}
                                                    </td>
                                                </tr>
                                                {/* <tr>
                                                    <th>Place of Issuing Identity:</th>
                                                    <td>
                                                        <Form.Control
                                                            placeholder={info?.NamePlace}
                                                            ref={IDNumberRef}
                                                            onClick={test}
                                                        />
                                                        {beginValidation && !IDNumberCheck && <div className="required-error"><span>*</span></div>}
                                                    </td>
                                                </tr> */}
                                                <tr>
                                                    <th>Licence Number:</th>
                                                    <td>
                                                        <Form.Control
                                                            type="number"
                                                            placeholder={info?.LicenceNumber}
                                                            ref={licenceNumberRef}
                                                        />
                                                        {beginValidation && !licenceNumberCheck && <div className="required-error"><span>*</span></div>}
                                                    </td>
                                                </tr>
                                            </tbody>
                                        </Table>
                                    </Col>
                                    <Col lg={6}>
                                        <div className="w-100 d-flex justify-content-center">
                                            <div
                                                className="rounded-circle d-flex light-turquoise-bg wh-45px "
                                                style={{ width: "200px", height: "200px" }}
                                            >
                                                <img
                                                    className="user-img-details"

                                                    src={info?.img ? "http://46.253.93.4/togo/MobileAPi/" + info.img : "https://freepikpsd.com/file/2019/10/avatar-icon-png-5-Images-PNG-Transparent.png"}

                                                    alt="user-profile-pic"
                                                />
                                            </div>
                                        </div>
                                        <div className="mt-3 ms-3 d-flex justify-content-center" style={{ fontSize: "1.5rem" }}>{/* fullName */ info?.fullName}</div>
                                        <div className="mt-3 ms-3 d-flex justify-content-center" style={{ fontSize: "0.8rem", color: "gray" }}>Customer ID: {id}</div>
                                        <div className="mt-3 ms-3 d-flex justify-content-center" style={{ fontSize: "0.8rem", color: "gray" }}>Odoo ID: {info?.OdooId}</div>
                                    </Col>
                                </Row>
                            </Container>
                        </Card.Body>
                        <Card.Footer>
                            <Button className="update-btn" onClick={updatePersonalInfo}>
                                UPDATE
                            </Button>
                        </Card.Footer>
                    </Card>
                </Tab>
                <Tab eventKey="WorkingTimeAndBusinessLocation" title=" Working Time and Business Location">
                    <Card className="customCardSm shadow m-5">
                        {/* <Card.Header>
                            Working Time and Business Location
                        </Card.Header> */}
                        <Card.Body>
                            <Container fluid>
                                <Row>
                                    <Col lg={6}>
                                        <Table>
                                            <thead>
                                                <tr>
                                                    <th scope="col">Day</th>
                                                    <th scope="col">From</th>
                                                    <th scope="col">To</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {
                                                    workingTimeList?.map((day, index) =>
                                                        <tr key={index}>
                                                            <th scope="row">{day.title}:</th>
                                                            <td>{day.from}</td>
                                                            <td>{day.to}</td>
                                                        </tr>
                                                    )
                                                }
                                            </tbody>
                                        </Table>
                                    </Col>
                                    <Col lg={6}>
                                        <Grid gridTemplateColumns="repeat(2, 1fr)">
                                            {businessLocations.map(({ IdCity, CityName, CheckAdded }, index) => (
                                                <Checkbox
                                                    colorScheme="green"
                                                    key={index}
                                                    defaultIsChecked={CheckAdded === "Added"}
                                                    value={IdCity}
                                                    /* onChange={handleClick}
                                                    {...styles.subTitle} */>{CityName}</Checkbox>
                                            ))}
                                        </Grid>
                                    </Col>
                                </Row>
                            </Container>
                        </Card.Body>
                        <Card.Footer>
                            <Button className="update-btn">
                                UPDATE
                            </Button>
                        </Card.Footer>
                    </Card>
                </Tab>
                <Tab eventKey="VehiclesInformation" title="Vehicles Information">
                    <Card className="customCardSm shadow m-5">
                        {/* <Card.Header>
                            Vehicles Information
                        </Card.Header> */}
                        <Card.Body>
                            {/* LicenceNumber, Name */}
                            <Table>
                                <thead>
                                    <tr>
                                        <th scope="col">Vehicle Name</th>
                                        <th scope="col">Licence Number</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {
                                        vehiclesInfo?.map((car, index) => {
                                            return <tr key={index}>
                                                <td><Form.Control placeholder={car.Name} /></td>
                                                <td><Form.Control placeholder={car.LicenceNumber} /></td>
                                            </tr>
                                        })
                                    }
                                </tbody>
                            </Table>
                        </Card.Body>
                        <Card.Footer>
                            <Button className="update-btn">
                                UPDATE
                            </Button>
                        </Card.Footer>
                    </Card>
                </Tab>
            </Tabs>
        </>
    )
}
