import React, { useState, useEffect } from 'react';
import Modal from "react-bootstrap/Modal";
import translate from "../../i18n/translate";
/*import AccountInfo from "../AccountInfo/AccountInfo";*/
import { Accordion, Card, Button, Badge } from "react-bootstrap";
import './TimeLine.css';
import './TimeLineEn.css';
import Address from "../Address/Address";

export default function Timeline(props) {

    const [show, setShow] = useState(false);
    const [user, setUser] = useState({});
    const [isEn, setIsEn] = useState(localStorage.getItem("Language") === 'en' ? true : false);

    const [users, setUsers] = useState(props.transportationParties[1].name === null ? props.transportationParties.splice(1,1) : props.transportationParties);

    const handleClose = () => {
        setShow(false);
    };

    const handleShow = (user) => {setShow(true); setUser(user)}

    useEffect(() => {
        if (localStorage.getItem("Language") === 'en') {
            setIsEn(true);
        } else {
            setIsEn(false);
        }
    })

    // console.log(users); /* test */

    return (
        <>
            <div className={!isEn ? "arrows" : "arrowsEn"}>
            {/*{console.log(window.innerWidth)}*/}
                {users.length !== 1 && users.map((user, index) => 
                    <div key={index} className={!isEn ? "arrow " + (index % 2 == 0 ? "step1" : "step2") :  "arrowEn " + (index % 2 == 0 ? "step1En" : "step2En")} onClick={() => handleShow(user)}>
                        <div style={{
                            background: "white",//"linear-gradient(90deg, #ff9966, #ff5e62)",
                            position: "absolute",
                            left: "10%",
                            top: "10px",
                            width: "50px",
                            height: "50px",
                        }}
                            className={!isEn ? "img d-flex justify-content-center rounded-circle align-self-center" : "imgEn d-flex justify-content-center rounded-circle align-self-center"}>
                            <svg xmlns="http://www.w3.org/2000/svg" width="90%" height="90%" fill="currentColor" className="bi bi-person-fill" viewBox="0 0 16 16">
                                <path d="M3 14s-1 0-1-1 1-4 6-4 6 3 6 4-1 1-1 1H3zm5-6a3 3 0 1 0 0-6 3 3 0 0 0 0 6z" />
                            </svg>
                        </div>
                        <span className={!isEn ? "text" : "textEn"}>{user.type == 1 ? (user.name.length > 15 ? user.name.substring(0, 13) + "..." : user.name) : user.type == 2 ? /*"Transporter" + index*/(user.name.length > 15 ? user.name.substring(0, 13) + "..." : user.name) : (user.name.length > 15 ? user.name.substring(0, 13) + "..." : user.name)}{' '}{user.current ? <span className="badge bg-danger">Current</span> : ""}{user.type == 1 ? <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className={!isEn ? "svg-icon bi bi-send" : "svg-iconEn bi bi-send"} viewBox="0 0 16 16">
                          <path d="M15.854.146a.5.5 0 0 1 .11.54l-5.819 14.547a.75.75 0 0 1-1.329.124l-3.178-4.995L.643 7.184a.75.75 0 0 1 .124-1.33L15.314.037a.5.5 0 0 1 .54.11ZM6.636 10.07l2.761 4.338L14.13 2.576 6.636 10.07Zm6.787-8.201L1.591 6.602l4.339 2.76 7.494-7.493Z"/>
                        </svg> : user.type == 3 ? <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className={!isEn ? "svg-icon bi bi-envelope-open" : "svg-iconEn bi bi-envelope-open"} viewBox="0 0 16 16">
                          <path d="M8.47 1.318a1 1 0 0 0-.94 0l-6 3.2A1 1 0 0 0 1 5.4v.817l5.75 3.45L8 8.917l1.25.75L15 6.217V5.4a1 1 0 0 0-.53-.882l-6-3.2ZM15 7.383l-4.778 2.867L15 13.117V7.383Zm-.035 6.88L8 10.082l-6.965 4.18A1 1 0 0 0 2 15h12a1 1 0 0 0 .965-.738ZM1 13.116l4.778-2.867L1 7.383v5.734ZM7.059.435a2 2 0 0 1 1.882 0l6 3.2A2 2 0 0 1 16 5.4V14a2 2 0 0 1-2 2H2a2 2 0 0 1-2-2V5.4a2 2 0 0 1 1.059-1.765l6-3.2Z"/>
                        </svg> : <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor"className={!isEn ? "svg-icon bi bi-truck" : "svg-iconEn bi bi-truck"} viewBox="0 0 16 16">
                          <path d="M0 3.5A1.5 1.5 0 0 1 1.5 2h9A1.5 1.5 0 0 1 12 3.5V5h1.02a1.5 1.5 0 0 1 1.17.563l1.481 1.85a1.5 1.5 0 0 1 .329.938V10.5a1.5 1.5 0 0 1-1.5 1.5H14a2 2 0 1 1-4 0H5a2 2 0 1 1-3.998-.085A1.5 1.5 0 0 1 0 10.5v-7zm1.294 7.456A1.999 1.999 0 0 1 4.732 11h5.536a2.01 2.01 0 0 1 .732-.732V3.5a.5.5 0 0 0-.5-.5h-9a.5.5 0 0 0-.5.5v7a.5.5 0 0 0 .294.456zM12 10a2 2 0 0 1 1.732 1h.768a.5.5 0 0 0 .5-.5V8.35a.5.5 0 0 0-.11-.312l-1.48-1.85A.5.5 0 0 0 13.02 6H12v4zm-9 1a1 1 0 1 0 0 2 1 1 0 0 0 0-2zm9 0a1 1 0 1 0 0 2 1 1 0 0 0 0-2z"/>
                        </svg>}</span>
                    </div>
                )}
            </div>
            <Modal show={show} onHide={handleClose} centered>
                <Modal.Header closeButton>
                    <Modal.Title>{translate("TEMP.INFO")}</Modal.Title>
                </Modal.Header>

                <Modal.Body>

                    {/* console.log(user)*/ }
                    

                    {/*{user.address != null ? <Address address={user.address} name={user.name}/> : <p>Account Info goes here...</p>}*/}

                    <Address address={user.address != null && user.address} name={user.name} phone={user.phone}/>

                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        {translate("GENERAL.CLOSE")}
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}
