                                    {
                                        vehiclesInfo?.map((car, index) => {
                                            return <tr key={index}>
                                                <td><Form.Control placeholder={car.Name} /></td>
                                                <td><Form.Control placeholder={car.LicenceNumber} /></td>
                                            </tr>
                                        })
                                    }
                                </tbody>
                            </Table> */}
                            <Table>
                                <tbody>
                                    <tr>
                                        <th scope="row">Vehicle Type</th>
                                        <td><Form.Control placeholder={vehiclesInfo[0]?.Name} /></td>
                                    </tr>
