                                        <th scope="row">Registration Finish Day</th>
                                        <td><Form.Control placeholder={vehiclesInfo[0]?.RegistrationFinshDay} /></td>
                                    </tr>
                                    <tr>
                                        <th scope="row">Registration Photo</th>
                                        <td>
                                            <img
                                                onClick={() => { setViewImg(true); setTempImgPath("https://togo.ps/togo/MobileAPi/" + vehiclesInfo[0]?.RegistrationImgPath) }}
                                                style={{ cursor: "pointer", height: "100px" }}
                                                src={"https://togo.ps/togo/MobileAPi/" + vehiclesInfo[0]?.RegistrationImgPath}

                                                alt="user-profile-pic"
                                            />
