                                    </tr>
                                    <tr>
                                        <th scope="row">Vehicle Photo</th>
                                        <td>
                                            <img
                                                onClick={() => { setViewImg(true); setTempImgPath("https://togo.ps/togo/MobileAPi/" + vehiclesInfo[0]?.CarImgPath) }}
                                                style={{ cursor: "pointer", height: "100px" }}
                                                src={"https://togo.ps/togo/MobileAPi/" + vehiclesInfo[0]?.CarImgPath}

                                                alt="user-profile-pic"
                                            />
                                        </td>
                                    </tr>
                                </tbody>
                            </Table>
                        </Card.Body>
                        <Card.Footer>
                            <Button disabled className="update-btn">
                                UPDATE
