import { motion } from "framer-motion";
import React from "react";
import { Badge } from "react-bootstrap";

export default function RecordsCard(props) {

    const formatAction = (record) => {

        const tempHolder = <div style={{ width: "35%" }}>
            <div style={{ fontWeight: "bold" }}>{record.description}</div>
            <div>{record.time_stamp.split(" ")[0] + " "}<Badge bg="primary">{record.time_stamp.split(" ")[1]}</Badge></div>
        </div>

        switch (record.action_id) {
            case "2":
                return <>
                    {tempHolder}
                    <div style={{ width: "35%" }}>{record.party_one_name + " created order " + record.order_id}</div>
                </>;
            case "3":
                return <>
                    {tempHolder}
                    <div style={{ width: "35%" }}>{record.party_one_name + " bid on order " + record.order_id}</div>
                </>;
            case "4":
                return <>
                    {tempHolder}
                    <div style={{ width: "35%" }}>{record.party_one_name + " cancel order " + record.order_id}</div>
                </>;
            case "5":
                return <>
                    {tempHolder}
                    <div style={{ width: "35%" }}>{record.party_one_name + " updated bid on order " + record.order_id}</div>
                </>;
            case "6":
                return <>
                    {tempHolder}
                    <div style={{ width: "35%" }}>{record.party_one_name + " canceled bid on order " + record.order_id}</div>
                </>;
            case "7":
                return <>
                    {tempHolder}
                    <div style={{ width: "35%" }}>{record.party_one_name + " accepted " + record.party_two_name + "'s bid on order " + record.order_id}</div>
                </>;
            case "8":
                return <>
                    {tempHolder}
                    <div style={{ width: "35%" }}>{record.party_one_name + " picked up order " + record.order_id}</div>
                </>;
            case "9":
                return <>
                    {tempHolder}
                    <div style={{ width: "35%" }}>{record.party_one_name + " finished order " + record.order_id}</div>
                </>;
            case "10":
                return <>
                    {tempHolder}
                    <div style={{ width: "35%" }}>{record.party_one_name + " assigned order " + record.order_id + " to " + record.party_two_name}</div>
                </>;
            case "11":
                return <>
                    {tempHolder}
                    <div style={{ width: "35%" }}>{record.party_one_name + " rejected assign on order " + record.order_id}</div>
                </>;
            case "12":
                return <>
                    {tempHolder}
                    <div style={{ width: "35%" }}>{record.party_one_name + " accepted assign on order " + record.order_id}</div>
                </>;
        }
    }

    return (
        <div style={{ flex: 1 }} className="d-flex flex-column bg-white me-3 mt-3 p-3 rounded-22 shadow">
            <p className="text-black">{props.title}</p>
            <hr />
            <div className="h-100 overflow-auto" style={{ fontFamily: "system-ui" }}>
                {
                    props?.list?.map((record, index) => {
                        return (
                            <motion.div
                                key={index}
                                initial={{ backgroundColor: "#fff" }}
                                whileHover={{ backgroundColor: "#f0fff0" }}
                                whileTap={{ scale: 0.95 }}
                                className="d-flex gap-3 align-items-center d-flex gap-3 text-nowrap mb-2"
                            >
                                {formatAction(record)}

                            </motion.div>
                        )
                    })
                }
            </div>
        </div>
    )
}
