import { motion } from "framer-motion";
import React, { useState, useEffect } from "react";
import Form from 'react-bootstrap/Form';
import "./UserCard.css";
import Modal from "react-bootstrap/Modal";
import { Accordion, Card, Button, Badge } from "react-bootstrap";
import translate from "../../i18n/translate";
import { useHistory } from "react-router";
import { FixedSizeList } from 'react-window';

export default function UsersCard(props) {

    const history = useHistory();

    const [users, setUsers] = useState(props.list);

    // window height and width variables to set records container width and height
    const [windowHeight, setWindowHeight] = useState(window.innerHeight);
    const [windowWidth, setWindowWidth] = useState(window.innerWidth / 4.5);

    // update window width and height when change window dimensions (zoom in and out, move to a bigger screen)
    useEffect(() => {
        function handleResize() {
            setWindowHeight(window.innerHeight)
            setWindowWidth(window.innerWidth / 4.5)
        }

        window.addEventListener('resize', handleResize)
    })

    useEffect(() => {
        setUsers(props.list)
    }, [props.list])

    /* using search bar, filter users (transporters/clients) by their names or phone numbers */
    const filterHandler = (val) => {
        let tempUsers = props.list.filter(user => user.FullName.toLowerCase().includes(val.toLowerCase()) || user.PhoneNumber.includes(val));
        setUsers(tempUsers)
    }

    /* FixedSizeList at each row */
    const RowComponent = ({ user, num, style }) => (
        <div  className={"list-group-item"}>
            <div className="d-flex justify-content-start user-div" onClick={() => { history.push("/customerInfo/" + user.id + "/" + (user.IsTransporter === "1" ? "1" : "0")) }}>

                <div
                    className="rounded-circle d-flex light-turquoise-bg wh-45px me-2"
                >
                    <img
                        className="user-img"

                        src={user.PersonalImgPath ? "http://46.253.93.4/togo/MobileAPi/" + user.PersonalImgPath : "https://freepikpsd.com/file/2019/10/avatar-icon-png-5-Images-PNG-Transparent.png"}

                        alt="user-profile-pic"
                    />
                </div>
                <div style={{ width: "40%" }}>
                    <div>{user.FullName}</div>
                    <div>{user.PhoneNumber}</div>
                </div>

            </div>
        </div>
    );

    /* FixedSizeList row */
    const Row = ({ index, style }) => {
        return <RowComponent user={users[index]} num={index} style={style} />
    };

    /* FixedSizeList, used to render only the visible rows to the DOM to enhance the performance */
    const ListComponent = () => {
        return <FixedSizeList
            height={windowHeight}
            width={windowWidth}
            itemSize={70}
            itemCount={users?.length}
            className="list-container"
        >
            {Row}
        </FixedSizeList>
    };

    return (
        <div className="h-100 d-flex flex-column bg-white p-3 rounded-22 shadow" style={{ position: "relative" }}>

            <div style={{ position: "absolute", top: "15px", left: "20px", right: "15px" }}>
                <p className="text-black">{props.title}</p>
                <Form.Control style={{ position: "absolute", right: "1.5rem", top: "0", height: "25px" }} className="w-25" type="text" placeholder="search..." onChange={(e) => filterHandler(e.target.value)} />
            </div>

            <ListComponent />
        </div>
    )
}
