import React, { useState } from 'react';
import Modal from "react-bootstrap/Modal";
import translate from "../../i18n/translate";
/*import AccountInfo from "../AccountInfo/AccountInfo";*/
import { Accordion, Card, Button, Badge } from "react-bootstrap";
import './TimeLine.css';
import Address from "../Address/Address";

export default function Timeline(props) {

    const [show, setShow] = useState(false);
    const [user, setUser] = useState({});

    const handleClose = () => {
        setShow(false);
    };

    const handleShow = (user) => {setShow(true); setUser(user)}

    return (
        <>
            <div className="arrows">
                {props.transportationParties.map((user, index) => 
                    <div key={index} className={"arrow " + (index % 2 == 0 ? "step1" : "step2")} onClick={() => handleShow(user)}>
                        <div style={{
                            background: "white",//"linear-gradient(90deg, #ff9966, #ff5e62)",
                            position: "absolute",
                            left: "10%",
                            top: "10px",
                            width: "50px",
                            height: "50px",
                        }}
                            className="img d-flex justify-content-center rounded-circle align-self-center">
                            <svg xmlns="http://www.w3.org/2000/svg" width="90%" height="90%" fill="currentColor" className="bi bi-person-fill" viewBox="0 0 16 16">
                                <path d="M3 14s-1 0-1-1 1-4 6-4 6 3 6 4-1 1-1 1H3zm5-6a3 3 0 1 0 0-6 3 3 0 0 0 0 6z" />
                            </svg>
                        </div>
                        <span className="text">{user.type == 1 ? "Sender" : user.type == 2 ? "Transporter" + index : "Receiver"}{' '}{user.current ? <span className="badge bg-danger">Current</span> : ""}<svg xmlns="http://www.w3.org/2000/svg" width="30" height="30" fill="currentColor" className="svg-icon bi bi-info-circle" viewBox="0 0 16 16">
                            <path d="M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14zm0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16z" />
                            <path d="m8.93 6.588-2.29.287-.082.38.45.083c.294.07.352.176.288.469l-.738 3.468c-.194.897.105 1.319.808 1.319.545 0 1.178-.252 1.465-.598l.088-.416c-.2.176-.492.246-.686.246-.275 0-.375-.193-.304-.533L8.93 6.588zM9 4.5a1 1 0 1 1-2 0 1 1 0 0 1 2 0z" />
                        </svg></span>
                    </div>
                )}
            </div>
            <Modal show={show} onHide={handleClose} centered>
                <Modal.Header closeButton>
                    <Modal.Title>{translate("TEMP.INFO")}</Modal.Title>
                </Modal.Header>

                <Modal.Body>
                    <div className="col-lg-3 d-flex justify-content-center">
                        <div style={{
                            background: "linear-gradient(90deg, #26a69a, #69d4a5)",//"linear-gradient(90deg, #ff9966, #ff5e62)",

                            width: "90px",
                            height: "90px",
                        }}
                            className="d-flex justify-content-center rounded-circle align-self-center">
                            {/* <img
                                                        style={{
                                                            width: "80px",
                                                            height: "80px",
                                                        }}
                                                        className="rounded-circle align-self-center bg-white"
                                                        src={``}
                                                        alt={""}

                                                    /> */}
                            <svg xmlns="http://www.w3.org/2000/svg" width="90%" height="90%" fill="currentColor" className="bi bi-person-fill" viewBox="0 0 16 16">
                                <path d="M3 14s-1 0-1-1 1-4 6-4 6 3 6 4-1 1-1 1H3zm5-6a3 3 0 1 0 0-6 3 3 0 0 0 0 6z" />
                            </svg>
                        </div>
                    </div>

                    {console.log("---------- " + props.address)}

                    {/*{props.address != null ? <Address address={user.address}/> : <p>Account Info goes here...</p>}*/}

                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        {translate("GENERAL.CLOSE")}
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}
