import { Button, Form, Table, Card, Container, Row, Col } from "react-bootstrap";
import React, { useRef, useEffect, useState } from "react";
import { getAllAreas, addToLogesAreas, updateTogoAreaName } from "../APIs/OrdersAPIs";


export default function TestComponent() {

    const [areas, setAreas] = useState([]);

    useEffect(() => {
        getAllAreas().then(res => {
            // console.log(res.data.areas);
            setAreas(res.data.areas);
        })
    }, [])

    const setLogesNameHandler = (togoId, togoName, logesName) => {
        // console.group(id + " --> " + name);
        addToLogesAreas(togoId, togoName, logesName).then(res => {
            console.log(res.data);
        })
    }

    const updateAreaNameHandler = (id, arName, enName) => {
        console.log(id + " => " + arName + " / " + enName);
    }

    return (
        <Container fluid className="p-5">
            <Row>
                <Col>
                    <Table>
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Area Name</th>
                                <th>City Name</th>
                                <th>New AR Name</th>
                                <th>New EN Name</th>
                                <th></th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                areas.map((item, index) => {
                                    return <tr key={index}>
                                        <td>{item.id}</td>
                                        <td>{item.areaName}</td>
                                        <td>{item.cityName}</td>
                                        <td>
                                            <Form.Control id={"arName" + index} placeholder="new ar name" />
                                        </td>
                                        <td>
                                            <Form.Control id={"enName" + index} placeholder="new en name" />
                                        </td>
                                        <td>
                                            <Button variant="danger" onClick={() => updateAreaNameHandler(item.id, document.getElementById("arName" + index).value, document.getElementById("enName" + index).value)}>
                                                Update
                                            </Button>
                                        </td>
                                    </tr>
                                })
                            }
                        </tbody>
                    </Table>
                </Col>
            </Row>
        </Container>
    )
}
