import React, { useEffect, useState } from "react";
import { Button, Form, FormControl, InputGroup, Spinner } from "react-bootstrap";
import {ReactComponent as CarIcon } from "../../assets/car.svg";
import {ReactComponent as SteeringIcon } from "../../assets/steering.svg";
import {ReactComponent as WheelIcon } from "../../assets/wheel.svg";
import logo from "../../assets/logo.png";
import { animate, motion, useAnimation, useMotionValue } from 'framer-motion';
import {ReactComponent as EyeIcon } from "../../assets/eye.svg";
import { refreshPage } from "../../Functions/CommonFunctions";
import { tempRegisterAdmin, adminLogin } from "../../APIs/AdminPanelApis";

export default function SignIn() {
    const x = useMotionValue(0)
    const [type, setType] = useState("password");
    useEffect(() => {
        const controls = animate(x, window.innerWidth - 220 , {
            type: "spring",
            stiffness: 10,
            repeat: Infinity,
            repeatType: "reverse",
            delay: "2",
            onComplete: v => {
                
                
            }
        });
    
        return controls.stop;
    }, []);
    const controls = useAnimation()

    useEffect(() => {
        controls.start(i => ({
            right: [100, 400, 100],
            top: [0, window.innerHeight - 250 , 0],
            rotate: 360,
            scale: 0.5,
            transition: { /* delay: i * 0.3, */ /* repeat: Infinity, */ duration: 4, repeatDelay: 2},
        }));
        
    }, []);

    const controls2 = useAnimation()

    useEffect(() => {
        controls2.start(i => ({
            scale: 0.5,
            rotate: 45,
        }));
        
    }, []);
    // const xx = useMotionValue(10);

    // const y = (xx, (value) => {
    //     console.log(value);
    //     return value * 2;
    // });

    const [loadingSubmit, setLoadingSubmit] = useState(false);

    return (
        <div className="align-items-center d-flex togo-bg h-100 justify-content-center w-100">
            <div className="sign-in bg-white rounded-3 shadow p-4">
                <img src={logo} alt="logo" />
                <Form onSubmit={(event)=>{
                    event.preventDefault();
                    const formData = new FormData(event.target);
                    const userInput = Object.fromEntries(formData.entries());

                    if (userInput.username && userInput.password) {

                        setLoadingSubmit(true)

                        adminLogin(userInput.username, userInput.password).then((res) => {
                            console.log(res.data)
                            setLoadingSubmit(false);

                            localStorage.setItem("signedin", true);

                            localStorage.setItem("FirstName", res.data.FirstName);
                            localStorage.setItem("LastName", res.data.LastName);
                            localStorage.setItem("Email", res.data.Email);
                            localStorage.setItem("MobileNumber", res.data.MobileNumber);

                            refreshPage();
                        })

                    }

                    /* if(userInput.password === "murad" && userInput.username === "maan"){
                        localStorage.setItem("signedin", true);
                        refreshPage();
                    } */
                }}>
                    <div className="login-lbl">
                        <p>Login</p>
                        <hr />
                    </div>
                    
                    <Form.Group controlId="togoUsername" className="">
                        
                        <InputGroup className="mb-3">
                            <FormControl
                                type="text" 
                                className=""
                                placeholder="Username"
                                name="username" 
                            />
                        </InputGroup>    
                    </Form.Group>

                    <Form.Group controlId="togoPassword" className="position-relative">
                        
                        <InputGroup className="mb-2">
                            <FormControl
                                type={type} 
                                className=""
                                placeholder="Password"
                                name="password" 
                            />
                            
                        </InputGroup>    
                        <EyeIcon className="eye-icon" onClick={()=>{
                            setType(type === "password" ? "text" : "password");
                        }}/>
                    </Form.Group>
                    <Button
                        variant="" 
                        type="submit"
                        className="d-block togo-button" 
                        disabled={loadingSubmit}
                        // onClick={(event)=>{
                        //     setLoadingSubmit(true);
                        //     // SetTransporterPersonalInfo(
                        //     // ).then((res) => {
                        //     //     setLoadingSubmit(false);
                        //     //     history.push("/business-info")
                        //     // });
                        // }}
                    >
                        {loadingSubmit && <Spinner animation="border" size="sm"/>} 
                        Sign In
                    </Button>
                   {/*  <Button onClick={() => { tempRegisterAdmin().then((res) => { console.log(res.data) }) }}> register </Button> */}
                </Form>
            </div>
            <motion.div
                style={{
                    position: "absolute",
                    animationName: "example",
                    animationDuration: "3s",
                    animationFillMode: "forwards"
                }}
                transition={{ duration: 2 }}
                //animate="example"
                animate={{
                    //left: [50, 30, 50],
                    //top: [50, 80, 100],
                    scale: 0.5,
                    rotate: 45,
                }}
                //initial="a"
                // variants={{
                //     a: {left: "10%", top: "50%", rotate: 0, scale: 1},
                //     b: {left: "-10%", top: "40%", rotate: 20, scale: 0.7},
                //     c: {left: "10%", top: "20%", rotate: 45, scale: 0.5},
                // }}
            >
                <CarIcon className="svg-bg" />
            </motion.div>
            <motion.div 
                animate={controls}
                style={{
                    position: "absolute"
                }}
                initial={{
                    scale: 0.5
                }}
            >
                <SteeringIcon className="svg-bg svg-steering"/>
            </motion.div>
            <motion.div
                style={{ left: x, position: "absolute", bottom: 40 }}
                animate={{
                    rotate: 360
                }}
                transition={{ duration: 2, repeat: Infinity }}
                initial={{rotate: 0}}
            >
                <WheelIcon className="svg-bg svg-wheel"/>
            </motion.div>
        </div>
    )
}
