import React from "react";
import "./Order.css";
import {Link} from "react-router-dom";
import translate from "../../i18n/translate";
import { deliverFormatter, PackageTypes } from "../Orders/OrdersTabularView";
import { PackageTypesIcons } from "../CreateNewOrder";

function Order(props){

    // const {
    //     order: {
    //         idOrder,
    //         DeliveryWays,
    //         FromAddress,
    //         ToAddress,
    //         DateOrder,
    //         TimeOrder,
    //         PackageType,
    //         OrderStatus
    //     },
    // } = props;
    const {
        order: {
            
    DateLoad,
    IsDeleted,
    TypeLoad,
    bidsCount,
    deliveryWay,
    fromAddress,
    fromCity,
    fromCityName,
    id,
    isAcceptDelivery,
    orderfinished,
    pickup_date,
    toAddress,
    toCity,
    toCityName,
        },
    } = props;

    return (
        <div style={{marginRight: "10%", marginLeft: "10%"}}>
            <div className="d-flex">
                <div style={{flexBasis: "100%"}} className="order-prop-label">
                    {React.createElement(PackageTypesIcons[TypeLoad], {style: {width: "20px", height: "20px"}, className: "me-1"})}
                    {translate("ORDERS." + PackageTypes[TypeLoad])}
                </div>

                <div style={{flexBasis: "100%"}}>
                    <div>
                        <div className="order-prop-label">Order ID</div>
                        <div>#{id}</div>
                    </div>
                    <div>
                        <div className="order-prop-label">Delivery method</div>
                        <div>{deliverFormatter({DeliveryWays: deliveryWay})}</div>
                    </div>
                    <div>
                        <div className="order-prop-label">From Address</div>
                        <div>{fromAddress}</div>
                    </div>
                    <div>
                        <div className="order-prop-label">From City</div>
                        <div>{fromCityName}</div>
                    </div>
                </div>
                <div style={{flexBasis: "100%"}}>
                    <div className={`custom-lbl`}>
                        <div></div>
                        <div>{id}</div>
                    </div>
                    <div>
                        <div className="order-prop-label">Order Time</div>
                        <div>{DateLoad}</div>
                    </div>
                    <div>
                        <div className="order-prop-label">To Address</div>
                        <div>{toAddress}</div>
                    </div>
                    <div>
                        <div className="order-prop-label">To City</div>
                        <div>{toCityName}</div>
                    </div>
                </div>
            </div>
            <hr className="my-4"/>
        </div>
        
    )
}

export default Order;
