import React, { useEffect, useState } from 'react';
import translate from "../../i18n/translate";
import './FinancialTransactions.css';
import DynamicTable from '../DynamicTable/DynamicTable';
import { isTransporter } from '../../Util';

import { Button } from "react-bootstrap";

import { transactionTest, invoicesTest } from '../../APIs/OrdersAPIs';

export default function Transactions() {

    const [transactions, setTransactions] = useState([]);
    const [invoices, setInvoices] = useState([]);

    const columns = [
        {
            label: "Name",
            key: "move_name",
        },
        {
            label: "date",
            key: "date"
        },
        {
            label: "Order ID",
            key: "ref",
        },
        {
            label: "Journal",
            key: "journal",
        },
        {
            label: "amount",
            key: "amount"
        }
    ];

    useEffect(() => {



        let isMounted = true;  
          transactionTest(localStorage.getItem("userId")).then(res => {
            if (isMounted) {
                let tempArr = res.data.server_response.data.result.response;

                tempArr.sort((a, b) => {
                    return b.ref - a.ref;
                });

                setTransactions(tempArr); 
            }
          })
        return () => { isMounted = false };


        // console.log(localStorage.getItem("userId"));

        /*transactionTest(localStorage.getItem("userId")).then((res)=>{  // edited (test transactions) 
            console.log(res.data.server_response);
            setTransactions(res.data.server_response.data.result.response);
        });*/

        /*invoicesTest(localStorage.getItem("userId")).then((res)=>{  // edited (test transactions) 
            // console.log(res.data.server_response.data.result.response);
            // setInvoices(res.data.server_response.data.result.response);
        });*/
    }, []);

    return (
        <div>
            <DynamicTable columns={columns} data={transactions}/>
        </div>
    );
   
};
