        <iframe width="100%" height="1000" frameBorder="0" src="https://superset.zone.ps/login/?next=https%3A%2F%2Fsuperset.zone.ps%2Fsuperset%2Fdashboard%2F6%2F%3Fstandalone%3Dtrue"> </iframe>
    )
} */

export default function SupersetComponent() {

    const [accessToken, setAccessToken] = useState("");

    useEffect(() => {
        fetchSupersetGusetToken().then(res => {
            // console.log(res.data.access_token);
            setAccessToken(res.data.access_token);
        })
    }, []);

    const fetchGuestTokenFromBackend = () => {
        fetchSupersetGusetToken().then(res => {
            // console.log(res.data);
            return res.data.access_token;
        })
    }

    embedDashboard({
        id: "63e35e3d-df1b-4c89-b9ce-bdc0a691e2a6", // given by the Superset embedding UI
        supersetDomain: "http://superset.zone.ps:8088",
        mountPoint: document.getElementById("my-superset-container"), // any html element that can contain an iframe
        fetchGuestToken: () => fetchGuestTokenFromBackend(),
        dashboardUiConfig: { hideTitle: true }, // dashboard UI config: hideTitle, hideTab, hideChartControls (optional)
    });

    return (
        <div id="my-superset-container">
            <iframe width="100%" height="1000" frameBorder="0" src="http://superset.zone.ps:8088/login/?next=https%3A%2F%2Fsuperset.zone.ps%2Fsuperset%2Fdashboard%2F6%2F%3Fstandalone%3Dtrue"> </iframe>
        </div>
    )
}
