import {getUserCreditialParams, PAGE_SIZE} from "../UserManager/UserManager";
import {refreshPage} from "../Functions/CommonFunctions";

const axios = require("axios");
const apiUrl = "http://46.253.93.4:80/togo/MobileAPi/public/FunctionApis.php";
const requestHeaders = {
    "Access-Control-Allow-Origin": `*`,
    "Content-Type": `application/x-www-form-urlencoded`,
};

export function getFunctions(functionType, pageNumber) {
    let params = getUserCreditialParams();
    params.append("CheckTypeFunction", functionType);
    params.append("PageSize", PAGE_SIZE);
    params.append("PageNumber", pageNumber);
    return axios
        .post(apiUrl, params, {
            headers: requestHeaders,
        });
}

export function getOrderDetails(orderId) {
    let params = getUserCreditialParams();
    params.append("CheckTypeFunction", "TransporterShowDetailsOrder");
    params.append("OrderId", orderId);
    return axios
        .post(apiUrl, params, {
            headers: requestHeaders,
        })
        .then((response) => {
            return response.data.server_response?.[0] || response.data;
        })
        .catch((error) => {
            console.log(error);
            return error.message;
        });
    // return response.data.server_response[0];
}

export function deleteOrder(orderId) {
    let params = getUserCreditialParams();
    params.append("CheckTypeFunction", "TransporterDeleteOrder");
    params.append("OrderId", orderId);

    axios
        .post(apiUrl, params, {
            headers: requestHeaders,
        })
        .then((response) => {
            // component.goBack();
        })
        .catch((error) => {
        });
}

