import React, { useState } from 'react'
import "./ManageCustomers.css"
import { Modal, Button } from 'react-bootstrap';
import { imgBaseUrl } from "../../Constants/GeneralCont";
import { FiEdit2 } from 'react-icons/fi';
/* import {

} from "../../APIs/OrdersAPIs"; */

function ManageCustomers() {

    const [clients, setClients] = useState([
        { client_id: 1, client_name: "client 1", client_logo_path: "img/BusinessLogo/BlankProfile.png" },
        { client_id: 2, client_name: "client 2", client_logo_path: "img/BusinessLogo/BlankProfile.png" },
        { client_id: 3, client_name: "client 3", client_logo_path: "img/BusinessLogo/BlankProfile.png" },
        { client_id: 4, client_name: "client 4", client_logo_path: "img/BusinessLogo/BlankProfile.png" },
        { client_id: 5, client_name: "client 5", client_logo_path: "img/BusinessLogo/BlankProfile.png" },
        { client_id: 6, client_name: "client 6", client_logo_path: "img/BusinessLogo/BlankProfile.png" },
    ])
    const [showEditModal, setShowEditModal] = useState(false);
    const [selectedClientId, setSelectedClientId] = useState();

    const handleShowEditModal = () => { setShowEditModal(true) }
    const handleCloseEditModal = () => { setShowEditModal(false) }

    const editClient = (clientId) => {
        handleShowEditModal()
        setSelectedClientId(clientId)
    }

    return (
        <div>
            <div className='clients-container'>
                {
                    clients?.map((client, index) => {
                        return <div key={index} className='client-div'>
                            <div className='client-image'>
                                <img style={{
                                    width: "50px",
                                    height: "50px",
                                    objectFit: "cover"
                                }}
                                    className="rounded-circle align-self-center" src={`${imgBaseUrl}${client.client_logo_path}`} alt="transImg" />
                            </div>

                            <div className='client-name'>
                                {client.client_name}
                            </div>

                            <div className='edit-client' onClick={() => editClient(client.client_id)}>
                                <FiEdit2 />
                            </div>
                        </div>
                    })
                }
            </div>

            <Modal
                show={showEditModal}
                onHide={handleCloseEditModal}
                size="lg"
                aria-labelledby="contained-modal-title-vcenter"
                centered
            >
                <Modal.Header closeButton>
                    <Modal.Title id="contained-modal-title-vcenter">
                        Modal heading
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <h4>Centered Modal</h4>
                    <p>
                        Cras mattis consectetur purus sit amet fermentum. Cras justo odio,
                        dapibus ac facilisis in, egestas eget quam. Morbi leo risus, porta ac
                        consectetur ac, vestibulum at eros.
                    </p>
                </Modal.Body>
                <Modal.Footer>
                    <Button onClick={handleCloseEditModal}>Close</Button>
                </Modal.Footer>
            </Modal>
        </div>
    )
}

export default ManageCustomers
