import { Button, Form, Table, Card, Container, Row, Col } from "react-bootstrap";
import React, { useRef, useEffect, useState } from "react";
import { getAllAreas, addToLogesAreas } from "../APIs/OrdersAPIs";


export default function TestComponent() {

    const [areas, setAreas] = useState([]);
    const nameRef = useRef();

    useEffect(() => {
        getAllAreas().then(res => {
            // console.log(res.data.areas);
            setAreas(res.data.areas);
        })
    }, [])

    const setLogesNameHandler = () => {
        console.log("test");
        if (nameRef.current.value) {
            console.log(nameRef.current.value);
        }
    }

    return (
        <Container fluid className="p-5">
            <Row>
                <Col>
                    <Table>
                        <thead>
                            <tr>
                                <th>id</th>
                                <th>name</th>
                                <th>Logestechs' Village Name</th>
                                <th></th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                areas.map((item, index) => {
                                    return <tr key={index}>
                                        <td>{item.id}</td>
                                        <td>{item.name}</td>
                                        <td>
                                            <Form.Control placeholder="logestechs' village name" ref={nameRef} />
                                        </td>
                                        <td>
                                            <Button onClick={setLogesNameHandler}>Set</Button>
                                        </td>
                                    </tr>
                                })
                            }
                        </tbody>
                    </Table>
                </Col>
            </Row>
        </Container>
    )
}
