import { motion } from "framer-motion";
import React from "react";
import { PackageTypesIcons } from "../CreateNewOrder";

export default function OrdersCard(props) {
    return (
        <div style={{ flex: 1 }} className="d-flex flex-column bg-white me-3 mt-3 p-3 rounded-22 shadow">
            <p className="text-black">{props.title}</p>
            <hr />
            <div className="h-100 overflow-auto" style={{ fontFamily: "system-ui" }}>
                {
                    props.list?.length > 0 ? props.list.map((order, index) => {
                        return (
                            <motion.div
                                key={index}
                                initial={{ backgroundColor: "#fff" }}
                                whileHover={{ backgroundColor: "#f0fff0" }}
                                whileTap={{ scale: 0.95 }}
                                className="d-flex gap-3 align-items-center d-flex gap-3 text-nowrap mb-2"
                            >
                                <div
                                    className="rounded-circle d-flex light-turquoise-bg wh-45px"
                                >
                                    {order.TypeLoad && React.createElement(PackageTypesIcons[order.TypeLoad],
                                        { style: { width: "30px", height: "30px", margin: "auto" } })
                                    }
                                </div>
                                <div style={{ width: "35%" }}>
                                    <div>From:{order.fromAddress}</div>
                                    <div>ID {order.id}</div>
                                </div>
                                <div style={{ width: "35%" }}>
                                    <div>To {order.toAddress}</div>
                                    <div>{order.DateLoad}</div>
                                </div>
                                <div>
                                    {order.bidsCount == "0" && props.title.indexOf("New") != -1 &&
                                        <div
                                            className="me-2 p-1 rounded purply-lbl">
                                            {order.bidsCount} Bids
                                        </div>
                                    }


                                    {order.bidsCount = "0" &&
                                        <div className={"me-2 p-1 rounded " + (order.order_status === "Bid Accepted" ? "verdigris" : (order.order_status === "Delivered" || order.order_status === "Out for delivery") ? "purply" : "orangy") + "-lbl"} >
                                            {order.order_status}
                                        </div>
                                    }

                                    {/* {!order.isAcceptDelivery && order.bidsCount == "0" &&
                                        <div className="me-2 p-1 rounded orangy-lbl" >
                                            Waiting
                                        </div>
                                    }
                                    {!order.isAcceptDelivery && order.bidsCount != "0" &&
                                        <div

                                            className="me-2 p-1 rounded purply-lbl">
                                            {order.bidsCount} Bids
                                        </div>
                                    }

                                    {!order.orderfinished && !order.pickup_date && props.title.indexOf("Active") != -1 &&
                                        <div
                                            className="me-2 p-1 rounded verdigris-lbl">
                                            Accepted
                                        </div>
                                    }

                                    {!order.orderfinished && order.pickup_date && props.title.indexOf("Active") != -1 &&
                                        <div
                                            style={{
                                                color: "#987456",
                                                backgroundColor: "#e3dac9"
                                            }}
                                            className="me-2 p-1 rounded">
                                            OnGoing
                                        </div>
                                    } */}
                                </div>
                            </motion.div>
                        )
                    }) : <div style={{ width: "100%" }} className="mt-2 d-flex justify-content-center">No Orders</div>
                }
            </div>
        </div>
    )
}
