import React, { useEffect, useState } from "react";
import Address from "../Address/Address";
import Map from "../Address/Map"; /* edited (Map imported) */
import { Accordion, Card, Button, Badge, Dropdown, Form, Modal, Spinner, Col, Row, Container, Table } from "react-bootstrap"; /* edited (new importd) */
import { MdOutlineFastfood } from 'react-icons/md'; /* edited (new import) */
import { BiPackage } from 'react-icons/bi'; /* edited (new import) */
import { FiPackage } from 'react-icons/fi'; /* edited (new import) */
import { FcPackage } from 'react-icons/fc'; /* edited (new import) */
import { GiEnvelope } from 'react-icons/gi'; /* edited (new import) */

import { FinalizeOrder } from "../OrdersManager/FinalizeOrder";
import { CancelOrder } from "../OrdersManager/CancelOrder";
import { BidOrder } from "../OrdersManager/AcceptOrder";
import { DeleteTripCost } from "../OrdersManager/DeleteTripCost";
import { EditTripCost } from "../OrdersManager/EditTripCost";
import Loader from "../Loader/Loader";
import "./OrderDetails.css";
import { getOrderDetails, checkTripCost, AcceptOfferReq, GetTransporterLocation, getOrderActions, cancelAssignedOrder } from "../../APIs/OrdersAPIs";
import translate from "../../i18n/translate";

import { Alert, AlertDescription, AlertIcon, AlertTitle } from "@chakra-ui/alert";
import { Box } from "@chakra-ui/layout";
import { deliverFormatter, packageFormatter } from "../Orders/OrdersTabularView";
import { isTeamMember, isTransporter, isTransporterMaster } from "../../Util";
import AssignToTransporter from "../AssigneToTransporter";

import { useLocation, useParams, useHistory } from "react-router";

import AssignToMemberOnNetworkDialog from "../AssginToMemberOnNetworkDialog";
import { refreshPage } from "../../Functions/CommonFunctions";

import Timeline from "../TimeLine/TimeLine"; /* edited (TimeLine imported for old orders "to be removed") */
// import FullTimeline from "../FullTimeline/FullTimeline"; /* edited (FullTimeline imported) */

import Rating from '@material-ui/lab/Rating'; /* edited (Rating imported) */

import { transactionsByOrder, getTransporterInfo, getTimeLine, responseToAssignedOrder, recordAction } from '../../APIs/OrdersAPIs'; /* edited (transactionsByOrder, getTimeLine, responseToAssignedOrder imported) */

import { motion } from "framer-motion";

/* edited (to test the timeline) */
// import { acceptOffer, pickupPackage, acceptAssignedOrder } from '../../APIs/OrdersAPIs';

// var Barcode = require('react-barcode');

function timeFormat(time) {
    // Check correct time format and split into components
    time = time.toString().match(/^([01]\d|2[0-3])(:)([0-5]\d)(:[0-5]\d)?$/) || [time];

    if (time.length > 1) { // If time format correct
        time = time.slice(1);  // Remove full string match value
        time[5] = +time[0] < 12 ? ' AM' : ' PM'; // Set AM/PM
        time[0] = +time[0] % 12 || 12; // Adjust hours
    }
    return time.join(''); // return adjusted time or original string
}

const calculateRange = (data, rowsPerPage) => {
    const range = [];
    const num = Math.ceil(data.length / rowsPerPage);
    let i = 1;
    for (let i = 1; i <= num; i++) {
        range.push(i);
    }
    return range;
};

const sliceData = (data, page, rowsPerPage) => {
    return data.slice((page - 1) * rowsPerPage, page * rowsPerPage);
};

const useTable = (data, page, rowsPerPage) => {
    const [tableRange, setTableRange] = useState([]);
    const [slice, setSlice] = useState([]);

    useEffect(() => {
        const range = calculateRange(data, rowsPerPage);
        setTableRange([...range]);

        const slice = sliceData(data, page, rowsPerPage);
        setSlice([...slice]);
    }, [data, setTableRange, page, setSlice]);

    return { slice, range: tableRange };
};

const TableFooter = ({ range, setPage, page, slice }) => {
    useEffect(() => {
        if (slice.length < 1 && page !== 1) {
            setPage(page - 1);
        }
    }, [slice, page, setPage]);
    return (
        <div className={'tableFooter'}>

            {/*<Button onClick={() => console.log(range)}>{"<"}</Button>*/}

            {range.map((el, index) => (
                <button
                    key={index}
                    className={`${'button'} ${page === el ? 'activeButton' : 'inactiveButton'
                        }`}
                    onClick={() => setPage(el)}
                >
                    {el}
                </button>
            ))}
        </div>
    );
};

const CustomTable = ({ data, rowsPerPage }) => {
    const [page, setPage] = useState(1);
    const { slice, range } = useTable(data, page, rowsPerPage);
    return (
        <>
            <Table>
                <thead>
                    <tr>
                        {/*<th scope="col">{translate("TRANSACTIONS.ORDER_ID")}</th>*/}
                        <th scope="col">{translate("TRANSACTIONS.NAME")}</th>
                        <th scope="col">{translate("TRANSACTIONS.DATE")}</th>
                        <th scope="col">{translate("TRANSACTIONS.TIME")}</th>
                        <th scope="col">{translate("TRANSACTIONS.JOURNAL_NAME")}</th>
                        <th scope="col">{translate("TRANSACTIONS.AMOUNT")}</th>
                    </tr>
                    <tr>
                        <th scope="col" colSpan="4"></th>
                        <th scope="col">
                            <div className="d-flex justify-content-around">
                                <span>{translate("TRANSACTIONS.IN")}</span>
                                <span>{translate("TRANSACTIONS.OUT")}</span>
                            </div>
                        </th>
                    </tr>
                </thead>
                <tbody>
                    {slice.map((item, index) =>
                        <tr
                            key={index}
                        >

                            {/*<td>
                              {item.ref}
                            </td>*/}
                            <td>
                                {item.move_name}
                            </td>
                            <td>
                                {item.create_date.split(" ")[0]}
                            </td>
                            <td>
                                {timeFormat(item.create_date.split(" ")[1])}
                            </td>
                            <td>
                                {item.journal_id_name}
                            </td>
                            <td>
                                <div className="d-flex justify-content-around">
                                    <span>{item.credit !== 0 && item.credit + " NIS"}</span>
                                    <span>{item.debit !== 0 && item.debit + " NIS"}</span>
                                </div>
                            </td>

                        </tr>
                    )}
                    {/*{!slice.length && <tr><td colSpan={columns.length}>Transactions not found!</td></tr>}*/}
                </tbody>
            </Table>
            <TableFooter range={range} slice={slice} setPage={setPage} page={page} />
        </>
    );
};

const OrderDetails = () => {

    const [refresh, setRefresh] = useState(false);

    const [show, setShow] = useState(false);
    const [cost, setCost] = useState("");

    const handleClose = () => {
        setShow(false);
    };

    const handleShow = () => setShow(true);

    const { id: orderId } = useParams();
    const location = useLocation();
    const history = useHistory();
    const [orderDetails, setOrderDetails] = useState({});
    const [orderActions, setOrderActions] = useState([]); /* edited (add orderActions) */
    const [timelineArr, setTimelineArr] = useState([]); /* edited (add timeline variable) */
    const [previousPage, setPreviousPage] = useState(null);
    const [tripCost, setTripCost] = useState(null);
    const [costSet, setCostSet] = useState(null);
    const [receiverAddress, setReceiverAddress] = useState({});
    const [startTracking, setStartTracking] = useState(false);

    const [previousTransporter, setPreviousTransporter] = useState({ // edited (add previousTransporter) for old orders
        type: 2,
        current: false,
        address: null,
        name: "",
        phone: "",
        price: null,
        imageURL: "",
        pickupDate: null
    });

    /* edited (selected available offer variables added) */
    const [bidReqTransImg, setBidReqTransImg] = useState();
    const [bidReqTransPrice, setBidReqTransPrice] = useState();
    const [bidReqTransId, setBidReqTransId] = useState();
    const [bidReqTransRate, setBidReqTransRate] = useState();
    const [bidReqTransName, setBidReqTransName] = useState();

    const [transactions, setTransactions] = useState([]); // edited (add order transactions variable)

    const styles = {
        cardHeaderLg: {
            position: 'absolute',
            left: '20px',
            right: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold',
            fontSize: "1.5rem"
        },
        cardHeaderSm: {
            position: 'absolute',
            left: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold',
            fontSize: "1.2rem"
        },
        orderNumber: {
            color: 'white',
            fontStyle: 'italic',
            marginLeft: '10px',
            marginRight: '10px'
        },
        orderStatus: {
            float: localStorage.getItem("Language") === "en" ? 'right' : 'left',

        }
    }

    useEffect(() => {

        let isMounted = true;
        transactionsByOrder(localStorage.getItem("userId"), orderId).then(res => {
            if (isMounted) {
                let tempArr = res.data.server_response.data.result.response;

                setTransactions(tempArr);
            }
        })
        return () => { isMounted = false };
    }, []);

    useEffect(() => {
        let live = false;
        if (startTracking === false) {
            return;
        }
        const intervalId = setInterval(() => {
            if (!live) {
                live = true;
                GetTransporterLocation(orderId).then((resp) => {
                    live = false;
                    let { TransporterLatLocation, TransporterLongLoc } = resp.data.server_response[0];
                    if (TransporterLatLocation === null || TransporterLongLoc === null) {
                        TransporterLatLocation = 0;
                        TransporterLongLoc = 0;
                    }
                    setReceiverAddress({
                        ...receiverAddress,
                        long: TransporterLongLoc,
                        lat: TransporterLatLocation
                    });
                });
            }
        }, 4000);
        return () => clearInterval(intervalId);
    }, [startTracking])

    /* edited (move checkTripCost to an updating useEffect to update it at any change) */

    useEffect(() => {
        /* const interval = setInterval(() => {
            checkTripCost(orderId, isTransporter()).then((tripCostRes) => {
                if (tripCostRes && tripCostRes !== "CostNotSend") {
                    setTripCost(isTransporter() ? tripCostRes.server_response[0] : tripCostRes.CostDetail);
                    setCostSet(true);
                } else {
                    setCostSet(false);
                }
            });
        }, 1000);
        return () => clearInterval(interval); */
        checkTripCost(orderId, isTransporter()).then((tripCostRes) => {
            if (tripCostRes && tripCostRes !== "CostNotSend") {
                setTripCost(isTransporter() ? tripCostRes.server_response[0] : tripCostRes.CostDetail);
                setCostSet(true);
            } else {
                setCostSet(false);
            }
        });
    }, []) /* to be edited */

    useEffect(() => {
        /* edited (comment checkTripCost request) */

        /*checkTripCost(orderId, isTransporter()).then((tripCostRes) => {
            if (tripCostRes && tripCostRes !== "CostNotSend") {
                setTripCost(isTransporter() ? tripCostRes.server_response[0] : tripCostRes.CostDetail);
                setCostSet(true);
            } else {
                setCostSet(false);
            }
        });*/

        getOrderDetails(orderId).then((orderDetailsRes) => {

            // console.log(orderDetailsRes); // temp test

            setOrderDetails(orderDetailsRes);

            setReceiverAddress({
                otherDetails: orderDetailsRes.OtherDetailsDes + ", " + orderDetailsRes.IdCityDes,
                phoneCustomer: orderDetailsRes.ReceiverAddressNum,
                long: orderDetailsRes.LongReciver,
                lat: orderDetailsRes.LatReciver
            });
        });

        getOrderActions(orderId).then((res) => {
            // console.log(res.data);

            setOrderActions(res.data.records_list);
        })

        setPreviousPage(location.state?.currentPage);
    }, [refresh]);

    /* edited (getTimeLine called) */
    useEffect(() => {
        getTimeLine(orderId).then((res) => {
            //  console.log(res.data.timeline); // temp test
            setTimelineArr(res.data.timeline)
        });
    }, [refresh]);

    function getTransporterPersonalInfo(transporterId) {
        let tempTrans = {
            type: 2,
            current: false,
            address: null,
            name: "",
            phone: "",
            price: null,
            imageURL: "",
            pickupDate: null,
            assignDate: null
        }

        getTransporterInfo(transporterId).then((res) => {

            tempTrans.name = res.data.server_response.fullName;
            tempTrans.phone = res.data.server_response.PhoneNumber;
            tempTrans.imageURL = res.data.server_response.PersonalImgPath;
        });

        // console.log(tempTrans);
        return tempTrans;
    }

    const formatAction = (record) => {

        const tempHolder = <div style={{ width: "40%" }}>
            <div style={{ fontWeight: "bold", color: "#03A65A" }}>{record.description}</div>
            <div>{record.time_stamp.split(" ")[0] + " "}<Badge bg="primary">{record.time_stamp.split(" ")[1]}</Badge></div>
        </div>

        switch (record.action_id) {
            case "2":
                return <>
                    {tempHolder}
                    <div style={{ width: "60%" }}>{record.party_one_name + " created order " + record.order_id}</div>
                </>;
            case "3":
                return <>
                    {tempHolder}
                    <div style={{ width: "60%" }}>{record.party_one_name + " bid on order " + record.order_id}</div>
                </>;
            case "4":
                return <>
                    {tempHolder}
                    <div style={{ width: "60%" }}>{record.party_one_name + " cancel order " + record.order_id}</div>
                </>;
            case "5":
                return <>
                    {tempHolder}
                    <div style={{ width: "60%" }}>{record.party_one_name + " updated bid on order " + record.order_id}</div>
                </>;
            case "6":
                return <>
                    {tempHolder}
                    <div style={{ width: "60%" }}>{record.party_one_name + " canceled bid on order " + record.order_id}</div>
                </>;
            case "7":
                return <>
                    {tempHolder}
                    <div style={{ width: "60%" }}>{record.party_one_name + " accepted " + record.party_two_name + "'s bid on order " + record.order_id}</div>
                </>;
            case "8":
                return <>
                    {tempHolder}
                    <div style={{ width: "60%" }}>{record.party_one_name + " picked up order " + record.order_id}</div>
                </>;
            case "9":
                return <>
                    {tempHolder}
                    <div style={{ width: "60%" }}>{record.party_one_name + " finished order " + record.order_id}</div>
                </>;
            case "10":
                return <>
                    {tempHolder}
                    <div style={{ width: "60%" }}>{record.party_one_name + " assigned order " + record.order_id + " to " + record.party_two_name}</div>
                </>;
            case "11":
                return <>
                    {tempHolder}
                    <div style={{ width: "60%" }}>{record.party_one_name + " rejected order " + record.order_id}</div>
                </>;
            case "12":
                return <>
                    {tempHolder}
                    <div style={{ width: "60%" }}>{record.party_one_name + " accepted order " + record.order_id}</div>
                </>;
            case "17":
                return <>
                    {tempHolder}
                    <div style={{ width: "60%" }}>{record.party_one_name + " canceled assign on order " + record.order_id}</div>
                </>;
        }
    }

    // console.log(orderDetails); /* temp test */

    if (typeof orderDetails === "object" && JSON.stringify(orderDetails) !== '{}') {
        let {
            OrderStatus, /* edited (OrderStatus added) */
            DateLoad, /* edited (DateLoad added) */
            DeliveryPrice, /* edited (DeliveryPrice added) */
            CostLoad, /* edited (CostLoad added) */
            OriginalDeliveryId, /* edited (OriginalDeliveryId added) */
            DeliveryId, /* edited (DeliveryId added) */
            AssignToName, /* edited (AssignToName added) */
            AssignToNumber, /* edited (AssignToNumber added) */
            transporterImgURL, /* edited (transporterImgURL added) */
            pickupDate, /* edited (pickupDate added) */
            customerImgURL, /* edited (customerImgURL added) */
            FullNameCustomer, /* edited (FullNameCustomer added) */
            LastNameCustomer, /* edited (LastNameCustomer added) */
            orderFinished, /* edited (orderFinished added) */
            IdCityDes, /* edited (IdCityDes added) */
            IdCitySource, /* edited (IdCitySource added) */
            isAssigned, /* edited (isAssigned added) */
            AssignerId, /* edited (AssignerId added) */
            order_status, /* edited (order_status added) */
            deliveryWay,
            HeightLoad,
            DetailsLoad,
            LengthLoad,
            WeightLoad,
            WidthLoad,
            OtherDetails,
            TypeLoad,
            PhoneCustomer,
            LongSender, LatSender,
            AssignStatus, AssignedMemberName,
            isAcceptDelivery,
            SenderName,
            ReceiverName
        } = orderDetails;

        let senderAddress = {
            name: SenderName,
            otherDetails: OtherDetails + ", " + IdCitySource,
            phoneCustomer: PhoneCustomer,
            long: LongSender,
            lat: LatSender
        };

        /* edited (timeline data:) */

        let tempTransportationParties = [];

        if (timelineArr.length !== 0) { // new timeline created for new orders to show all order related parties

            tempTransportationParties = [
                {
                    type: 1,
                    current: false,
                    address: senderAddress,
                    customerName: FullNameCustomer + " " + LastNameCustomer,
                    name: SenderName,
                    imageURL: customerImgURL
                },
                {
                    type: 3,
                    current: false,
                    address: receiverAddress,
                    name: ReceiverName
                }
            ]

            for (let i = 0; i < timelineArr.length; i++) {

                // let tempTrans = getTransporterPersonalInfo(timelineArr[i].transporter_id);
                // console.log(tempTrans);

                /* tempTrans.price = timelineArr[i].transporter_bidprice;
                tempTrans.pickupDate = timelineArr[i].transporter_pickupdate;
                tempTrans.current = timelineArr[i].isCurrent === "1" ? true : false;
                tempTrans.assignDate = timelineArr[i].assign_date; */

                tempTransportationParties.splice(i + 1, 0, {
                    type: 2,
                    address: null,
                    name: timelineArr[i].fullName,
                    phone: timelineArr[i].PhoneNumber,
                    price: timelineArr[i].transporter_bidprice,
                    current: timelineArr[i].isCurrent === "1" ? true : false,
                    imageURL: timelineArr[i].PersonalImgPath,
                    pickupDate: timelineArr[i].transporter_pickupdate,
                    assignDate: timelineArr[i].assign_date
                })

                // console.log(tempTrans);
            }

        } else { // temp code to view old orders timeline. it shows sender, receiver, first transporter, and last transporter

            /* if (DeliveryId !== OriginalDeliveryId && DeliveryId !== null) {
                getTransporterInfo(OriginalDeliveryId).then((res) => { // nono
                    // console.log(res.data) // temp test

                    previousTransporter.name = res.data.server_response.fullName;
                    previousTransporter.phone = res.data.server_response.PhoneNumber;
                    previousTransporter.imageURL = res.data.server_response.PersonalImgPath;
                });
            } */

            if (/* AssignToName !== null */ false) {
                if (DeliveryId !== OriginalDeliveryId) {
                    tempTransportationParties = [
                        {
                            type: 1,
                            current: false,
                            address: senderAddress,
                            customerName: FullNameCustomer + " " + LastNameCustomer,
                            name: SenderName,
                            imageURL: customerImgURL
                        },
                        previousTransporter, // assigner transpoter
                        { // assignee transporter
                            type: 2,
                            address: null,
                            name: AssignToName,
                            phone: AssignToNumber,
                            price: DeliveryPrice,
                            current: true,
                            imageURL: transporterImgURL,
                            pickupDate: pickupDate
                        },
                        {
                            type: 3,
                            current: false,
                            address: receiverAddress,
                            name: ReceiverName
                        }
                    ]
                } else {
                    tempTransportationParties = [
                        {
                            type: 1,
                            current: false,
                            address: senderAddress,
                            customerName: FullNameCustomer + " " + LastNameCustomer,
                            name: SenderName,
                            imageURL: customerImgURL
                        },
                        {
                            type: 2,
                            address: null,
                            name: AssignToName,
                            phone: AssignToNumber,
                            price: DeliveryPrice,
                            current: true,
                            imageURL: transporterImgURL,
                            pickupDate: pickupDate
                        },
                        {
                            type: 3,
                            current: false,
                            address: receiverAddress,
                            name: ReceiverName
                        }
                    ]
                }
            } else {
                tempTransportationParties = [
                    {
                        type: 1,
                        current: false,
                        address: senderAddress,
                        customerName: FullNameCustomer + " " + LastNameCustomer,
                        name: SenderName,
                        imageURL: customerImgURL
                    },
                    {
                        type: 3,
                        current: false,
                        address: receiverAddress,
                        name: ReceiverName
                    }
                ]
            }
        }

        // console.log("------ previousPage: " + previousPage); // temp test

        return (

            <React.Fragment>
                {orderDetails && <Container fluid className='ps-5 pe-5 pb-5'>

                    <div style={{ position: "absolute", left: 0, right: 0, backgroundColor: "#ededed", height: "140px", zIndex: "-1" }}></div>

                    <Row className="">
                        {/* Order main info (order number, delivery type, order time, and load details) */}
                        <Col className="mt-5" xl="4">
                            <Card className="shadow rounded-22 p-2">
                                <Card.Header style={styles.cardHeaderLg}>
                                    <span style={styles.orderNumberTitle}>{translate("TEMP.ORDER")}</span>
                                    <span style={styles.orderNumber}>{orderId}</span>
                                    <span style={styles.orderStatus}>
                                        {
                                            <Badge bg="light" style={{ color: "#35b09d" }}>{order_status}</Badge>
                                        }
                                    </span>
                                </Card.Header>
                                <Card.Body>
                                    <div>
                                        <Table style={{ fontSize: "1.2rem", marginTop: "30px" }}>
                                            <tbody>
                                                <tr>
                                                    <th scope="row">
                                                        <span>{translate("ORDER_DETAILS.DELIVERY_TYPE")}</span>
                                                    </th>
                                                    <td style={{ textAlign: "right" }}>
                                                        <span style={{ color: "#35b09d" }}>
                                                            {deliverFormatter({ DeliveryWays: deliveryWay })}
                                                            {CostLoad && <Badge bg="secondary ms-1">{CostLoad} NIS</Badge>}
                                                        </span>
                                                    </td>
                                                </tr>

                                                <tr>
                                                    <th scope="row">
                                                        <span>{translate("ORDERS.ORDER_TIME")}</span>
                                                    </th>
                                                    <td style={{ textAlign: "right" }}>
                                                        <span style={{ color: "#35b09d" }}>{DateLoad.split(" ")[0]} <Badge bg="secondary">{/*DateLoad.split(" ")[1]*/ timeFormat(DateLoad.split(" ")[1])}</Badge></span>
                                                    </td>
                                                </tr>

                                                <tr>
                                                    <th scope="row">
                                                        <span>{translate("ORDER_DETAILS.LOAD_DETAILS")}</span>
                                                    </th>
                                                    <td style={{ textAlign: "right" }}>
                                                        <span style={{ color: "#35b09d" }}>{DetailsLoad}</span>
                                                    </td>
                                                </tr>
                                            </tbody>
                                        </Table>

                                    </div>
                                </Card.Body>
                            </Card>
                        </Col>

                        {/* Actions Buttons */}
                        <Col className="mt-5" xl="8">
                            <Row style={{
                                marginTop: "40px"
                            }}>
                                {/* Display assign status for transporters (when order is assigned to another transporter and when the other transporter response to the asigned order) */}
                                {isTransporter() && order_status !== "Delivered" && <Col>
                                    {
                                        AssignerId === localStorage.getItem("userId") && isAssigned == 1 ? <div style={{ marginTop: "0" }}><Button variant="outline-danger" disabled>{translate("TEMP.ASSIGNED_AND_WAITING")}</Button></div> :
                                            AssignerId !== localStorage.getItem("userId") && isAssigned == 1 ? <div style={{ marginTop: "0" }}><Button variant="outline-danger" disabled>{translate("TEMP.ASSIGNED_TO_ME")}</Button></div> :
                                                AssignerId === localStorage.getItem("userId") && isAssigned == 2 ? <div style={{ marginTop: "0" }}><Button variant="outline-danger" disabled>{translate("TEMP.ASSIGNED_AND_ACCEPTED")}</Button></div> :
                                                    AssignerId === localStorage.getItem("userId") && isAssigned == 0 ? <div style={{ marginTop: "0" }}><Button variant="outline-danger" disabled>{translate("TEMP.ASSIGNED_AND_REJECTED")}</Button></div> : <></>
                                    }
                                </Col>}

                                <Col>

                                    {/* For tansporter, display edit and delete bid buttons when the order is waiting for bids and there is tripCost(bid price) */}
                                    {order_status === 'Waiting for Bids' && isTransporter() && tripCost && <div className="ActionButtons ms-auto">
                                        <DeleteTripCost orderId={orderId} />
                                        <EditTripCost orderId={orderId} tripCost={tripCost.CostDelivery} />
                                    </div>
                                    }

                                    {/* For client, display cancel and print order buttons when the is waiting for bids */}
                                    {order_status === 'Waiting for Bids' && !isTransporter() &&
                                        <div className="d-flex align-items-center">
                                            <Button
                                                style={{ width: "150px", marginLeft: "10px", marginRight: "10px" }}
                                                className="ms-auto"
                                                onClick={() => {
                                                    history.push("/account/print/" + orderId + "?print=true");
                                                }}
                                            >
                                                {translate("ORDER_DETAILS.PRINT")}
                                            </Button>
                                            <CancelOrder orderId={orderId} className="mx-1" onSuccess={() => {
                                                history.goBack();
                                            }} />
                                        </div>
                                    }

                                    {/* For transporter, display bid button when the order is waiting for bids */}
                                    {order_status === 'Waiting for Bids' && isTransporter() &&
                                        !costSet && isTransporter() && <div className="d-flex justify-content-end">
                                            <BidOrder orderId={orderId} />
                                        </div>
                                    }

                                    {/* For transporter, display print order, accept order, reject order, and cancel assigned order buttons when the order is not waiting for bids */}
                                    {order_status !== 'Waiting for Bids' && (
                                        <Box display="flex" alignItems="center" justifyContent="end">
                                            <Button
                                                variant="outline-primary"
                                                onClick={() => {
                                                    history.push("/account/print/" + orderId + "?print=true");
                                                }}
                                                style={{ marginRight: "5px", width: "150px" }}
                                            >
                                                {translate("ORDER_DETAILS.PRINT")}
                                                <svg style={{ display: "inline-block", marginLeft: "5px", marginRight: "5px" }} xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-printer" viewBox="0 0 16 16">
                                                    <path d="M2.5 8a.5.5 0 1 0 0-1 .5.5 0 0 0 0 1z" />
                                                    <path d="M5 1a2 2 0 0 0-2 2v2H2a2 2 0 0 0-2 2v3a2 2 0 0 0 2 2h1v1a2 2 0 0 0 2 2h6a2 2 0 0 0 2-2v-1h1a2 2 0 0 0 2-2V7a2 2 0 0 0-2-2h-1V3a2 2 0 0 0-2-2H5zM4 3a1 1 0 0 1 1-1h6a1 1 0 0 1 1 1v2H4V3zm1 5a2 2 0 0 0-2 2v1H2a1 1 0 0 1-1-1V7a1 1 0 0 1 1-1h12a1 1 0 0 1 1 1v3a1 1 0 0 1-1 1h-1v-1a2 2 0 0 0-2-2H5zm7 2v3a1 1 0 0 1-1 1H5a1 1 0 0 1-1-1v-3a1 1 0 0 1 1-1h6a1 1 0 0 1 1 1z" />
                                                </svg>
                                            </Button>

                                            {/* display accept order, reject order, and cancel assigned order buttons when order is not finished */}
                                            {order_status !== "Delivered" &&
                                                <>
                                                    {/*
                                                     display "assign to member on network" button when 
                                                        there is no assigner (AssignerId == null) OR 
                                                        the current user (transporter) is not the assigner AND the assigne is accepted from the other party (AssignerId !== localStorage.getItem("userId") && isAssigned == 2) OR
                                                        the current user (transporter) is the assigner AND the order is not assigned (AssignerId === localStorage.getItem("userId") && isAssigned == 0) OR
                                                        there is no assigner AND the order is not assigned (AssignerId == -1 && isAssigned == 0)
                                                    */}
                                                    {AssignerId == null || (AssignerId !== localStorage.getItem("userId") && isAssigned == 2) || (AssignerId === localStorage.getItem("userId") && isAssigned == 0) || (AssignerId == -1 && isAssigned == 0) ? <AssignToMemberOnNetworkDialog
                                                        onSuccess={() => {
                                                            setRefresh(!refresh);
                                                        }}
                                                        orderId={orderId}
                                                    /> : AssignerId !== localStorage.getItem("userId") && isAssigned == 1 ? // display accept and reject assigned order button when the current user (transporter) is not the assigner AND the order is assigned
                                                        <>
                                                            <Button style={{ marginRight: "5px", width: "150px" }} onClick={() => {
                                                                responseToAssignedOrder(orderId, "accept").then((res) => {
                                                                    setRefresh(!refresh);
                                                                })
                                                            }}>{translate("TEMP.ACCEPT")}</Button>
                                                            <Button variant="danger" style={{ marginRight: "5px", width: "150px" }} onClick={() => {
                                                                responseToAssignedOrder(orderId, "reject").then((res) => {
                                                                    history.goBack();
                                                                })
                                                            }}>{translate("TEMP.REJECT")}</Button>
                                                        </> : AssignerId === localStorage.getItem("userId") && isAssigned == 1 ? // display cancel assigned order button when the current user is the assigner AND the order is assigned
                                                            <>
                                                                <Button style={{ marginRight: "5px", width: "150px" }} variant="danger" onClick={() => {
                                                                    cancelAssignedOrder(orderId).then((res) => {
                                                                        setRefresh(!refresh);
                                                                    })
                                                                }}>{translate("TEMP.CANCEL_ASSIGN")}</Button>
                                                            </> :
                                                            <></>}
                                                </>
                                            }
                                        </Box>
                                    )}</Col>
                            </Row>
                        </Col>
                    </Row>

                    {/* Transportation Timeline */}
                    <Row className="mt-5">
                        <Col>
                            <Timeline transportationParties={tempTransportationParties} />
                        </Col>
                    </Row>

                    <Row className="mt-5">
                        {/* Load Details */}
                        <Col xl="3">
                            <Card className='shadow h-100 rounded-22'>
                                <Card.Header style={styles.cardHeaderSm}>
                                    {translate("TEMP.LOAD_INFO")}
                                </Card.Header>
                                <Card.Body className='mt-3'>
                                    <div className="w-100 d-flex justify-content-center mt-2" style={{ fontSize: "1.5rem" }}>
                                        <Badge bg="warning" className="rounded-22">
                                            {
                                                TypeLoad === "1" ? <MdOutlineFastfood style={{ height: '50px', width: '50px' }} /> :
                                                    TypeLoad === "2" ? <FcPackage style={{ height: '50px', width: '50px' }} /> :
                                                        TypeLoad === "3" ? <BiPackage style={{ height: '50px', width: '50px' }} /> :
                                                            <FiPackage style={{ height: '50px', width: '50px' }} />
                                            }
                                        </Badge>
                                    </div>
                                    <div className="w-100 d-flex justify-content-center" style={{ fontSize: "1.5rem" }}>
                                        {packageFormatter({ PackageType: TypeLoad })}
                                    </div>
                                    <Table className="mt-3">
                                        <tbody>
                                            <tr>
                                                <th scope='row'>{translate("ORDER_DETAILS.LOAD_HEIGHT")}</th>
                                                <td>{HeightLoad ? HeightLoad : "--"}</td>
                                            </tr>
                                            <tr>
                                                <th scope='row'>{translate("ORDER_DETAILS.LOAD_WIDTH")}</th>
                                                <td>{WidthLoad ? WidthLoad : "--"}</td>
                                            </tr>
                                            <tr>
                                                <th scope='row'>{translate("ORDER_DETAILS.LOAD_LENGTH")}</th>
                                                <td>{LengthLoad ? LengthLoad : "--"}</td>
                                            </tr>
                                            <tr>
                                                <th scope='row'>{translate("ORDER_DETAILS.LOAD_WEIGHT")}</th>
                                                <td>{WeightLoad ? WeightLoad : "--"}</td>
                                            </tr>
                                        </tbody>
                                    </Table>
                                </Card.Body>
                            </Card>
                        </Col>

                        {/* Map / Available Offers */}
                        <Col xl="5">
                            <Card className='shadow h-100 rounded-22'>
                                <Card.Header style={styles.cardHeaderSm}>
                                    {!isTransporter() && order_status === "Waiting for Bids" ? translate("ORDERS.AVAILABLE_OFFERS") : translate("TEMP.LOCATION")}
                                </Card.Header>

                                {!isTransporter() && order_status === "Waiting for Bids" ? <Card.Body className='mt-4' style={{ height: '345.8px', overflowY: 'scroll' }}>
                                    <Table>
                                        <thead>
                                            <tr>
                                                <th></th>
                                                <th>Name</th>
                                                <th>Price</th>
                                                <th></th>
                                            </tr>
                                        </thead>
                                        {previousPage === "all-orders" && !isTransporter() && tripCost && tripCost.length !== 0 ? <tbody>
                                            {tripCost.map((costs, index) => {
                                                return <tr key={index}>
                                                    <td><img style={{
                                                        width: "60px",
                                                        height: "60px",
                                                        objectFit: "cover"
                                                    }}
                                                        className="rounded-circle align-self-center" src={`http://46.253.93.4/togo/MobileAPi/${costs.TransporterPersonalImg}`} alt="transImg" />
                                                    </td>
                                                    <td>{costs.TransporterName}</td>
                                                    <td>{costs.BidCost}</td>
                                                    <td>
                                                        <Button
                                                            variant="primary"
                                                            onClick={() => { handleShow(); setBidReqTransName(costs.TransporterName); setBidReqTransRate(costs.TotalRate); setBidReqTransId(costs.IdTransporterBidRequist); setBidReqTransPrice(costs.BidCost); setBidReqTransImg(costs.TransporterPersonalImg) }}
                                                            style={{

                                                            }}
                                                        >
                                                            {translate("TEMP.ACCEPT_OFFER")}
                                                        </Button>
                                                        <Modal show={show} onHide={handleClose} centered>
                                                            <Modal.Header closeButton>
                                                                <Modal.Title>{translate("TEMP.ACCEPT_OFFER")}</Modal.Title>
                                                            </Modal.Header>

                                                            <Modal.Body>
                                                                <div className="" style={{ fontSize: "1.5rem" }}>
                                                                    <div className="w-100 d-flex justify-content-center">
                                                                        <div style={{
                                                                            background: "linear-gradient(90deg, #26a69a, #69d4a5)",

                                                                            width: "80px",
                                                                            height: "80px",
                                                                        }}
                                                                            className="d-flex justify-content-center rounded-circle align-self-center">
                                                                            <img
                                                                                style={{
                                                                                    width: "80%",
                                                                                    height: "80%",
                                                                                    objectFit: "cover"
                                                                                }}
                                                                                className="rounded-circle align-self-center bg-white"
                                                                                src={`http://46.253.93.4/togo/MobileAPi/${bidReqTransImg}`}
                                                                                alt={"img"}

                                                                            />
                                                                        </div>
                                                                    </div>
                                                                    <div className="d-flex justify-content-between">
                                                                        <div>Transporter:</div>
                                                                        <div>{bidReqTransName}</div>
                                                                    </div>
                                                                    <div className="d-flex justify-content-between">
                                                                        <div>Bid Price:</div>
                                                                        <div>{bidReqTransPrice}</div>
                                                                    </div>
                                                                    <div className="w-100 d-flex justify-content-center">
                                                                        <Rating name="size-large" size="large" defaultValue={bidReqTransRate} precision={0.1} readOnly />
                                                                    </div>
                                                                </div>
                                                            </Modal.Body>
                                                            <Modal.Footer>
                                                                <Button variant="secondary" onClick={handleClose}>
                                                                    {translate("GENERAL.CANCEL")}
                                                                </Button>
                                                                <Button
                                                                    variant="primary"
                                                                    onClick={() => {
                                                                        AcceptOfferReq(bidReqTransId, orderId, bidReqTransPrice).then((resp) => {
                                                                            if (resp.data.indexOf("Success") !== -1) {
                                                                                // refreshPage();
                                                                                history.goBack();
                                                                            }
                                                                        })
                                                                    }}
                                                                >
                                                                    {translate("TEMP.ACCEPT")}
                                                                </Button>
                                                            </Modal.Footer>
                                                        </Modal>
                                                    </td>
                                                </tr>
                                            })}
                                        </tbody> : <tbody>
                                            <tr>
                                                <td colSpan={4}>{translate("ORDERS.NO_OFFERS")}</td>
                                            </tr>
                                        </tbody>}
                                    </Table> </Card.Body> : <Card.Body className='mt-4'><Map className="" address1={senderAddress} address2={receiverAddress} /> </Card.Body>}

                            </Card>
                        </Col>

                        {/* Order's Actions Log */}
                        <Col xl="4">
                            <Card className='shadow h-100 rounded-22'>
                                <Card.Header style={styles.cardHeaderSm}>
                                    {translate("TEMP.ORDER_ACTIONS")}
                                </Card.Header>
                                <Card.Body className='mt-4' style={{ height: '345.8px', overflowY: 'scroll' }}>
                                    {
                                        orderActions?.map((record, index) => {
                                            return (<div key={index}>
                                                <motion.div
                                                    key={index}
                                                    initial={{ backgroundColor: "#fff" }}
                                                    whileHover={{ backgroundColor: "#f0fff0" }}
                                                    whileTap={{ scale: 0.95 }}
                                                    className="align-items-center d-flex gap-3 mb-2 w-100"
                                                >
                                                    {formatAction(record)}

                                                </motion.div>
                                                <hr style={{ marginRight: "10px" }} />
                                            </div>
                                            )
                                        })
                                    }
                                    <hr style={{ marginRight: "10px" }} />
                                </Card.Body>
                            </Card>
                        </Col>
                    </Row>

                    {/* Display Map in another row if available-orrfers displaied */}
                    {!isTransporter() && order_status === "Waiting for Bids" && <Row className="mt-5">
                        <Col>
                            <Card className='shadow rounded-22'>
                                <Card.Header style={styles.cardHeaderSm}>
                                    {translate("TEMP.LOCATION")}
                                </Card.Header>
                                <Card.Body className='mt-3'>
                                    <Map className="" address1={senderAddress} address2={receiverAddress} />
                                </Card.Body>
                            </Card>
                        </Col>
                    </Row>}

                    {/* Order's Financial Transactions */}
                    {transactions.length > 0 && <Row className="mt-5">
                        <Card className='shadow rounded-22'>
                            <Card.Header style={styles.cardHeaderSm}>
                                {translate("TEMP.ORDER_TRANSACTIONS")}
                            </Card.Header>
                            <Card.Body className="mt-3">
                                <CustomTable data={transactions} rowsPerPage={15} />
                            </Card.Body>
                        </Card>
                    </Row>}
                </Container>}
                {/* edited (to test the timeline) */}
                {/* <div className="mt-5">
                    <Button variant="outline-light" onClick={() => {console.log("test 1"); acceptOffer(1000, 12, 11).then((res) => { console.log(res.data) })}}>test 1 (accecpt offer)</Button>
                    <Button variant="outline-light" onClick={() => {console.log("test 2"); pickupPackage(1000, 12).then((res) => { console.log(res.data) })}}>test 2 (pickup package)</Button>
                    <Button variant="outline-light" onClick={() => {console.log("test 3"); acceptAssignedOrder(1000, 40).then((res) => { console.log(res.data) })}}>test 3 (accept assigned order)</Button>
                </div> */}

                {/* edited (to test actions records) */}
                {/* recordAction(party_one_id, party_one_name, party_two_id, party_two_name, order_id, description, action_id) */}
                {/* <div className="mt-5">
                    <Button variant="outline-light" onClick={() => {recordAction(41, "party_one_name", 12, "party_two_name", 100, "description", 5).then((res) => { console.log(res.data) })}}>test</Button>
                </div> */}
            </React.Fragment>
        );
    } else if (typeof orderDetails === "string") {
        return (
            <Alert status="error" w="80%" m="10px auto">
                <AlertIcon />
                <AlertTitle mr={2}>{orderId}</AlertTitle>
                <AlertDescription>{orderDetails}</AlertDescription>
            </Alert>
        );
    } else {
        return <Box height="400px"><Loader /></Box>
    }
}

export default OrderDetails;
