import React from 'react';
import {Table, Badge} from "react-bootstrap";
import translate from '../../i18n/translate';

export default function DynamicTable({columns, data, onRowSelect, selected}) {

    // console.log(data); // temp test

    return (
        <Table hover size="sm" responsive className="ms-4 me-4 mt-2">
            <thead>
                <tr>
                    {columns.map(({label, key}, idx) => <th key={key+"-"+idx}>{label}</th>)}
                </tr>
            </thead>
            <tbody>
            {data.map((item, index) =>
                <tr 
                    key={index + "-tr"} 
                    onClick={onRowSelect ? ()=>{onRowSelect(item, index)} : undefined} 
                    className={`${selected === index ? "highlight-row": ""}`}>
                    {columns.map(({key, format}, idx) =>
                        <td key={key + "-td" + idx}>
                            {item[key] === "Waiting for bid" && item.bidsCount === 0 ? <Badge pill bg="primary">{!!format ? format(item) : item[key]}</Badge> : 
                            item[key] === "Waiting for bid" && item.bidsCount === 1 ? <Badge pill bg="primary">{item.bidsCount + " bid"}</Badge> : 
                            item[key] === "Waiting for bid" && item.bidsCount > 1 ? <Badge pill bg="primary">{item.bidsCount + " bids"}</Badge> : 
                            item[key] === "Out For Delivery" ? <Badge pill bg="warning">{!!format ? format(item) : item[key]}</Badge> :
                            item[key] === "Bid Accepted" ? <Badge pill bg="success">{!!format ? format(item) : item[key]}</Badge> :
                            item[key] === "Delivered" || item[key] === "Delivered and Collected" ? <Badge pill bg="danger">{!!format ? format(item) : item[key]}</Badge> :
                            item[key] === "Order Deleted" ? <Badge pill bg="secondary">{!!format ? format(item) : item[key]}</Badge> :
                            <span>{!!format ? format(item) : item[key]}</span>}
                        </td>
                    )}
                </tr>
            )}
            {!data.length && <tr><td colSpan={columns.length}>{translate("ORDERS.NO_ORDERS_FOUND")}</td></tr>}
            </tbody>
        </Table>
    );
};
