                                                    <th scope="col">Name</th>
                                                    <th scope="col" style={{ width: "10%" }}>Phone</th>
                                                    <th scope="col" style={{ width: "10%" }}>Auto offer</th>
                                                    <th scope="col" style={{ width: "40%" }}>Description</th>
                                                </tr>
                                            </thead>

                                            <tbody>
                                                {networkMembers?.map((member, index) => {
                                                    return (
                                                        <Network
                                                            key={index}
