import React, { useEffect, useState } from 'react';
import translate from "../../i18n/translate";
import './FinancialTransactions.css';
import DynamicTable from '../DynamicTable/DynamicTable';
import { isTransporter } from '../../Util';

import { Button, Form } from "react-bootstrap";

import { transactionTest, invoicesTest } from '../../APIs/OrdersAPIs';

import Table from "react-bootstrap/Table";

/* styles */

import "./FinancialTransactions.css";

import { Link } from "react-router-dom";

import Loader from "../Loader/Loader";

import { Box } from "@chakra-ui/layout";


const calculateRange = (data, rowsPerPage) => {
  const range = [];
  const num = Math.ceil(data.length / rowsPerPage);
  let i = 1;
  for (let i = 1; i <= num; i++) {
    range.push(i);
  }
  return range;
};

const sliceData = (data, page, rowsPerPage) => {
  return data.slice((page - 1) * rowsPerPage, page * rowsPerPage);
};

const useTable = (data, page, rowsPerPage) => {
  const [tableRange, setTableRange] = useState([]);
  const [slice, setSlice] = useState([]);

  useEffect(() => {
    const range = calculateRange(data, rowsPerPage);
    setTableRange([...range]);

    const slice = sliceData(data, page, rowsPerPage);
    setSlice([...slice]);
  }, [data, setTableRange, page, setSlice]);

  return { slice, range: tableRange };
};

const TableFooter = ({ range, setPage, page, slice }) => {
  useEffect(() => {
    if (slice.length < 1 && page !== 1) {
      setPage(page - 1);
    }
  }, [slice, page, setPage]);
  return (
    <div className={'tableFooter'}>

      {/*<Button onClick={() => console.log(range)}>{"<"}</Button>*/}

      {range.map((el, index) => (
        <button
          key={index}
          className={`${'button'} ${page === el ? 'activeButton' : 'inactiveButton'
            }`}
          onClick={() => setPage(el)}
        >
          {el}
        </button>
      ))}
    </div>
  );
};

function showOrder(orderId) {
  console.log(orderId);
}

function timeFormat(time0) {

  const tempStr = time0.split(":");
  let time = time0;
  if (tempStr[0].length === 1)
    time = "0".concat(time0);

  // Check correct time format and split into components
  time = time.toString().match(/^([01]\d|2[0-3])(:)([0-5]\d)(:[0-5]\d)?$/) || [time];

  if (time.length > 1) { // If time format correct
    time = time.slice(1);  // Remove full string match value
    time[5] = +time[0] < 12 ? ' AM' : ' PM'; // Set AM/PM
    time[0] = +time[0] % 12 || 12; // Adjust hours
  }
  return time.join(''); // return adjusted time or original string
}

function convertToLocalDate(oldDate) {
  // console.log("Old Date: " + oldDate)
  const date = new Date(oldDate);
  // console.log("New Date: " + date);

  // console.log("To Local String: " + date.toLocaleString());
  return date.toLocaleString();

  /*const hours = date.getHours();
  console.log("H: " + hours);
  const minutes = date.getMinutes();
  console.log("M: " + minutes);
  const seconds = date.getSeconds();
  console.log("S: " + seconds);*/
}

const CustomTable = ({ data, rowsPerPage }) => {
  const [page, setPage] = useState(1);
  const { slice, range } = useTable(data, page, rowsPerPage);
  return (
    <>
      <Table responsive="xl" hover>
        <thead>
          <tr>
            <th scope="col">{translate("TRANSACTIONS.ORDER_ID")}</th>
            <th scope="col">{translate("TRANSACTIONS.NAME")}</th>
            <th scope="col">{translate("TRANSACTIONS.DATE")}</th>
            <th scope="col">{translate("TRANSACTIONS.TIME")}</th>
            <th scope="col">{translate("TRANSACTIONS.JOURNAL_NAME")}</th>
            <th scope="col">{translate("TRANSACTIONS.AMOUNT")}</th>
          </tr>
          <tr>
            <th scope="col" colSpan="5"></th>
            <th scope="col">
              <div className="d-flex justify-content-around">
                <span>{translate("TRANSACTIONS.IN")}</span>
                <span>{translate("TRANSACTIONS.OUT")}</span>
              </div>
            </th>
          </tr>
        </thead>
        <tbody>
          {slice.map((item, index) =>
            <tr
              key={index}
            >

              <td>
                {/*<span className="orderLink" onClick={() => {showOrder(item.ref)}}>{item.ref}</span>*/}
                
                  <Link
                    to={{
                      pathname: `/account/Order/${item.ref}`,
                      // state: {currentPage: currentPage},
                    }}
                    variant="outline-primary" className='rounded-pill'>
                  >
                    {item.ref}
                    {/*<svg style={{ display: "inline", marginRight: "10px", marginLeft: "10px" }} xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-info-circle" viewBox="0 0 16 16">
                                      <path d="M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14zm0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16z"/>
                                      <path d="m8.93 6.588-2.29.287-.082.38.45.083c.294.07.352.176.288.469l-.738 3.468c-.194.897.105 1.319.808 1.319.545 0 1.178-.252 1.465-.598l.088-.416c-.2.176-.492.246-.686.246-.275 0-.375-.193-.304-.533L8.93 6.588zM9 4.5a1 1 0 1 1-2 0 1 1 0 0 1 2 0z"/>
                                    </svg>*/}
                  </Link>
                
              </td>
              <td>
                {item.move_name}
              </td>
              <td>
                {item.create_date.split(" ")[0]}
              </td>
              <td>
                {timeFormat(item.create_date.split(" ")[1]) /*convertToLocalDate(item.create_date).split(",")[1]*/}
              </td>
              <td>
                {item.journal_id_name}
              </td>
              <td>
                <div className="d-flex justify-content-around">
                  <span>{item.credit !== 0 && item.credit + " NIS"}</span>
                  <span>{item.debit !== 0 && item.debit + " NIS"}</span>
                </div>
              </td>

            </tr>
          )}
          {/*{!slice.length && <tr><td colSpan={columns.length}>Transactions not found!</td></tr>}*/}
        </tbody>
      </Table>
      <TableFooter range={range} slice={slice} setPage={setPage} page={page} />
    </>
  );
};

export default function Transactions() {

  // console.log(styles)

  const [transactions, setTransactions] = useState([]);
  const [tmpTransactions, setTmpTransactions] = useState([]);
  const [isEmpty, setIsEmpty] = useState(false);

  // const [invoices, setInvoices] = useState([]);

  /*const columns = [
      {
          label: "Name",
          key: "move_name",
      },
      {
          label: "date",
          key: "date"
      },
      {
          label: "Order ID",
          key: "ref",
      },
      {
          label: "Journal",
          key: "journal",
      },
      {
          label: "amount",
          key: "amount"
      }
  ];*/

  useEffect(() => {

    // console.log(localStorage.getItem("userId"));

    let isMounted = true;
    transactionTest(localStorage.getItem("userId")).then(res => {

      // console.log(res.data);

      if (isMounted) {
        let tempArr = res.data.server_response.data.result.response;

        /*tempArr.sort((a, b) => {
            return b.ref - a.ref;
        });

        for (let i = 0; i < tempArr.length; i++) {
            if (tempArr[i].amount < 0) {
                tempArr[i].amount = Math.abs(tempArr[i].amount) + " - in"
            } else if (tempArr[i].amount > 0) {
                tempArr[i].amount = tempArr[i].amount + " - out"
