import React from "react";
import "./AdminPanel.css"
import { GetAllActiveOrders, GetAllClients, GetAllFinishedOrders, GetAllReturnedOrders, GetAllNewOrders, GetAllTransporters, getRecordsActions, getTotalBalance, getLastOrderId, getLastRecord, updateAdminWebNotificationToken } from "../../APIs/AdminPanelApis";
import { useEffect, useState } from "react";
import { ResponsivePie } from '@nivo/pie';
import OrdersCard from "./OrdersCard";
import UsersCard from "./UserCard";
import RecordsCard from "./RecordsCard";
import { useDispatch } from "react-redux";
import { useHistory } from "react-router";

import "bootstrap-icons/font/bootstrap-icons.css";

//https://dribbble.com/shots/15254181-CRM-Dashboard-Managency
//https://dribbble.com/shots/16485287-Trustland-Logistic-Management-App

export default function AdminPanel() {

    const history = useHistory();

    let dispatch = useDispatch();

    const styles = {
        cardHeaderLg: {
            position: 'absolute',
            left: '20px',
            right: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold'
        },
        cardHeaderSm: {
            position: 'absolute',
            left: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold'
        }
    }

    const [orderStat, setOrderStat] = useState([]); // for orders stat. 
    const [userStat, setUserStat] = useState([]); // for users stat.
    const [newOrders, setNewOrders] = useState([]); // all newly created orders
    const [activeOrders, setActiveOrders] = useState([]); // all in process orders
    // const [finishedOrders, setFinishedOrders] = useState([]); // all finished/deleted orders
    const [exceptionOrders, setExceptionOrders] = useState([]); // all stuck/returned orders
    const [activeOrdersNum, setActiveOrdersNum] = useState(0); // number of active orders
    const [records, setRecords] = useState([]); // all system actions
    const [creditAmount, setCreditAmount] = useState(0); // total escrow, all system balance
    const [totalEscrowBalance, setTotalEscrowBalance] = useState(0); // total in escrow, active money
    const [allTransporters, setAllTransporters] = useState([]); // all transporters
    const [allClients, setAllClients] = useState([]); // all clients
    const [lastorderId, setLastorderId] = useState(); // last action record id, from system actions

    /* get last order id as number of total orders */
    useEffect(() => {
        getLastOrderId().then((res) => {
            setLastorderId(res.data);
        })
    }, []);

    /* get system actions */
    useEffect(() => {
        getRecordsActions().then((res) => {
            setRecords(res.data.records_list);
        });
    }, []);

    /* get total balance */
    useEffect(() => {
        getTotalBalance().then((res) => {

            setTotalEscrowBalance(res.data.server_response.data?.result.differ_balance);
            setCreditAmount(res.data.server_response.data?.result.credit_amount);
        });
    }, []);

    /* get all orders */
    useEffect(() => {
        // reset orders stat. to reset orders pie chart
        setOrderStat([]);

        // get all new orders
        GetAllNewOrders().then((res) => {
            setNewOrders(res.data.orders_list);
            setOrderStat((prev) => {
                return [...prev, {
                    "id": "new",
                    "label": "New",
                    "value": res.data.NumberOfOrders,
                }]
            });
        });

        // get all in-process orders
        GetAllActiveOrders().then((res) => {
            console.log(res.data.orders_list);
            setActiveOrders(res.data.orders_list);
            setActiveOrdersNum(res.data.NumberOfOrders);
            setOrderStat((prev) => {
                return [...prev, {
                    "id": "active",
                    "label": "Active",
                    "value": res.data.NumberOfOrders,
                }]
            });
        });

        // get all stuck/returned orders
        GetAllReturnedOrders().then((res) => {
            // setFinishedOrders(res.data.orders_list);
            setExceptionOrders(res.data.orders_list);
            setOrderStat((prev) => {
                return [...prev, {
                    "id": "finished",
                    "label": "Finished",
                    "value": res.data.NumberOfOrders,
                }]
            });

        });
    }, []);

    useEffect(() => {
        // reset users stat. to reset users pie chart
        setUserStat([]);

        // get all transporters
        GetAllTransporters().then((res) => {
            let tempArr = res.data.transporters_list;

            setAllTransporters(tempArr);
            setUserStat((prev) => {
                return [...prev, {
                    "id": "transporter",
                    "label": "Transporters",
                    "value": res.data.NumberOfTransporters,
                }]
            });
        });

        // get all clients
        GetAllClients().then((res) => {
            let tempArr = res.data.clients_list;

            setAllClients(tempArr);
            setUserStat((prev) => {
                return [...prev, {
                    "id": "client",
                    "label": "Clients",
                    "value": res.data.NumberOfClients,
                }]
            });
        });
    }, []);

    return (
        <>
            <div className="d-flex w-100 main-wrapper">
                <div className="stat-container shadow container">
                    <div className="row h-100 w-100">
                        <div className="col-md-4 border-right">
                            <div className="row mt-2">
                                <div className="col">
                                    <span>New Orders icon</span><br />
                                    <span>Active Orders icon</span>
                                </div>
                                <div className="col">
                                    <span>New Orders</span><br />
                                    <span>Active Orders</span>
                                </div>
                                <div className="col">
                                    <span>0</span><br />
                                    <span>0</span>
                                </div>
                            </div>
                        </div>

                        <hr className="hr-line" />

                        <div className="col-md-4 border-right">

                        </div>

                        <hr className="hr-line" />

                        <div className="col-md-4">
                        </div>
                    </div>
                </div>

                <div className="body-container m-3">

                </div>
            </div>
        </>
    )
}
