import {Box, Grid} from "@chakra-ui/layout";
import styles from "./Styles";
import React, {useEffect, useState} from "react";
import {useDispatch, useSelector} from 'react-redux';
import {getPersonalInfo} from "../../Actions/ProfileActions";
import Loader from "../../components/Loader/Loader";
import {isTransporter} from "../../Util";
import translate from '../../i18n/translate';

import { imgBaseUrl } from "../../Constants/GeneralCont"; /* edited (import imgBaseUrl) */

/*export function PersonalDetailsOut() {
    const dispatch = useDispatch();
    const {FullName} = useSelector(state => state.profile.personalInfo);
    const [loading, setLoading] = useState(true);
    const isTransporterVal = isTransporter();

    useEffect(() => {
        dispatch(getPersonalInfo(() => {
            setLoading(false);
        }));
    }, [dispatch]);

    let name = FullName;

    return name
}*/

export default function PersonalDetails() {
    const dispatch = useDispatch();
    const {FullName, NamePlace, IDNumber, LicenceNumber, Email, AccountName, LogoUrl} = useSelector(state => state.profile.personalInfo);
    const [loading, setLoading] = useState(true);
    const isTransporterVal = isTransporter();

    // console.log(imgBaseUrl + " - " + LogoUrl); // temp test

    useEffect(() => {
        dispatch(getPersonalInfo(() => {
            setLoading(false);
        }));
    }, [dispatch]);

    let personalDetails = [
        {
            title: translate("PERSONAL_DETAILS.FULL_NAME"),
            value: FullName
        },
        {
            title: translate("PERSONAL_DETAILS.EMAIL"),
            value: Email
        }
    ];

    if (isTransporterVal) {
        personalDetails.push(
            {
                title: translate("PERSONAL_DETAILS.ISSUING_IDENTITY"),
                value: NamePlace
            },
            {
                title: translate("PERSONAL_DETAILS.ID_NUMBER"),
                value: IDNumber
            },
            {
                title: translate("PERSONAL_DETAILS.DRIVER_NUM"),
                value: LicenceNumber
            },
            {
                title: translate("PERSONAL_DETAILS.ACCOUNT_NAME"),
                value: AccountName
            }
        )
    }

    const styles = {
        defaultLine: {
            width: "80%",
            margin: "auto",
            marginTop: "10px",
            marginBottom: "10px"
        },
        info: {
            color: "#3FB79E",
            fontSize: "20px"
        },
        fontSize_1: {
            fontSize: "20px"
        }
    }

    /*return <Box {...styles.content}>
        {loading ? <Loader/> :
            <Grid {...styles.personalInfoContainer} gridTemplateColumns={`repeat(${isTransporterVal ? 3 : 2}, 1fr)`}>
                {personalDetails.map((item, index) => (
                    <Box key={index}>
                        <Box {...styles.title} {...styles.greyTitle} textAlign="center">
                            {item.title}
                        </Box>
                        <Box {...styles.subTitle}>
                            {item.value}
                        </Box>
                    </Box>
                ))}
                <img src={`${imgBaseUrl}${LogoUrl}`} />
            </Grid>
        }
    </Box>*/

    return <div className="container">
        <div classNam="row w-100 justify-content-center">
            <img  style={{
                            borderRadius: "50%",
                            //margin: "auto",
                            border: "4px solid rgba(0,0,0,0.15)",
                            width: "100px",
                            height: "100px",
                            objectFit: "cover"
                        }} 
                src={`${imgBaseUrl}${LogoUrl}`} />
        </div>
        <div className="row">
            <div className="col d-flex justify-content-between">
                <span style={styles.fontSize_1}>Full name:</span>
                <span style={styles.info}>{personalDetails[0].value}</span>
            </div>
        </div>

        <hr style={styles.defaultLine} />

        <div className="row">
            <div className="col d-flex justify-content-between">
                <span style={styles.fontSize_1}>Email:</span>
                <span style={styles.info}>{personalDetails[1].value}</span>
            </div>
        </div>

        <hr style={styles.defaultLine} />
        
        <div className="row">
            <div className="col d-flex justify-content-between">
                <span style={styles.fontSize_1}>Place if Issuing Identitiy:</span>
                <span style={styles.info}>{personalDetails[2].value}</span>
            </div>
        </div>

        <hr style={styles.defaultLine} />
        
        <div className="row">
            <div className="col d-flex justify-content-between">
                <span style={styles.fontSize_1}>ID Number:</span>
                <span style={styles.info}>{personalDetails[3].value}</span>
            </div>
        </div>

        <hr style={styles.defaultLine} />
        
        <div className="row">
            <div className="col d-flex justify-content-between">
                <span style={styles.fontSize_1}>Driver Licence Number:</span>
                <span style={styles.info}>{personalDetails[4].value}</span>
            </div>
        </div>

        <hr style={styles.defaultLine} />
        
        <div className="row">
            <div className="col d-flex justify-content-between">
                <span style={styles.fontSize_1}>Account Name:</span>
                <span style={styles.info}>{personalDetails[5].value}</span>
            </div>
        </div>
    </div>

};
