import { motion } from "framer-motion";
import React, { useState, useEffect } from "react";
import { Badge, Form } from "react-bootstrap";
import { FixedSizeList } from 'react-window';
import './RecordsCard.css';

export default function RecordsCard(props) {

    const [records, setRecords] = useState(props.list);

    const [windowHeight, setWindowHeight] = useState(window.innerHeight - 100);
    const [windowWidth, setWindowWidth] = useState(window.innerWidth/4);

    useEffect(() => {
        setRecords(props.list)
    }, [props.list])

    useEffect(() => {
        function handleResize() {
            // console.log('resized to: ', window.innerWidth, 'x', window.innerHeight)
            setWindowHeight(window.innerHeight - 100)
            setWindowWidth(window.innerWidth/4)
        }

        window.addEventListener('resize', handleResize)
    })

    const filterHandler = (val) => {

        let tempRecords = props.list.filter(record => record.order_id.includes(val.toLowerCase()));

        setRecords(tempRecords)
    }

    const formatAction = (record) => {

        const tempHolder = <div style={{ width: "40%" }}>
            <div style={{ fontWeight: "bold", color: "#03A65A" }}>{record.description}</div>
            <div>{record.time_stamp.split(" ")[0] + " "}<Badge bg="primary">{record.time_stamp.split(" ")[1]}</Badge></div>
        </div>

        switch (record.action_id) {
            case "2":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{record.party_one_name + " created order " + record.order_id}</div>
                </div>;
            case "3":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{record.party_one_name + " bid on order " + record.order_id}</div>
                </div>;
            case "4":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{record.party_one_name + " cancel order " + record.order_id}</div>
                </div>;
            case "5":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{record.party_one_name + " updated bid on order " + record.order_id}</div>
                </div>;
            case "6":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{record.party_one_name + " canceled bid on order " + record.order_id}</div>
                </div>;
            case "7":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{record.party_one_name + " accepted " + record.party_two_name + "'s bid on order " + record.order_id}</div>
                </div>;
            case "8":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{record.party_one_name + " picked up order " + record.order_id}</div>
                </div>;
            case "9":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{record.party_one_name + " finished order " + record.order_id}</div>
                </div>;
            case "10":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{record.party_one_name + " assigned order " + record.order_id + " to " + record.party_two_name}</div>
                </div>;
            case "11":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{record.party_one_name + " rejected order " + record.order_id}</div>
                </div>;
            case "12":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{record.party_one_name + " accepted order " + record.order_id}</div>
                </div>;
            case "17":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{record.party_one_name + " canceled assign on order " + record.order_id}</div>
                </div>;
            case "18":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{record.party_one_name + " created and assigned order " + record.order_id + " to " + record.party_two_name}</div>
                </div>;
            case "19":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{record.party_one_name + " marked order " + record.order_id + " as stuck"}</div>
                </div>;
            case "20":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{record.party_one_name + " marked order " + record.order_id + " as returned"}</div>
                </div>;
        }
    }

    const RowComponent = ({ record, num, style }) => (
        <div style={style} className={"list-group-item"}>
            {/* <div className="avatar">
                {orderId}
            </div>

            <div className="details">
                <div className="info">
                    <p className="number">#{num + 1}</p>
                </div>

            </div> */}
            {formatAction(record)}
        </div>
    );

    const Row = ({ index, style }) => {
        return <RowComponent record={records[index]} num={index} style={style} />
    };

    const ListComponent = () => {
        return <FixedSizeList
            height={windowHeight}
            width={windowWidth}
            itemSize={100}
            itemCount={records.length}
            className="list-container"
        >
            {Row}
        </FixedSizeList>
    };

    return (
        <div className="h-100" style={{ fontFamily: "system-ui", overflow: "hidden", position: "relative" }}>
            <spna style={{ position: "absolute", top: "15px", left: "15px"  }} className="text-black">Orders Actions</spna>
            <ListComponent />
        </div>

    )
}
