import React, {useState} from "react";
import "./CancelOrder.css";
import Modal from "react-bootstrap/Modal";
import {deleteOrder} from "../APIs/OrdersAPIs";
import translate from "../../i18n/translate";
import {
    Button
} from "@chakra-ui/react";
import { DeleteIcon } from '@chakra-ui/icons'

export function CancelOrder(props) {
    const [show, setShow] = useState(false);
    const handleClose = () => {
        setShow(false);
    };
    const handleShow = () => setShow(true);
    const cancelAnOrder = (orderId) => {
        deleteOrder(orderId);
        handleClose();
    };
    return (
        <>
            <Button colorScheme="red" leftIcon={<DeleteIcon mt="-3px" />} {...props.style} onClick={handleShow}>
                {translate("ORDER_DETAILS.CANCEL_ORDER")}
            </Button>
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>{translate("ORDER_DETAILS.CANCEL_ORDER")} {props.orderId} </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <p>{translate("ORDER_DETAILS.CONFIRM_CANCEL")}</p>
                </Modal.Body>
                <Modal.Footer>
                    <Button
                        colorScheme="red" leftIcon={<DeleteIcon mt="-3px" />}
                        onClick={() => cancelAnOrder(props.orderId)}
                    >
                        {translate("GENERAL.CANCEL")}
                    </Button>
                    <Button bg="#0000008f" color="white" onClick={handleClose}>
                        {translate("GENERAL.CLOSE")}
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}
