import React, { useEffect, useState, useRef } from 'react';
import { Button, Dropdown, Form, Modal, Spinner } from "react-bootstrap";
import translate from "../i18n/translate";

export default function SubmitAndAssignDialog(props) {
    const [open, setOpen] = useState(true);

    const assignedDeliveryCostRef = useRef();

    return (
        <>
            <Modal size="md" show={open} onHide={() => { setOpen(false) }} centered animation={true} backdrop="static" style={{ backgroundColor: "rgba(0,0,0,0.5)" }}>

                <Modal.Header closeButton>
                    <Modal.Title>{translate("ORDER_DETAILS.ASSIGN")}</Modal.Title>
                </Modal.Header>

                <Modal.Body>

                    <Form.Control type="text" className="" placeholder="Delivery Cost" ref={assignedDeliveryCostRef} />

                </Modal.Body>
                <Modal.Footer className="d-flex justify-content-center">

                    <Button
                        variant="primary"
                        onClick={() => {
                            // props.setStartLoadingSubmit()

                            props.onSuccess();
                            props.setCloseMainDialog();
                            props.setStopLoadingSubmit();
                            props.setCloseAssignDialog(false);
                            setOpen(false);
                        }
                        }
                    >
                        {props.loadingSubmit && <Spinner animation="border" size="sm" />}
                        {translate("ORDERS.SUBMIT")}
                    </Button>

                    <Button
                        variant="danger"
                        onClick={() => {
                            props.setCloseAssignDialog();
                            setOpen(false);
                        }
                        }
                    >
                        {translate("GENERAL.CANCEL")}
                    </Button>


                </Modal.Footer>


            </Modal>
        </>
    )
}
