                    <ListGroup className="mt-4">
                        {
                            listAreas.map((item, index) => {
                                return <ListGroup.Item key={index}>{item.number + " - " + item.name}</ListGroup.Item>
                            })
                        }
                    </ListGroup>
                </Col>
            </Row> */}

            <Row>
                <Col>
                    <Form.Label>Provinces:</Form.Label>
                    <Form.Select aria-label="Default select example" onChange={(e) => {
                        console.log(e.target.value);
                    }}>
                        <option style={{ color: "lightgray" }}>Select Provice</option>
                        {
                            provinces.map((item, index) => {
                                return <option key={index} value={item.id}>{item.name}</option>
                            })
                        }
                    </Form.Select>
                </Col>
                <Col>
                    <Form.Label>Governorates:</Form.Label>
                    <Form.Select aria-label="Default select example">
                        <option style={{ color: "lightgray" }}>Select Governorate</option>
                        {
                            governorates.map((item, index) => {
                                return <option key={index} value={item.id}>{item.name}</option>
                            })
                        }
                    </Form.Select>
                </Col>
                <Col>
                    <Form.Label>Cities:</Form.Label>
                    <Form.Select aria-label="Default select example">
                        <option style={{ color: "lightgray" }}>Select City</option>
                        {
                            cities.map((item, index) => {
                                return <option key={index} value={item.id}>{item.name}</option>
                            })
                        }
                    </Form.Select>
                </Col>
                <Col>
                    <Form.Label>Areas:</Form.Label>
                    <Form.Select aria-label="Default select example">
                        <option style={{ color: "lightgray" }}>Select Area</option>
                        {
                            areas.map((item, index) => {
                                return <option key={index} value={item.id}>{item.name}</option>
                            })
                        }
                    </Form.Select>
                </Col>
            </Row>
        </Container>
    )
}
