import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useLocation, useParams } from "react-router";
import { getBusinessLocation, getPersonalInfo } from "../Actions/ProfileActions";
import translate from "../i18n/translate";
import { getOrderDetails } from "./APIs/OrdersAPIs";
import { packageFormatter } from "./Orders/OrdersTabularView";
var Barcode = require('react-barcode');

export default function WayBill(){
    const{ id } = useParams();
    
    const [orderDetails, setOrderDetails] = useState();
    const location = useLocation();
    const dispatch = useDispatch();
    const {FullName, Email} = useSelector(state => state.profile.personalInfo);
    const { BusinessName } = useSelector(state => state.profile.businessLocations);
    const [loading, setLoading] = useState(true);
    
    useEffect(()=>{
        if(FullName && Email && orderDetails && BusinessName && new URLSearchParams(location.search).get("print") === "true"){
            window.print();
        }
    }, [FullName, Email, orderDetails])

    useEffect(() => {
        dispatch(getPersonalInfo(() => {
            setLoading(false);
        }));
        dispatch(getBusinessLocation());
    }, [dispatch]);

    useEffect(()=>{
        
        getOrderDetails(id).then((orderDetails) => {
            setOrderDetails(orderDetails);
        });

    }, [id]);
    
    return (
    <div className="container pt-3">
        <div className="d-flex">
            <div className="border border-dark border-2 w-50 d-flex justify-content-between p-2 m-inline-e-2">
                <img 
                    style={{
                        maxWidth: "200px"
                    }}
                    alt="business logo"
                    src="https://cdn.dribbble.com/users/1111447/screenshots/7697273/media/ba28a6aaee0f52b59662c6d6122c94ef.png?compress=1&resize=400x300" 
                />
                <div>
                    <p>{FullName}</p>
                    <p>{BusinessName}</p>
                    <p>{translate("PERSONAL_DETAILS.EMAIL")}: {Email}</p>
                </div>
            </div>
            <div className="w-50">
                <p>{translate("WAYBILL.PRINT_DATE")}: {new Date().toDateString()} </p>
                {orderDetails?.BarCode && <Barcode value={orderDetails.BarCode} />}
            </div>
        </div>
        
        <div className="d-flex">
            
            <div className="w-50  m-inline-e-2">
                <fieldset 
                    style={{
                        all: "revert",
                        margin: "0"
                    }}
                    className="border-dark"
                >
                    <legend 
                        style={{
                            all: "revert"
                        }}
                    >
                        {translate("WAYBILL.SENDER")}
                    </legend>
                    <div>
                        <p>{translate("WORKING_TIME.FROM")}: {orderDetails?.ReceiverName}, <br /> {orderDetails?.IdCitySource},<br /> {orderDetails?.OtherDetailsDes}</p>
                        <hr className="my-2" />
                        <p>{translate("ADMIN.MOBILE_NUMBER")}: {orderDetails?.ReceiverAddressNum}</p>
                    </div>
                </fieldset>
            </div>

            <div className="w-50">
                <fieldset 
                    style={{
                        all: "revert",
                        margin: "0",
                    }}
                    className="border-dark"
                >
                    <legend style={{
                        all: "revert"
                    }}>
                        {translate("WAYBILL.RECEIVER")}
                    </legend>
                    <p>{translate("WORKING_TIME.TO")}: {FullName},<br />
                    {orderDetails?.IdCityDes},<br />
                    {orderDetails?.OtherDetails}
                    </p>
                    <hr className="my-2" />
                    <p>{translate("ADMIN.MOBILE_NUMBER")}: {orderDetails?.PhoneCustomer}</p>
                </fieldset>
            </div>
        </div>
        
        <div className="d-flex pt-2">
            <div className="w-75 m-inline-e-2">
                <div className="border border-dark p-2 border-2">
                    <p>{translate("WAYBILL.CONTENTS")}: </p>
                    <p></p>
                    <hr className="my-2" />
                    <p>{translate("WAYBILL.NOTES")}: </p>
                </div>

                <div className="d-flex pt-2">
                    <div className="w-75 m-inline-e-2">
                        <table className="table table-bordered border-2 border-dark">
                            <thead>
                                <tr>
                                    <th>{translate("WAYBILL.LENGTH")}</th>
                                    <th>{translate("WAYBILL.HEIGHT")}</th>
                                    <th>{translate("WAYBILL.WIDTH")}</th>
                                    <th>{translate("WAYBILL.WEIGHT")}</th>
                                </tr>
                            </thead>
                            <tbody>
                                <tr>
                                    <td>
                                        {orderDetails?.LengthLoad}
                                    </td>
                                    <td>
                                        {orderDetails?.HeightLoad}
                                    </td>
                                    <td>
                                        {orderDetails?.WidthLoad}
                                    </td>
                                    <td>
                                        {orderDetails?.WeightLoad}
                                    </td>
                                </tr>
                            </tbody>
                        </table>
                    </div>
                    <div className="w-25">
                        <table className="table table-bordered border-2 border-dark">
                            <thead>
                                <tr>
                                    <th>{translate("WAYBILL.SHIP")}</th>
                                </tr>
                            </thead>
                            <tbody>
                                <tr>
                                    <td>{packageFormatter({PackageType: orderDetails?.TypeLoad})}</td>
                                </tr>
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
            <div className="border border-2 border-dark w-25 text-center">
                <p className="p-2 border-bottom border-dark">قيمه التحصيل</p>
                
                <div className="d-flex justify-content-center flex-column" style={{height: "110px"}}>
                    <h1 className="h1">{orderDetails?.CostLoad}</h1>
                    <p>{translate("WAYBILL.NIS")}</p>
                </div>
            </div>
        </div>

    </div>)
}
