import React from "react";
import togoLogo from "../../assets/logo.png"
import { FaHome, FaEnvelope, FaMoneyCheckAlt, FaSignOutAlt } from "react-icons/fa"
import "./AdminPanel.css"
import { GetAllActiveOrders, GetAllClients, GetAllFinishedOrders, GetAllNewOrders, GetAllTransporters, transactionTest } from "../../APIs/AdminPanelApis";
import { useEffect, useState } from "react";
import { ResponsivePie } from '@nivo/pie';
import OrdersCard from "./OrdersCard";
import UsersCard from "./UserCard";
import SignIn from "./SignIn";
import { refreshPage } from "../../Functions/CommonFunctions";
//https://dribbble.com/shots/15254181-CRM-Dashboard-Managency
//https://dribbble.com/shots/16485287-Trustland-Logistic-Management-App

export default function AdminPanel() {

    const [orderStat, setOrderStat] = useState([]);
    const [userStat, setUserStat] = useState([]);
    const [newOrders, setNewOrders] = useState([]);
    const [activeOrders, setActiveOrders] = useState([]);
    const [finishedOrders, setFinishedOrders] = useState([]);

    const [allTransporters, setAllTransporters] = useState([]);
    const [allClients, setAllClients] = useState([]);

    useEffect(() => {
        transactionTest().then((res)=>{  // edited (test transactions) 
            console.log(res.data);
        });
        GetAllNewOrders().then((res)=>{
            // console.log(res.data);
            setNewOrders(res.data);
            setOrderStat((prev) => {
                return [...prev, {
                    "id": "new",
                    "label": "New",
                    "value": res.data.NumberOfOrders,
                }]
            });
        });
        GetAllActiveOrders().then((res)=>{
            setActiveOrders(res.data);
            setOrderStat((prev) => {
                return [...prev, {
                    "id": "active",
                    "label": "Active",
                    "value": res.data.NumberOfOrders,
                }]
            });
        });
        GetAllFinishedOrders().then((res)=>{
            setFinishedOrders(res.data);
            setOrderStat((prev) => {
                return [...prev, {
                    "id": "finished",
                    "label": "Finished",
                    "value": res.data.NumberOfOrders,
                }]
            });
            
        });

        GetAllTransporters().then((res)=>{
            // console.log(res.data);
            setAllTransporters(res.data);
            setUserStat((prev) => {
                return [...prev, {
                    "id": "transporter",
                    "label": "Transporters",
                    "value": res.data.NumberOfTransporters,
                }]
            });
        });
        GetAllClients().then((res)=>{
            // console.log(res.data);
            setAllClients(res.data);
            setUserStat((prev) => {
                return [...prev, {
                    "id": "client",
                    "label": "Clients",
                    "value": res.data.NumberOfClients,
                }]
            });
        });
    });
    
    if(localStorage.getItem("signedin") !== "true"){
        return <SignIn />
    }
    
    return (
        <div className="d-flex h-100 w-100 panel-wrapper green-tea-bg">
            <div
                className="bg-white mt-3 mb-3 ms-3 rounded-22 shadow admin-left-menu" 
            >
                <div className="p-3">
                    <div className="align-items-center d-flex fs-5 justify-content-center text-black">
                        <img src={togoLogo} style={{width: "100px"}} alt="logo" />
                        <span className="forest-color">Management System</span>
                    </div>
                    <div className="border-bottom border-top fs-6 fw-bold mt-5 py-3 text-black">
                        Welcome Abu Hussam!
                    </div>
                </div>
                <div>
                    <div 
                        style={{
                            borderLeft: "2px solid",
                        }}
                        className="align-items-center d-flex mt-2 py-2 menu-item ps-3 Shamrock-color"
                    >
                        <FaHome className="menu-icon" />
                        Dashboard
                    </div>
                    
                    <div className="align-items-center d-flex mt-2 py-2 menu-item ps-3">
                        <FaEnvelope className="menu-icon" />
                        Messages
                    </div>
                    
                    <div className="align-items-center d-flex mt-2 py-2 menu-item ps-3">
                        <FaMoneyCheckAlt className="menu-icon" />
                        Payments
                    </div>
                </div>

                <div className="admin-bottom-menu">
                    <div className="align-items-center d-flex mt-2 py-2 menu-item ps-3" onClick={()=>{
                        localStorage.setItem("signedin", false);
                        refreshPage()
                    }}>
                        <FaSignOutAlt className="menu-icon" />
                        Logout
                    </div>
                </div>

            </div>
            <div className="w-100 ps-3"/* d-flex flex-wrap  */ style={{overflowX: "auto"}}>
                <div className="w-100 d-flex">
                    <div 
                        className="bg-white me-3 mt-3 rounded-22 shadow togo-chart"
                    >
                        
                    </div>
                    <div 
                        
                        className="bg-white me-3 mt-3 rounded-22 shadow togo-chart"
                    >
                        
                    </div>
                </div>
                <div className="w-100 d-flex h-250px">
                    <OrdersCard title="New Orders" list={newOrders.orders_list} />
                    
                </div>

                <div className="w-100 d-flex h-250px">
                    <OrdersCard title="Active Orders" list={activeOrders.orders_list} />
                </div>
                    
                <div className="w-100 d-flex h-250px">
                    <OrdersCard title="Finished Orders" list={finishedOrders.orders_list} />
                </div>
            </div>
            <div className="m-3 w-50">

                <div className="h-250px">
                    <UsersCard title="All Clients" list={allClients.clients_list} />
                </div>

                <div className="mt-3 h-250px">
                    <UsersCard title="All Transporters" list={allTransporters.transporters_list} />
                </div>
            </div>
        </div>
    )
}
