import React, { useEffect, useState } from 'react';
import {
    Grid, GridItem, Box, Divider
} from "@chakra-ui/react";
import { useDispatch } from 'react-redux';
import VehiclesInformation from "./VehiclesInformation";
import styles from './Styles';
import BusinessLocation from "./BusinessLocation";
import PersonalDetails from "./PersonalDetails";
import WorkingTime from "./WorkingTime";
import { isTransporter } from "../../Util";
import { accountDetailsCleanUp } from "../../Actions/ProfileActions";
import translate from "../../i18n/translate";

import { Accordion, Card } from "react-bootstrap"; /* edited (Accordion, Card imported) */

export function CardComponent(props) {

    const styles = {
        cardHeaderLg: {
            position: 'absolute',
            left: '20px',
            right: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold'
        },
        cardHeaderSm: {
            position: 'absolute',
            left: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold'
        }
    }

    return (
        <Card
            bg={"Danger"}
            text={"black"}
            className="mt-5 shadow"
            style={{ height: props?.bodyNumber === 1 ? "90%" : "" }}
        >
            <Card.Header style={styles.cardHeaderSm}>{props?.title}</Card.Header>
            <Card.Body className="d-flex align-items-center mt-3">
                {
                    props?.bodyNumber === 1 ? <PersonalDetails /> :

                        props?.bodyNumber === 2 ? <BusinessLocation /> :

                            props?.bodyNumber === 3 ? <VehiclesInformation /> :

                                <WorkingTime />
                }
            </Card.Body>
        </Card>
    )
}

export default function AccountDetails() {
    let isTransporterVal = isTransporter();
    const dispatch = useDispatch();

    // window height and width variables to set records container width and height
    const [windowHeight, setWindowHeight] = useState(window.innerHeight);
    const [windowWidth, setWindowWidth] = useState(window.innerWidth);

    // update window width and height when change window dimensions (zoom in and out, move to a bigger screen)
    useEffect(() => {
        function handleResize() {
            setWindowHeight(window.innerHeight)
            setWindowWidth(window.innerWidth)
        }

        window.addEventListener('resize', handleResize)
    })

    useEffect(() => {
        return () => dispatch(accountDetailsCleanUp())
    }, [dispatch]);

    const containerStyle = {
        /*background: "white",
        borderRadius: "10px",
        borderWidth: "1px",
        boxShadow: "5px 5px 5px lightgray",
        padding: "50px 10px 10px 10px",*/
    }

    return (


        <div>
            <div className='container-fluid'>
                <div className='row'>
                    <div className='col-xl-4' style={{ height: windowHeight - 161 + "px" }}>
                        <CardComponent title={translate("ACCOUNT_DETAILS.PERSONAL_INFORMATION")} bodyNumber={1} />
                    </div>

                    <div className='col-xl-8' style={{ height: windowHeight - 161 + "px" }}>

                    </div>
                </div>
            </div>
        </div>
    );

};

